INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(178, 245, 'Ford планирует выпустить бюджетный автомобиль', 'ford-planiruet-vypustit-byudzhetnyj-avtomobil', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Ford планирует пополнить свой модельный ряд бюджетным автомобилем, который будет дешевле хэтчбека Fiesta.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Предполагается, что новинка будет создана на базе все той же «Фиесты», реализовывать свой «бюджетник» Ford будет на глобальных рынках, информирует «АвтоСреда».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Мы пока не имеем четкого плана на счет этой модели, но считаем, что достаточно важно быть конкурентоспособным в каждом сегменте рынка», - заявил Алан Маллали, руководитель компании .</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Однако он не уточнил о сроках появления новой модели. Вместе с этим компания не намерена создавать отдельный бренд для «бюджетников», на подобии Nissan с Datsun и Renault с Dacia.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Купить <strong>авто с пробегом</strong> марки <strong>Ford </strong>в <strong>автосалоне Адмирал-Моторс</strong> будет правильным решением. Так как у нас вы можете <strong>купить автомобиль б у в кредит</strong> по разумной цене.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-09-14 04:40:20', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-09-14 04:40:20', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 779, 'авто с пробегом, ford, автосалоне адмирал-моторс, купить автомобиль б у в кредит', 'Бюджетный автомобиль от ford', 1, 930, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(179, 246, 'Volkswagen Golf снова в продаже!', 'volkswagen-golf', '<p><span style="color: #222222; font-family: Arial, Helvetica, sans-serif; font-size: 14px; line-height: 19px;">В ноябре на Российском рынке будет доступен </span><strong style="color: #222222; font-family: Arial, Helvetica, sans-serif; font-size: 14px; line-height: 19px;">Volkswagen Golf</strong><span style="color: #222222; font-family: Arial, Helvetica, sans-serif; font-size: 14px; line-height: 19px;"> шестого поколения в комплектации Comfortline.</span></p>', '<p class="text" style="box-sizing: border-box; font-size: 14px; line-height: 19px; margin: 0px; padding: 0px 30px 15px 0px; color: #222222; font-family: Arial, Helvetica, sans-serif; ">Каждая из серий Golf до сих пор популярна. Golf 2, Golf 3, Golf 4, Golf 5, до сих пор окружают нас. И вот Volkswagen представил очередную версию!</p>\n<p class="text" style="box-sizing: border-box; font-size: 14px; line-height: 19px; margin: 0px; padding: 0px 30px 15px 0px; color: #222222; font-family: Arial, Helvetica, sans-serif; ">Модификация получила электростеклоподъемники спереди и сзади, 2-зонный климат-контроль Climatronic, подогрев передних сидений, омыватель фар, мультифункциональное рулевое колесо с клавишами управления музыкой и дисплеем, электрозеркала с обогревом, центральный замок с дистанционным управлением и другие опции.</p>\n<p class="text" style="box-sizing: border-box; font-size: 14px; line-height: 19px; margin: 0px; padding: 0px 30px 15px 0px; color: #222222; font-family: Arial, Helvetica, sans-serif; ">Golf Comfortline будет доступен с механической коробкой передач и тремя моторами семейства TSI мощностью 85, 105, 122 л.с., а также с агрегатом объемом 1,6 литра. Стоимость данной модификации начнется с отметки в 699 000 рублей.</p>\n<h4 style="font-size: 12px; text-align: right;">Полезная информация:</h4>\n<p class="text" style="font-weight: normal; box-sizing: border-box; font-size: 14px; line-height: 19px; margin: 0px; padding: 0px 30px 15px 0px; color: #222222; text-align: right;">В автосалоне Адмирал Моторс вы всегда сможете <a href="cars.html" title="купить бу авто, подержанные авто в СПб">купить подержанные авто</a> по низкой цене.</p>\n<p> </p>', 1, 13, '2012-09-23 17:00:42', 792, '', '2012-09-23 17:14:59', 792, 0, '0000-00-00 00:00:00', '2012-09-23 17:00:42', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 778, 'подержанные авто, Volkswagen Golf 2, 3, 4, 5', 'В ноябре в России появится Volkswagen Golf шестого поколения в комплектации Comfortline.', 1, 649, '{"robots":"","author":""}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(180, 247, 'Европейский дебют Infiniti LE Concept состоялся в Париже', 'evropejskij-debyut-infiniti-le-concept-sostoyalsya-v-parizhe', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Наконец-то в Париже состоялся дебют <strong>Infiniti </strong>LE Concept - первого 100%-го электромобиля.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения российского представительства компании <strong>Infiniti</strong>, автомобиль <strong>Infiniti </strong>LE Concept с превосходным силовым агрегатом выглядит абсолютным Infiniti за одним исключением: в нем нет выхлопной трубы. Электромобиль Infiniti LE может превратиться в серийную модель.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Электромобиль LE Concept, разработанный в стилистике компактного седана, является вестником серийного автомобиля, продажи которого стартуют на некоторых рынках уже в ближайшие два года. Модель автомобиля, которая будет показана в демонстрационных залах, будет не только укомплектована новейшим силовым агрегатом LE Concept, но и позаимствует некоторую часть его дизайна, который можно назвать как стремительный и роскошный. Салон, оснащен дружественными для пользователей технологиями, плюс ко всему беспроводной системой зарядки батарей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>При длине 4,7 м. размеры концепт-кара близки к размерам <strong>Infiniti </strong>G Saloon, что говорит о том, что экологичный автомобиль не обязан быть маленьким и примитивным. В комплектации модели LE Concept предусмотрено оснащение средствами и приборами связи следующего поколения. Высококачественный электродвигатель кара, мгновенно развивает мощность до 100 кВт (134л.с.) при крутящем моменте в 325 Н•м. Управление трансмиссией происходит с помощью скульптурной рукоятки.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Безусловно, лучший электромобиль, выпущенный за последнее время. В <strong>автосалоне Адмирал-Моторс</strong> еще не скоро появится электромобили, ну уже сейчас вы можете<strong> купить поддержанный автомобиль в Санкт-Петербурге</strong> без лишних хлопот.  Любая популярная марка и не только <strong>авто с пробегом</strong> доступна в любое время года, для знакомства с ассортиментом посетите наш <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> в Интернете.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-01 09:30:59', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-01 09:30:59', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 777, 'Infiniti, автосалон адмирал-моторс, купить поддержанный автомобиль в санкт-петербурге, авто с пробегом, сайт по продаже автомобилей', 'На Парижском автосалоне дебютировался  Infiniti LE Concept', 1, 607, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(181, 248, 'В планах у Skoda создание новейшего центра по тестированию двигателей', 'v-planakh-u-skoda-sozdanie-novejshego-tsentra-po-testirovaniyu-dvigatelej', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Skoda </strong>Auto планирует инвестировать более 34 млн. евро в расширение существующего центра технологий и разработок в г. Чесана рядом со штаб-квартирой <strong>Skoda </strong>в Млада-Болеславе.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения автоконцерна, этот проект станет самым крупным в сфере технологических разработок в Чехии. Новый центр будет снабжен 21 стендом для тестирования двигателей и компонентов. Инвестиционный проект осуществляется в рамках стратегии роста марки Skoda.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По планам компании, к 2018 году объемы продаж <strong>Skoda </strong>должны установиться на уровне в 1,5 млн. автомобилей в год, одновременно с этим каждое полугодие на рынок будет появляться обновленная или новая модель. Новое технологическое оборудование для тестирования комплектующих и двигателей предоставит возможность увеличить  «ноу-хау» и развивать технологии.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сейчас в рамках Группы компания <strong>Skoda </strong>осуществляет значительную работу в области разработки компонентов, трансмиссий, тормозных систем и двигателей. Инвестиции <strong>Skoda </strong>в новое строительство - одни из крупнейших в сфере развития технологий в Чехии.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Суммарный объем инвестиций на первом этапе расширения центра составляет 34 млн. евро. Главными инструментами в центре станут 15 стендов для тестирования двигателей мощностью от 250 - 400 кВт. В ближайшем будущем, помимо тестирования силовых агрегатов следующего поколения перед запуском в серийное производство, представится возможность проведения высокодинамичных испытаний мощностью до 400 кВт.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Шесть других стендов будут применены для ресурсных и функциональных испытаний систем двигателей и отдельных компонентов внутреннего сгорания. Такое оборудование позволит повысить эффективность проверки правильного функционирования двигателя, акустические характеристики, срок службы компонентов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Взяв за внимание постоянно повышающиеся требования к эксплуатационным характеристикам двигателей, специалисты будущего центра будут работать над разработкой двигателей, отвечающих разного рода запросам мировых рынков, к примеру, возможность тестировать двигатели, функционирующих на разных видах топлива, а также на LPG, этанол и CNG.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Компания <strong>Skoda </strong>как и многие другие автомобильные бренды не останавливаются на достигнутом и всегда развиваются в правильном направлении. <strong>Автосалон Адмирал-Моторс</strong> тоже не стоит на месте и постоянно пополняет ассортимент <strong>авто с пробегом</strong> самых популярнейших марок. <strong>Купить авто в Санкт-Петербурге с пробегом</strong> вместе с Адмиралом стало еще легче, за счет доступной всем системой выгодного кредитования.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-01 09:34:42', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-01 09:34:42', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 776, 'Skoda, автосалон адмирал-моторс, авто с пробегом, купить авто в санкт-петербурге с пробегом', 'Skoda Auto скоро порадует новым центром тестирования двигателей', 1, 628, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(182, 249, 'Киноальманах под названием «Astra, I Love You» выиграл на фестивале  «Киношок-2012»', 'kinoalmanakh-pod-nazvaniem-astra-i-love-you-vyigral-na-festivale-kinoshok-2012', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В солнечном городе Анапа прошел XXI Открытый кинофестиваль стран СНГ, Литвы, Латвии и Эстонии, «Киношок-2012».', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения российского представительства компании <strong>Opel</strong>, фильм «Astra, I Love You» выиграл в абсолютно новой программе киноальманахов Omnibus, в которой представлялись работы как признанных мастеров кино: Федора Бондарчука, Егора Кончаловского, Александра Лунгина, Алексея Попогребского и Андрея Звягинцева, так и начинающих режиссеров. Это первое признание профессионального кинопроекта, который был реализован при поддержке Opel.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кинофестиваль «Киношок» проходит ежегодно начиная с 1992 года, и за 20 лет завоевал статус одного из влиятельных межнациональных кинофорумов. В текущем году его гостями и участниками стали кинозвезды из России, стран Балтии и СНГ. Это уникальная возможность познакомиться поближе с разными кинематографическими школами и соответственно повысить интерес к культуре наций.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Немного о киноальманахе, «Astra, I Love You» – это 9 коротких историй, выполненных в разных жанрах и объединенных одной темой – это истории жизни и любви в современном большом городе, где, как известно любому, автомобиль стал уже давно неотъемлемой частью каждого из нас. Герои киноновелл не исключение – вместе с автомобилем <strong>Opel Astra</strong> они влюбляются и встречаются, работают и отдыхают, ссорятся и мирятся.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Купить поддержанный автомобиль в СПб</strong> (Санкт-Петербурге) марки <strong>Opel Astra</strong> вам обязательно нужно в <strong>автосалоне Адмирал-Моторс</strong>, так как мы предлагаем самые низкие цены, выгодные условия страхования и многое другое. У нас вы можете также <strong>продать авто с пробегом,</strong> а также<strong> купить авто б у в кредит</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-02 09:32:45', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-02 09:32:45', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 775, 'Opel Astra, купить поддержанный автомобиль в спб, автосалоне адмирал-моторс, продать авто с пробегом, купить авто б у в кредит', 'На XXI Открытом кинофестивале «Киношок-2012», реализованном при поддержке Opel победил фильм «Astra, I Love You»', 1, 485, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(183, 250, 'Chery M11 в раллийной версии прошла боевое крещение', 'chery-m11-v-rallijnoj-versii-proshla-boevoe-kreshhenie', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Пилот Сергей Бакулин на автомобиле<strong> Chery М11</strong> после финиша ралли «Санкт-Петербург 2012» сообщил, что теперь этот автомобиль готов бороться за призовые места.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения российского представительства компании Chery, «обучение» и постройка раллийной версии <strong>Chery M11</strong> побеждать на гонке заняла больше года. При создании спортивного автомобиля был применён принципиальный подход - применение как можно большего числа стандартных оригинальных деталей без серьезных усилений и доработок.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из автомобиля удалили обшивку салона, кондиционер, стандартные кресла заменили на спортивные и поставили каркас безопасности, выполненный по всем требованиям FIA (Международной федерации автоспорта), рычаги, балки задней и передней подвески оставили оригинальные. Силовой агрегат вместимостью 1600 куб.см. сохранил все оригинальные детали, доработаны были лишь распредвалы с целью изменения фаз газораспределения (в основании которых, лежат стандартные комплектующие).</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>С заменой выхлопной и впускной системы были достигнуты следующие цифры: 170 НМ, 180 л.с., ограничение оборотов 9200 об./мин. Помимо этого, автомобиль получил спортивные тормоза, секвентальную коробку передач и амортизаторы. Добиться существенного снижения веса авто не удалось, что не мешает <strong>Chery M11</strong> весом в 1250 кг. бороться с автомобилями, которые имеют более мощные моторы и в среднем на центнер легче.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ралли в Санкт-Петербурге 2012 года – первый финиш гоночной версии <strong>Chery M11</strong>, который стал зачетным. До этого были лишь тестовые заезды в рамках этапов Кубка и Чемпионата России. В этом тестировании техника не подвела ни разу, за исключением незначительных проблем. Экипаж Сhery наконец-то смог подняться на восьмую строчку итогового зачета.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В <strong>автосалоне Адмирал-Моторс</strong> в наличии <strong>авто с пробегом</strong> марки <strong>Chery Tiggo (T11)</strong> 2007 года выпуска в отличном состоянии. <strong>Купить в Санкт-Петербурге авто с пробегом</strong> этой или любой другой марки от Адмирал-Моторс стало еще проще, достаточно зайти на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> и ознакомиться с ассортиментом автомобилей и с услугами.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-02 09:38:44', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-02 09:38:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 774, 'Chery М11, Chery Tiggo (T11), автосалоне адмирал-моторс, авто с пробегом, купить в санкт-петербурге авто с пробегом, сайт по продаже автомобилей', '', 1, 645, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(184, 251, 'Новый автомобиль Clio расширит линейку Renault', 'novyj-avtomobil-clio-rasshirit-linejku-renault', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Новый автомобиль <strong>Clio</strong>, <strong>Clio </strong>R.S. 200 EDC и <strong>Clio </strong>Estate расширят линейку <strong>Renault</strong> уже в 2013 году.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из официального сообщения компании <strong>Renault</strong>, марка <strong>Clio </strong>целиком обновила линейку двигателей для этих авто. На автосалоне в Париже были представлены два обновленных агрегата Energy и новые бензиновые турбированные двигатели с трансмиссией EDC с двойным сцеплением и прямым впрыском топлива.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В автомобилях применяется множество новейших систем и технологий. <strong>Renault </strong>R-Link - это сенсорный планшет на приборной панели, воплощающий в себе совершенно новый уровень коммуникации с внешним миром из салона автомобиля: мультимедийная система, навигация, интерактивный магазин с приложениями и масса других функций.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для нового <strong>Clio </strong>дополнительно предлагается новые двигатели, на основе обширного опыта экспертов, созидающих для Формулы-1. Первоочередно  на автосалоне в Париже публике представлены 2 бензиновых двигателя с трансмиссией Renault EDC с двойным сцеплением - это 1.6 T <strong>Renault </strong>Sport 200 для нового <strong>Clio </strong>R.S. 200 EDC и TCe 120 EDC для топовых версий <strong>Clio</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В ходе создания и проектирования ZOE было сформировано более 60 патентов. ZOE - первый автомобиль Renault, разрабатываемый с самого начала как электромобиль, в нем широко применяются оригинальные решения <strong>Renault </strong>для электротранспорта.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Купить авто в Санкт-Петербурге с пробегом</strong> с большими скидками вы можете в <strong>автосалоне Адмирал-Моторс</strong>. У нас представлен целый ряд <strong>авто с пробегом</strong> на любой вкус и цвет. У нас вы можете также <strong>продать автомобиль с пробегом</strong>, который вам уже надоел, и сменить тут же на другой приглянувшийся автомобиль.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-03 09:38:43', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-03 09:38:43', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 773, 'купить авто в санкт-петербурге с пробегом, автосалоне адмирал-моторс, авто с пробегом, продать автомобиль с пробегом, clio, renault', 'Новый Clio от Renault с полностью обновленными двигателями', 1, 740, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(185, 252, 'На российской территории продан  100-тысячный автомобиль марки Volkswagen Polo седан', 'na-rossijskoj-territorii-prodan-100-tysyachnyj-avtomobil', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В салоне дилерского официального центра «Фольксваген Центр Пермь», находящегося в Перми, 29 сентября был продан 100-тысячный <strong>Volkswagen Polo</strong> седан.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения российского представительства компании <strong>Volkswagen</strong>, с того момента как вышел  <strong>Volkswagen Polo</strong> седан на  рынок России в сентябре 2010 года автомобиль стал безусловно настоящим бестселлером в своем классе. А 29 сентября стало известно имя обладателя юбилейного автомобиля  – Алина Екимова, студентка «Высшей школы экономики»  из Перми.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобиль был получен в подарок Алиной от своего отца Олега Екимова, выбравшего, по его словам, автомобиль <strong>Polo </strong>седан по принципу «автомобиль должен быть немецким». 100 - тысячной проданной моделью стал Polo седан в комплектации Highline белого цвета.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Polo </strong>седан – это первый автомобиль марки <strong>Volkswagen</strong>, созданный специально для России и внесший новые стандарты в линейку недорогих компактных автомобилей. За счет умеренной ценовой политики, седан <strong>Volkswagen Polo</strong> рассчитан на самую широкую категорию автолюбителей, это подтверждают данные по продажам.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобили <strong>Volkswagen Polo</strong> седаны собираются на заводе VOLKSWAGEN Group Rus, находящихся в Калуге, по технологии полного цикла, включая сварку, штамповку, окраску и сборку. Производство целиком соответствует мировым стандартам качества VOLKSWAGEN AG.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Седан <strong>Polo </strong>оснащается бензиновым двигателем 1.6 (77 кВт / 105 л.с.), способным комбинироваться с 5-ступенчатой механической трансмиссией, или с 6-ступенчатой автоматической коробкой передач со спортивным режимом и с функцией ручного переключения Tiptronic. Помимо этого, клиенты могут осуществлять выбор между традиционными для <strong>Volkswagen </strong>версиями исполнения Trendline, Highline и Comfortline. Стоимость модели начинается от 441 тыс. 500 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Наверное, Олег Якимов даже и не думал, что станет 100-тысячным покупателем, но кому как повезет. Но вам безусловно повезет, если вы станете нашим покупателем, ведь <strong>автосалон Адмирал-Моторс</strong> представляет только самые качественные<strong> авто с пробегом</strong>. У нас <strong>купить поддержанный автомобиль</strong> не составит никакого труда, нет необходимости даже выезжать к нам, просто зайдите на наш <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> и ознакомьтесь с линейкой автомобилей.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-03 09:42:21', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-03 09:42:21', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 772, 'автосалон адмирал-моторс, авто с пробегом, купить поддержанный автомобиль, сайт по продаже автомобилей, volkswagen polo, volkswagen', '100-тысячный автомобиль Volkswagen Polo седан проданный в России достался в подарок', 1, 763, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(186, 253, 'Volvo показал новый V40 Cross Country', 'volvo-pokazal-novyj-v40-cross-country', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Volvo </strong>показала новый V40 Cross Country, который стал обладателем наилучшей ездовой динамики в своем классе.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения компании, шасси разрешает быстрые реакции при маневрировании, что доставляет истинное удовольствие водителю за рулем. На территории России V40 Cross Country будет реализовываться с пятицилиндровым 2.0-литровым двигателем T4 мощностью 180 л.с. а также крутящим моментом 300 Нм.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобиль оснащен динамическим шасси в стандарте. Низко расположенный центр тяжести и прочный кузов гарантируют точные и быстрые реакции, одновременно с этим в салоне для водителя и пассажиров царит удобство и комфорт.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Водитель автомобиля получает более четкую обратную связь с дорожным покрытием и колесами. Ему предоставляется выбор 3-х уровней мощности электроусилителя.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Система контроля тяги в повороте – это совершенно новая функция, регулирующая крутящий момент и обеспечивающая автомобилю более уверенное прохождение поворотов. Водитель может объезжать повороты на более высокой скорости, а автомобиль собственно не допустит развитие тенденции недостаточной поворачиваемости.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Данная система облегчает постижение поворотов на извилистой трассе, на скользкой дороге и на развязках с круговым движением.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Датчик угла крена разрешает обнаружить тенденцию к заносу автомобиля на самой первой стадии. Устройство предотвращения пробуксовки колес благодаря усилению оборотов двигателя не допускает блокировку колес в момент торможения двигателем на скользкой дороге.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>И правда ездовая динамика впечатляет. Если вы хотите впечатлиться ездовой динамикой наших <strong>авто с пробегом</strong> от <strong>автосалона Адмирал-Моторс</strong>, то приезжайте к нам или зайдите на наш <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong>. У нас вы сможете <strong>продать поддержанный автомобиль в СПб</strong> (Санкт-Петербурге) или <strong>купить авто с пробегом</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-04 04:36:36', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-04 04:36:36', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 771, 'авто с пробегом, автосалона адмирал-Моторс, сайт по продаже автомобилей, продать поддержанный автомобиль в СПб, купить авто с пробегом, Volvo', 'Ездовая динамика Volvo V40 Cross Country впечатляет', 1, 830, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(187, 254, 'GM и Ford разработают АКПП С 9-ю и более ступенями', 'gm-i-ford-razrabotayut-kpp-s-9-yu-i-bolee-stupenyami', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компании General Motors и <strong>Ford </strong>совместно разрабатывают 9-ти- и 10-тиступенчатые автоматические трансмиссии.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения в издании Automotive News, такое количество ступеней в АКПП разрешает держать обороты двигателя на достаточно низком уровне в момент разгона автомобиля – двигателю приходится выполнять существенно меньше работы, что отлично сказывается на расходе топлива, разъяснил изданию эксперт из компании LMC Automotive.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Компании начали работу по разработке новых автоматических трансмиссий уже в начале 2012 года и в настоящие время проект находится в начальной стадии. Источник издания также сообщил, что новые «автоматы» будут оснащаться автомобилями компаний не раньше 2015 года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Специалисты из GM заняты разработкой 9-тиступенчатых автоматических трансмиссий для своих переднеприводных моделей. А компания <strong>Ford</strong>, в свою очередь, создает «автоматы» с десятью ступенями, предназначенные для пикапов, спорткаров, внедорожников и заднеприводных моделях компании, - сообщает motor.ru.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Предлагаю не ждать появление автомобилей с таким «автоматом» а купить <strong>авто в Санкт-Петербурге с пробегом</strong> в <strong>автосалоне Адмирал-Моторс</strong>. Помимо <strong>покупки автомобиля</strong> у нас вы можете <strong>продать </strong>свой старый <strong>поддержанный автомобиль</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-04 04:40:52', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-04 04:40:52', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 770, 'авто в санкт-петербурге с пробегом, автосалоне адмирал-моторс, покупки автомобиля, продать поддержанный автомобиль, Ford', '9-ти и 10-тиступенчатые АКПП от General Motors и Ford', 1, 632, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(188, 255, 'За вождение в нетрезвом виде будут лишать прав сроком на 10 лет', 'za-vozhdenie-v-netrezvom-vide-budut-lishat-prav-srokom-na-10-let', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В Госдуму внесен законопроект о лишении водительских прав для автомобилистов, управлявших машиной в пьяном виде, на срок до 10 лет.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По действующему законодательству, права могут отбираться только на два года. Новый законопроект предполагает лишать прав на срок до 10 лет водителей, передавшим личный автомобиль лицам, находящимся в нетрезвом состоянии.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Плюс к этому, авторы проекта предлагают повысить штраф за езду без прав с 2,5 тыс. до 50 тыс. рублей. Данный документ внесен на рассмотрение в Госдуму законодательным собранием Калужской области, - сообщают РИА «Новости».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если примут такой законопроект, то на дорогах водителей явно станет меньше. Быть может просто не садиться за руль в состоянии алкогольного опьянения и тогда вы сможете рассекать дороги и двигаться к своей мечте. Ведь для этого вы можете <strong>купить в Санкт-Петербурге авто с пробегом</strong> вашей любой марки  в <strong>автосалоне Адмирал-Моторс</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-05 06:15:59', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-05 06:15:59', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 769, 'купить в санкт-петербурге авто с пробегом, автосалоне адмирал-моторс, авто с пробегом', 'Скоро будут лишать прав за вождение в пьяном виде на срок до 10 лет', 1, 450, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(189, 256, 'Журнал Forbes назвал BMW влиятельнейшим автомобильным брендом', 'zhurnal-forbes-nazval-bmw-vliyatelnejshim-avtomobilnym-brendom', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Крупнейший концерн <strong>BMW </strong>Group занимает первое место среди автомобильных брендов в рейтинге самых влиятельных брендов этого года по статистике журнала Forbes.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения российского представительства автоконцерна, стоимость бренда  <strong>BMW </strong>оценивается в $26,3 млрд. В топе потребительского восприятия концерн <strong>BMW </strong>занял 5-е место в общем зачете и соответственно 1-е среди автомобильных брендов. Признанием самого высокого качества автомобилей <strong>BMW</strong> потребителями стало его лидерство в категории «услуги и продукты высокого качества» потребительского рейтинга.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если сравнивать бренды различных отраслей в общем зачете, то компания расположилась на 9-й строчке. Этот рейтинг возглавила корпорация Apple, стоимость которой составляет в $87,1 млрд. Второе место занимает компания Microsoft со стоимостью в $54,7 млрд. Далее расположилась Coca-Cola - $50 млрд. IBM, оцененный в $48,5 млрд. занимает 4-е место, Google 5-е место, его стоимость составляет $37,6 млрд. и Intel на 6-м месте с оценкой $32,3 млрд.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ключевыми критериями оценки в рейтинге журнала Forbes была выбрана прибыль компаний за последние 3 года и доля прибыли, т.е. роль которую играет та или иная марка играет в своей сфере. Затем были исследованы мнения 2000 потребителей по всему миру, которые оценивали по десяткам критериев почти 130 брендов, стоимость каждого из которых превышала $2,5 млрд. Данные исследования проводили консалтинговые фирмы PSB и Landor.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отличное место в таком влиятельном списке занимает <strong>BMW</strong>.  Всем фанатам автомобилей <strong>BMW</strong> мы предлагает посетить наш <strong>автосалон Адмирал-Моторс</strong> в котором вы можете совершить<strong> покупку поддержанного автомобиля</strong> по выгодной цене.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-05 06:32:22', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-05 06:32:22', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 768, 'BMW, автосалон адмирал-моторс, покупку поддержанного автомобиля', 'Концерн BMW Group - самых влиятельных брендов, оценивается в в $26,3 млрд', 1, 761, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(190, 257, 'Автомобильный бренд Fiat показал на автосалоне в Париже 3 новых модели Panda', 'vtomobilnyj-brend-fiat-pokazal-na-avtosalone-v-parizhe-3-novykh-modeli-panda', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span><strong>Fiat </strong>на Парижском Международном Автосалоне 2012 года показал модели <strong>Panda </strong>4x4, <strong>Panda </strong>Natural Power, <strong>Panda </strong>Trekking и три новых версии 500L.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения компании «Крайслер РУС», официальный дилер автоконцерна в России, на автосалоне выделилось семейство Panda с 3-мя мировыми премьерами: вариантами 4x4, Natural Power и Trekking. Они предназначены для того, чтобы закрепить лидерство модели в европейском сегменте A. На данный момент это семейство, абсолютно, самое большое в своем классе: три варианта по схеме привода, шесть вариантов по двигателю, пять вариантов отделки, три по коробке передач, 13 вариантов оформления салона и 12 цветов окраски кузова, обеспечивают более 850 возможных сочетаний.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Fiat Panda</strong> 4x4 преемник бестселлера, впервые появившегося почти 30 лет назад, сегодня это полноприводный автомобиль третьего поколения, обладающий прекрасным воплощением духа времени, которое доказывает возможность объединить совершенный дизайн, внедорожные способности с  эффективной, компактной конструкцией.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Natural Power является связующим звеном между полноприводным и неполноприводным вариантами, совмещая в себе совершенный привод на передние колеса с системой Traction+ и облик внедорожника. В модели используется передовая противобуксовочная система, которая улучшает управляемость на скользкой поверхности и полностью гарантирует прекрасную работу во время движения.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>500L – это автомобиль, поднимающий планку для своего сегмента: символ стиля, просторный внутри и компактный снаружи, экономичный в эксплуатации, необыкновенно универсальный и обладающий конкурентоспособной ценой.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На стенде в Париже были представлены три версии, оснащенные тремя важными новшествами, на европейском рынке которые появятся в ближайшие месяцы. Гости автосалона смогут увидеть новую премиальную систему класса Hi-Fi от Beats Audio, в которой применен усовершенствованный алгоритм компенсации, создающий кристально-чистое и четкое звучание.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Бренд <strong>Fiat </strong>не перестает нас радовать самыми современными новинками. <strong>Автосалон Адмирал-Моторс</strong> всегда пополняется новинками <strong>авто с пробегом</strong> как импортного так и отечественного производства. Зайдите прямо сейчас на наш<strong><a href="cars/" title="Сайт по продаже автомобилей"> сайт по продаже автомобилей</a></strong> и познакомьтесь с коллекцией автомобилей.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-06 07:06:25', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-06 07:06:25', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 767, 'Fiat, Fiat Panda, Panda, автосалон адмирал-моторс, авто с пробегом, сайт по продаже автомобилей', 'Новые модели Fiat Panda просто затмили все другие на Парижском автосалоне', 1, 571, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(191, 258, 'В Калуге с конвейра сошла 200-тысячная Skoda', 'v-kaluge-s-konvejra-soshla-200-tysyachnaya-skoda', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Юбилейным автомобилем калужского завода стал автомобиль <strong>Skoda Octavia</strong> – самая популярная модель марки в мире, в частности России.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения российского представительства чешского автоконцерна, известность марки <strong>Skoda </strong>в России набирает обороты с каждым днем: объем производства данной марки в Калуге превзошел отметку в 200 тыс. автомобилей. Юбилейная <strong>Octavia </strong>ярко-красного цвета 2 октября сошла с конвейера завода.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Классический дизайн, соотношение «цена-качество», просторный салон, ходовые характеристики и многообразие модификаций – все это представляют собой достаточно убедительный набор аргументов в пользу выбора именно этого автомобиля. В России Skoda Octavia реализуется в двух вариантах кузова: универсал и лифтбэк.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На рынке России <strong>Octavia </strong>предлагается с 4-мя бензиновыми двигателями, а универсал-версии Combi доступен с 2-литровым дизельным двигателем и мощностью 140 л.с.. Стартовая стоимость автомобиля<strong> Skoda Octavia</strong> в России составляет 564 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из истории, завод в Калуге ФОЛЬКСВАГЕН Груп Рус открылся 28 ноября 2007 года и стал по счету пятым заводом Skoda за границей. Сейчас с конвейера калужского предприятия сходят <strong>Skoda Fabia</strong> и <strong>Skoda Octavia</strong>. В прошлом году завод в Калуге вышел на полную мощность.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На сегодняшний день общий объем производства завода (включая автомобили VW) составляет 160 тыс. автомобилей в год. В ближайшем будущем концерн <strong>Volkswagen </strong>планирует повысить мощность завода в Калуге и выпускать до 220 тыс. автомобилей в год.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Самая популярная модель марки в России есть и у нас в <strong>автосалоне Адмирал-Моторс</strong>. У нас вы можете <strong>купить поддержанный автомобиль</strong> или<strong> продать авто с пробегом в Санкт-Петербурге</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-06 07:11:59', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-06 07:11:59', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 766, 'Skoda Octavia, Skoda, Skoda Fabia, Volkswagen, автосалоне адмирал-моторс, купить поддержанный автомобиль, продать авто с пробегом в Санкт-Петербурге', '200-тысячная Skoda Octavia сошла с конвейера в Калуге', 1, 1543, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(192, 259, 'Первая партия Volvo V60 Plug-in Hybrid была распродана до появления в салоне', 'pervaya-partiya-volvo-v60-plug-in-hybrid-byla-rasprodana-do-poyavleniya-v-salone', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Автомобильный бренд <strong>Volvo </strong>Car Corporation объявила о сенсации: первая партия гибридных инновационных автомобилей <strong>Volvo </strong>V60 Plug-in Hybrid распродана, еще не доставленная в салоны дилеров.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения компании, заказ на автомобили, которые будут поставляться только в 2013 году, идет полным ходом. После выпуска первой партии V60 Plug-in Hybrid2013 модельного года, начнется производство следующей партии – 2014 модельного года, количество автомобилей которой достигнет 5000 единиц. В Европе спрос очень высок, но часть этих автомобилей компания также планирует поставить в Россию.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>V60 Plug-in Hybrid – это первый в мире гибридный дизельный автомобиль с зарядкой аккумуляторов от обычной сети. Первая тысяча автомобилей  2013 модельного года поставляется в модификации «Pure Limited» с  окраской кузова - Electric Silver. Автомобиль с электромотором и дизельным двигателем отличают встроенные выхлопные трубы, 17-дюймовые диски, а также ряд элементов кузова, реализованных в блестящем черном цвете.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Водителю V60 Plug-in Hybrid предлагается на выбор режим движения – Hybrid («Гибридный режим»), Pure («Чистая энергия») или Power («Полная мощность») – одним нажатием кнопки на панели приборов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобиль в состоянии проехать до 50 км. исключительно на электротяге, у которой отсутствуют выбросы вредных веществ. При максимальной мощности электромотора и дизельного двигателя (440+200 Нм и 215+70 л.с.) автомобиль разгоняется с ходу до 100 км/ч за 6.1 секунды. В  режиме гибрида расход топлива составит 1,8 л/100 км.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новая интерактивная графическая панель приборов модели V60 Plug-in Hybrid с индивидуальным выводом информации разрешила добиться интуитивно-понятного управления системами автомобиля.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Гибридные дизельные автомобили компании <strong>Volvo </strong>Car Corporation пользуются огромной популярностью. А в <strong>автосалоне Адмирал-Моторс</strong> большой популярностью пользуются <strong>авто с пробегом</strong> таких марок как <strong>Skoda</strong>, <strong>Ford</strong>, <strong>Chevrolet </strong>и <strong>Volvo </strong>конечно. <strong>Цена на поддержанный автомобиль</strong> не высока и полнее доступна любому клиенту.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-07 08:37:26', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-07 08:37:26', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 765, 'Volvo, автосалоне адмирал-моторс, авто с пробегом, Skoda, Ford, Chevrolet, цена на поддержанный автомобиль', 'Инновационный гибридный автомобиль Volvo V60 Plug-in Hybrid настолько популярен что первая партия была распродана прежде чем появилась в дилерских салонах', 1, 908, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(193, 260, 'Концептуальный кроссовер Suzuki сегмента «С» S-Cross', 'kontseptualnyj-krossover-suzuki-segmenta-s-s-cross', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>На международном автомобильном салоне в Париже в этом году прошла премьера <strong>Suzuki </strong>S-Cross.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения российского представительства компании <strong>Suzuki</strong>, это концепткар, предвещающий выпуск компанией кроссовера абсолютно нового поколения в сегменте «C». Новый кроссовер - первый автомобиль в рамках программы по ежегодному выпуску новых моделей на рынке Европы, начиная со следующего года, и будет входить в ряд кроссоверов сегмента «С» с самым низким уровнем выбросов CO2.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Suzuki</strong> также планирует устанавливать на модель S-Cross систему полного привода новейшего поколения, разработанную компанией на базе многолетнего опыта по разработке внедорожных трансмиссий. Новая модель имеет широкий диапазон ездовых характеристик, позволяющих выбрать как экономичный и спокойный стиль вождения, так и более спортивную манеру и даже езду по снегу.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Возможности и характеристики нового автомобиля будут нацелены, в первую очередь, на комфорт и удобство. Одновременно с этим модель будет отличаться и высокой практичностью, за счет наличия одного из самых больших багажных отсеков среди кроссоверов сегмента «С», - отмечают в компании.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобили компании <strong>Suzuki </strong>очень популярны в нашем <strong>автосалоне Адмирал-Моторс,</strong> поэтому их очень быстро разбирают. У нас вы можете <strong>купить авто с пробегом в Санкт-Петерубрге</strong> самых разнообразных моделей и марок.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-07 08:41:21', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-07 08:41:21', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 764, 'Suzuki, автосалоне адмирал-моторс, купить авто с пробегом в санкт-петерубрге', 'Suzuki S-Cross - концепткар  абсолютно нового поколения в сегменте «C»', 1, 681, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(194, 261, 'Компания Honda показала новый Accord 9-го поколения', 'kompaniya-honda-pokazala-novyj-accord-9-go-pokoleniya', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В Москве прошла долгожданная премьера легендарного бизнес седана 9-го поколения <strong>Honda Accord</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По сообщению российского представительства компании <strong>Honda</strong>, в дилерских центрах новая модель появится уже в начале 2013 года. Стоимость и комплектации на девятое поколение автомобиля будут объявлены заблаговременно до начала продаж.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На протяжении 37 лет, <strong>Accord </strong>— это одна из ключевых для компании <strong>Honda </strong>моделей. Новое, девятое поколение автомобилей Honda Accord будет выпускаться с этого года. Для рынка России будут доступны две версии 3,5 (280 л.с) и 2,4 (180 л.с) и 6-тиступенчатой механической или ступенчатой автоматической трансмиссией. Старт продаж намечен на начало 2013 года в России.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Спереди <strong>Accord </strong>оснащен новой «агрессивной» решеткой радиатора, соединяющий вытянутые блоки фар головного света, и новый бампер с противотуманными фарами и ячеистой решеткой.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый <strong>Honda Accord</strong> — это единственный в своем сегменте автомобиль, укомплектованный двухламповыми светодиодными фарами, обеспечивающими лучшее распределение светового луча, а именно увеличивают обзорность и видимость в ночное время и содействуют энергосбережению почти вдвое, по сравнению с обычными галогеновыми лампами.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кузов имеет каплевидную форму, только положительно влияющую на аэродинамические характеристики автомобиля. Окна окаймлены хромированным ободом. Инженерам удалось улучшить обзорность за счет уменьшения размеров передних стоек.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для нового поколения <strong>Honda Accord</strong> был разработан совершенно новый дизайн интерьера. Новый 2,4-литровый двигатель i-VTEC Earth Dream Technologies на целых 3,5% экономичнее расходует топливо и на 4,5% легче, по сравнению с его предшественником.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый <strong>Honda Accord</strong> также оснащен технологически совершенными системами пассивной  и активной  безопасности.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый <strong>Honda Accord </strong>несомненно вдохновляет. <strong>Автосалон Адмирал-Моторс</strong> удивит вас ассортиментом <strong>поддержанных автомобилей</strong>. Нет возможности посетить наш автосалон, не беда, просто зайдите на наш <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> в Интернете и увидите наши автомобили, здесь же вы можете ознакомиться со всеми услугами, которые мы предоставляем.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-08 04:30:52', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-08 04:30:52', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 763, 'Honda Accord, Honda, автосалон адмирал-моторс, поддержанных автомобилей, сайт по продаже автомобилей', 'Honda Accord  9-го поколения никого не оставит равнодушным', 1, 946, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(195, 262, 'Не обыкновенная кампания бренда Mini', 'ne-obyknovennaya-kampaniya-brenda-mini', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Новая рекламная кампания, девиз которой «NOT NORMAL MINI» запускается по всему миру.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По официальному сообщению компании, целью проекта станет пропаганда индивидуальности, сочного творческого мировосприятия автовладельцев  <strong>Mini </strong> и их близкой эмоциональной связи со своими автомобилями. Слоган подчеркивает независимость и силу  <strong>Mini </strong> , проводя параллель между жизненным кредо его владельца и автомобилем.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Рекламная кампания, охватившая все континенты и страны, где представлен  <strong>Mini </strong> , будет состоять из массы акций. По всему миру любители автомобилей  <strong>Mini </strong> будут непосредственно вовлечены в творческие мероприятия и перформансы, показывающие только позитивное восприятие моделей  <strong>Mini </strong> и мира бренда.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Рекламная кампания NOT NORMAL стартует с видеороликом в сети, в котором ярким краскам индивидуальности, оригинальному мироощущению и выразительности  <strong>Mini </strong> противопоставляются серости будней. Видеоролик Bear («Медведь») — короткий юмористический сюжет. В печати будут публиковаться сюжеты харизматичных, смешных и необычных историй.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Главным каналом коммуникации станут социальные сети. В создании нескольких визуальных образов кампании принял участие Ральф Шмерберг.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> <strong>Mini </strong>никогда не оставляла нас без внимания и сейчас решила добавить позитивных ноток. Ассортимент наших <strong>авто с пробегом</strong> тоже не оставят вас без внимания, вы удивитесь насколько много в <strong>автосалоне Адмирал-Моторс</strong> самых различных моделей <strong>поддержанных автомобилей</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-08 04:34:37', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-08 04:34:37', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 762, 'Mini, авто с пробегом, автосалоне адмирал-моторс, поддержанных автомобилей', 'Рекламная кампания «NOT NORMAL MINI» совсем не обычная', 1, 434, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(196, 263, 'Kia Motors в списке - 100 лучших мировых брендов', 'kia-motors-v-spiske-100-luchshikh-mirovykh-brendov', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Корпорация <strong>Kia </strong>Motors находиться в рейтинге «100 лучших мировых брендов» по итогам исследования за текущий год специализированным консалтинговым агентством Interbrand.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По данным официального сообщения автоконцерна, рост ценности автомобильного бренда <strong>Kia</strong>, оцениваемый в 4,1 млрд. долларов США, разрешил компании уверенно занять 87-ю строчку в списке «100 лучших мировых брендов». Если сравнивать с 2011 годом рост бренда составил 50%, что существенно превышает средний показатель по автомобильной отрасли на 11%.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Исследование «Лучшие мировые бренды», которое проводиться ежегодно агентством Interbrand в этом году проводилось на основе тщательного анализа эффективности брендов экспертами-аналитиками. В исследовании учитывались финансовые показатели компаний в части возврата финансов инвесторам, роль бренда при принятии решения о приобретении, а также известность бренда, таким образом, оценивались факторы, на основе которых можно прогнозировать доходы компании.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Скоростной рост ценности бренда <strong>Kia </strong>на уровне с динамикой прироста продаж в мире. Корпорация <strong>Kia</strong> укрепила свой статус как одного из быстро растущих автопроизводителей, показывая в течение трех лет подряд рост продаж, в двузначной цифре. С 2008 по 2011 ежегодные продажи Kia по всему миру выросли на 81 % и достигли порядка 2,5 млн.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если вы предпочитаете автомобили <strong>Kia </strong>то не проходите мимо <strong>автосалона Адмирал-Моторс</strong>, который  славиться разнообразным ассортиментом <strong>автомобилей с пробегом</strong>. Мы поможем вам выбрать лучший автомобиль, который потом вы сможете <strong>купить авто б у в кредит</strong> по низкой процентной ставке.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-09 06:53:05', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-09 06:53:05', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 761, 'Kia, автосалона адмирал-моторс, автомобилей с пробегом, купить авто б у в кредит', 'Kia Motors вошла в «100 лучших мировых брендов» заняв 87 место', 1, 743, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(197, 264, 'В.В.Путин будет ездить на российском лимузине', 'vvputin-budet-ezdit-na-rossijskom-limuzine', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Президент РФ пообещал скоро сменить автомобиль на российский лимузин.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения в интервью телеканалу НТВ Владимир Владимирович Путин, скоро будет ездить на лимузине произведенного в России — на основе ЗИЛа.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Мы сделали российский лимузин. Сейчас проходят тесты», - сообщил президент.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На сегодняшний день премьер-министру и президенту страны предлагаются машины иностранного производства, закупающимися Федеральной службой охраны.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>А еще весной 2010 года было известно о том, что президент страны поручил детально изучить вопрос о  наладке выпуска новых правительственных лимузинов на базе ЗИЛа.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В январе 2012 года представители российского завода рассказали о том, что первые образцы правительственных лимузинов, возможно, появились бы через 2,5–3 года, а массовое производство началось бы через 3,5–4 года, напоминает «Интерфакс».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По данным РБК, от старого советского лимузина "ЗИЛ" для транспортирования президента РФ отказались еще в 1995-1996гг. Еще тогда все российские президенты ездили исключительно на бронированной версии <strong>Mercedes </strong>Pullman S600.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Уже давно известно, что президент РФ предпочитает автомобили российского производства. Мы тоже поддерживаем российский автопром, поэтому у нас вы можете <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> российского производства.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-09 07:00:33', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-09 07:00:33', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 760, 'ЗИЛ, автомобили российского производства, купить поддержанный автомобиль в санкт-петербурге, Mercedes', 'Президент РФ возможно пересядет на российский лимузин', 1, 486, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(198, 265, 'Цена бренда Hyundai остановилась на историческом максимуме', 'tsena-brenda-hyundai-ostanovilas-na-istoricheskom-maksimume', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Hyundai </strong>Motor сообщила о повышении стоимости бренда <strong>Hyundai </strong>на 24,4% всего за один год.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из заявления автоконцерна, общая стоимость бренда на сегодня составляет 7,5 млрд. долларов США, по данным исследования бренд-консалтингового агентства Interbrand - Best 100 Global Brands 2012 .</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Hyundai </strong>уже восемь лет подряд входит в рейтинг Interbrand. Компания в 2005 году занимала 84 место, сейчас поднялась на 53 позицию (из 100), и это исторический рекорд.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Рост стоимости бренда <strong>Hyundai </strong>на 24,4% существенно превысил средний для индустрии показатель в 11,2%. Но <strong>Hyundai </strong>отличилась не только в сфере автомобилей - бренд является одним из быстрорастущих в мире по всем категориям, как Google, Apple и Amazon.com.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Основу рейтинга Interbrand составляет финансовая отчетность компаний, и их маркетинговая активность. Помимо этого, в исследовании отражена потенциальная доходность каждого бренда.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Hyundai </strong>отлично движется вперед, не отставая, как и <strong>Адмирал-Моторс</strong>. Автосалон каждый день предлагает что-нибудь свеженькое из мира<strong> авто с пробегом</strong>.  К примеру, сегодня вы можете<strong> купить в Санкт-Петербурге поддержанный автомобиль</strong> марки Jeep Cherokee II.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-10 06:32:22', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-10 06:32:22', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 759, 'Hyundai, адмирал-моторс, авто с пробегом, купить в санкт-петербурге поддержанный автомобиль', 'Стоимость бренда Hyundai находится на историческом максимуме', 1, 634, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(199, 266, 'Новый Touareg Editiob X наконец-то показали', 'novyj-touareg-editiob-x-nakonets-to-pokazali', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Volkswagen </strong>к юбилейному 10-летию <strong>Touareg </strong>выпускает Touareg Edition X – автомобиль в самом эксклюзивном исполнении.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из информации российского представительства автоконцерна, новый <strong>Touareg </strong>Edition X выполнен с матовым или перламутровым эффектом в синем цвете с и укомплектован 19-дюймовыми легкосплавными дисками «Moab». Панорамный большой наклоняемый/сдвижной потолочный люк, биксеноновые фары со светодиодами, анодированные рейлинги серебристого цвета, а также задние блоки фонарей с затемнёнными рассеивателями как по волшебству создают яркий облик внедорожника.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кроме этого, в стандартной комплектации доступен «Chrome & Style» - пакет декоративной отделки, тонированные боковые и заднии стёкла, а также логотипы «<strong>Touareg </strong>X» на крыльях. Новая модель <strong>Volkswagen</strong> - изящное сочетание техники и безупречного стиля, - отмечают в компании.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для <strong>Touareg</strong> Edition X предлагается на выбор в пяти комбинациях двигателей/коробок передач, включая гибридную версию. Стоимость в Германии начинаются от 61025 евро за модель в базовой комплектации, которая оснащена 8-ступенчатой автоматической коробкой передач и двигателем V6 TDI BlueMotion Technology мощностью 150 Квт/204 л.с..</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В России <strong>Touareg</strong> Edition X ожидается в следующем году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если вы фанат автомобилей <strong>Volkswagen</strong> то добро пожаловать к нам. Наш автосалон предлагает вам <strong>купить авто с пробегом в СП</strong> (Санкт-Петербург) этой марки или любой другой на ваш выбор.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-10 06:36:16', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-10 06:36:16', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 758, 'Volkswagen, Volkswagen Touareg, купить авто с пробегом в СП, купить авто  в СП с пробегом', 'Новый эксклюзив от Volkswagen - Touareg Editiob X', 1, 626, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(200, 267, 'General Motors открывает двери нового центра Информационных технологий', 'general-motor-otkryvaet-dveri-novogo-tsenta-informatsionnykh-tekhnologij', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В рамках нового подхода в сфере предоставления ИТ-услуг компания <strong>General Motors</strong> (GM) открывает Центр информационных технологий в Уоррене, штат Мичиган.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По данным официального сообщения автоконцерна, в течение нескольких лет в центр примут на работу порядка 1 тыс. 500 специалистов в сфере ИТ. Центр информационных технологий  – это уже второе из четырех подразделений, планируемые открыть GM в США. В прошлом месяце компания сообщила о запуске аналогичного объекта в г. Остин, штат Техас, на 500 рабочих мест.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сейчас компания GM ведет набор сотрудников на должности, напрямую связанные с бизнес-аналитикой, разработкой программного обеспечения, управлением проектами и управлением базами данных. Рабочие места предоставляются как специалистам с опытом работы, так и выпускникам учебных заведений.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Интересно какие ИТ-услуги будет оказывать компания и чем они будут отличаться от услуг других компаний функционирующих в сфере ИТ. А мы базируемся на <strong>продаже поддержанных автомобилей</strong>, которые вы можете <strong>купить авто с пробегом в кредит</strong> или за наличные в <strong>автосалоне Адмирал-Моторс</strong>.</p>\n<p style="text-align: justify;"> </p>\n</p>', 1, 13, '2012-10-11 05:42:29', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-11 05:42:29', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 757, 'GM, General Motors, продажа поддержанных автомобилей, купить авто с пробегом в кредит, автосалоне адмирал-моторс', 'Центр информационных технологий компания General Motors открывается в штате Мичиган', 1, 579, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(201, 268, 'На АвтоВАЗе машины персонала будут «Просвечивать»', 'na-vtovze-mashiny-personala-budut-prosvechivat', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Чтобы не дать возможность вывозить краденое с завода, руководство <strong>АвтоВАЗа </strong>приняло координальные меры.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>С помощью тайников в автомобилях происходит наибольшее количество хищений, поэтому на одном из КПП АвтоВАЗ стартовал в работу досмотровый комплекс «ФИЛИН». Такая установка «просвечивает» машины, выявляя содержимое грузов, - повествует «АвтоСреда».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Источником рентгеновского излучения в устройстве является генератор, а не радиоактивные элементы. Все данные вывешены на специальном стенде рядом с установкой, у сотрудников имеются дозиметры, с помощью которых можно убедиться, что радиационный фон здесь не выше естественного уровня.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В планах компании поставить такие аппараты и на остальных КПП.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Правильно, так и надо бороться с воровством. В нашем автосалоне не продаются угнанные или краденные автомобили, автомобили <strong>автосалона Адмирал-Моторс</strong> чистые и со всеми документами. Поэтому не бойтесь<strong> купить поддержанный автомобиль в Санкт-Петербурге</strong> в нашем автосалоне.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-11 05:45:25', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-11 05:45:25', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 756, 'АвтоВАЗа, автосалон адмирал-моторс, купить поддержанный автомобиль в санкт-петербурге', 'Машины персонала АвтоВАЗа будут просвечивать', 1, 408, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(202, 269, 'В Париже прошла премьера концепт-кара Peugeot Onyx', 'v-parizhe-proshla-premera-kontsept-kara-peugeot-onyx', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">На Автомобильном салоне в Париже был продемонстрирован концепт-кар </span><strong style="text-align: justify;">Peugeot </strong><span style="text-align: justify;">Onyx.</span></p>', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">По данным российского представительства компании </span><strong style="text-align: justify;">Peugeot</strong><span style="text-align: justify;">, во время самого крупного автомобильного салона в мире суперкар Peugeot Onyx завоевал титул «Лучшего концепта» от ведущего автомобильного журнала Autoweek. Журнал подметил продуманность всех деталей концепта: помимо ошеломляющего дизайна, высокой эффективности концепт-кара и гибридной силовой установки с дизелем V8, находящимся в центре автомобиля,  Onyx отличился применением абсолютно новых материалов.</span></p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Легкий и прочный каркас из углеродного волокна кузова сочетает в себе углепластик и медь, образующие впечатляющий контраст. Интерьер автомобиля - пример экономичного использования материалов, оставляя только самое нужное для формирования частей салона.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Отделка интерьера полностью изготовлена из экологичного фетра, по истечении срока эксплуатации автомобиля он может быть целиком переработан.  Конструкция продумана следующим образом: в отделке нет швов и стыков, и она образует вокруг пассажиров своего рода кокон, и выполнят многочисленные функции, такие как теплоизоляция, шумоизоляция, образование конструкции пола, верхней консоли, сидений и потолка.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Панель управления концепт-кара выполнена из материала новейшего поколения – «бумажного дерева», образующегося в результате переработки старых газет. Полное сходство с деревом дополняется соответствующими рисунками, характерными для него. Однако при более внимательном рассмотрении секрет раскрывается, и напечатанные буквы становятся заметны.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Консоль из фетра, содержит элементы, которые состоят из высококачественного стекла. Топливный бак, уровень в котором отображается на верхней панели в виде линии с делениями, тоже содержит внутреннюю оболочку. Панель управления содержит в себе панель приборов, находящуюся на уровне головы водителя. Скорость движения автомобиля и частота вращения вала двигателя обозначаются исчезающими или возникающими металлическими деталями, похожими на клавиши фортепьяно. А работа гибридного привода отражается на цифровом дисплее.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Концепт-кар <strong>Peugeot </strong>Onyx действительно хорошо, также как <strong>авто с пробегом</strong> <strong>автосалона Адмирал-Моторс</strong>. Где <strong>покупка поддержанного автомобиля</strong> действительно может быть в радость.</p>\n<p> </p>', 1, 13, '2012-10-12 03:31:49', 792, '', '2012-10-12 03:37:35', 792, 0, '0000-00-00 00:00:00', '2012-10-12 03:31:49', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 755, 'Peugeot, авто с пробегом, автосалона адмирал-моторс, покупка поддержанного автомобиля', 'Новый  концепт-кар Peugeot Onyx с каркасом кузова из углепластика и меди', 1, 628, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(203, 270, 'Продажа легковых автомобилей Volkswagen впервые за свою историю превысила 4 млн.', 'prodazha-legkovykh-avtomobilej-volkswagen-vpervye-za-svoyu-istoriyu-prevysila-4-mln', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Новый рекорд компании <strong>Volkswagen</strong>: за первые три квартала 2012 года продано более 4 млн. легковых автомобилей.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По данным российского представительства автоконцерна, с января по сентябрь этого года крупнейший европейский автопроизводитель продал покупателям 4,21 млн. автомобилей. В сентябре владельцами автомобилей марки <strong>Volkswagen </strong>стали 492 тыс. 500.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В Азиатско-Тихоокеанском регионе за этот же период реализовано 1,68 млн. автомобилей, что на 16,4% больше показателей прошлого года. Из суммарного числа продаж на долю крупнейшего рынка в регионе – Китая  пришлось 1,52 млн. автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На американском континенте тоже наблюдался рост продаж. В Северной Америке поставки легковых автомобилей марки Volkswagen выросли на 26,1% и составили 456 тыс. 500 единиц.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В первую очередь, <strong>Volkswagen </strong>показала стабильный рост в США, где клиентам было передано 323 тыс. 100 автомобилей. В Южной Америке продажи подросли на 8,1% и равны 626 тыс. 200 автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Подразделением <strong>Volkswagen </strong>– это легковые автомобили в Европе за три квартала сначала 2012 года реализовано 1,30 млн. автомобилей, прирост составил 0,2%. Увеличение продаж марки удалось добиться в Восточной и Центральной Европе, где поставки увеличились на 32,1%, достигнув 199 тыс. 800.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В России, на крупнейшем рынке региона, сбыт вырос при этом до 123 тыс.  единиц. В Западной Европе (не учитывая Германии) за счет сложной рыночной ситуации объемы продаж понизились на 6,2%, и равны 653 тыс. 300.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По состоянию на сентябрь, отдельно в Германии марка <strong>Volkswagen</strong> реализовала 447 тыс.  новых автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если вы предпочитаете <strong>авто с пробегом </strong>марки <strong>Volkswagen </strong>то обязательно загляните в <strong>автосалон Адмирал-Моторс</strong>. У нас множество моделей <strong>Volkswagen </strong>и других брендов. У нас вы можете также <strong>продать </strong>свой старый <strong>поддержанный автомобиль </strong>и <strong>купить</strong> другой <strong>автомобиль</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-12 03:37:37', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-12 03:37:37', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 754, 'Volkswagen, авто с пробегом, автосалон адмирал-моторс, продать поддержанный автомобиль, купить  автомобиль', 'Новый рекорд компании Volkswagen - более 4 млн. легковых автомобилей продано за  три квартала 2012 года', 1, 648, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(204, 271, 'Депутаты внесли предложение о лишении прав на пожизненный срок', 'deputaty-vnesli-predlozhenie-o-lishenii-prav-na-pozhiznennyj-srok', '<p> </p>\n<p style="text-align: justify;"> </p>\n<p><span style="white-space: pre;"> </span>Специальная рабочая группа Госдумы готова в пятницу завершить подготовку законопроекта с жестким наказанием за вождение в пьяном виде.</p>', '<p> </p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Данный законопроект должен получить отзыв Верховного суда и правительства. Как повествуют РИА «Новости», после законопроект может быть рассмотрен и Госдумой.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> Первый зампред комитета по госстроительству и конституционному законодательству Вячеслав Лысаков предлагает ввести пожизненное лишение прав для водителей, сбивших двух и более человек в пьяном виде. По его словам, это предложение есть в законопроекте, который будет рассмотрен в Госдуме.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>"Человек, за плечами которого несколько унесенных жизней, иногда это 5-10 жизней, и который сел в пьяным виде за руль, у такого человека не должно быть морального права садиться за руль и пожизненно ему нужно оставаться пешеходом", - подметил Лысаков.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Персонал <strong>автосалона Адмирал-Моторс</strong> считает это правильным решением и поддерживает Лысакова. Ну а если вам ничего такого не грозит тогда не медлите и <strong>купите поддержанный автомобиль в Санкт-Петербурге</strong>.</p>\n<p> </p>', 1, 13, '2012-10-13 02:56:49', 792, '', '2012-10-13 03:22:25', 792, 0, '0000-00-00 00:00:00', '2012-10-13 02:56:49', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 753, 'автосалона адмирал-моторс, купите поддержанный автомобиль в санкт-петербурге, bibika ru, продажа  авто, продажа авто с пробегом', 'Депутаты предлагают лишать прав на пожизненный срок водителей-убийц', 1, 470, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(205, 272, 'За  предоставления услуг такси на неисправных машинах грозит штраф до 500 тыс. руб.', 'za-predostavleniya-uslug-taksi-na-neispravnykh-mashinakh-grozit-shtraf-do-500-tys-rub', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В Министерстве транспорта создали поправки к законодательству, которые позволяют многократно повысить штрафы для водителей, занимающихся частным извозом на неисправных автомобилях.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сейчас недобросовестный частник за этот вид правонарушения может выплатить не более 30-40 тыс. рублей. По сведениям, находящихся в распоряжение "Российской газеты", чиновники хотят повысить размер максимального штрафа до 500 тысяч. рублей</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По действующим законам, данный штраф полагается выплачивать лишь юридическим лицам. Разницу будут отменять.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Проверка за исправностью транспортных средств, применяемых для извоза, будет возложен на Росттранснадзор. Предполагается проводить плановые (рейдовые) проверки автобусов, частных автомашин и трамваев, - сообщает РБК.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Все автомобили <strong>автосалона Адмирал-Моторс</strong> исправны и вполне подойдут для извоза. Поэтому поторопитесь <strong>купить в Санкт-Петербурге авто с пробегом</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-13 03:00:15', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-13 03:00:15', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 752, 'автосалона адмирал-моторс, купить в санкт-петербурге авто с пробегом, купить авто с пробегом в санкт петербурге, купить авто в санкт-петербурге', 'Разработали поправки к законодательству,  которые позволяют повысить размер максимального штрафа до 500 тысяч. рублей', 1, 421, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(206, 273, 'Генеральным директором УАЗа стал нынешний гендиректор «СОЛЛЕРС»', 'generalnym-direktorom-uza-stal-nyneshnij-gendirektor-sollers', '<p style="text-align: justify; "><span style="white-space:pre"> </span>Генеральным директором ульяновского автомобильного завода стал Вадим Швецов, действующий генеральный директор ОАО «СОЛЛЕРС», который сменил Сергея Юрасова.</p>', '<p> </p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>По данным пресс-службы компании, решение о вступлении в должность директора Швецова связано с необходимостью разработки будущей стратегии развития автомобильного завода в Ульяновске, а также оперативной реализации принятых решений.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Главный акционер УАЗа, компания СОЛЛЕРС, также рассматривает вероятность перехода на механизм управляющей компании для своего предприятия. Эта форма менеджмента предполагает отдать основные функции управления УАЗом компании СОЛЛЕРС ее основным руководителям и подразделениям.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>«В настоящее время нам необходимо определить, каким <strong>УАЗ </strong>должен стать уже через 2-3 года, - сообщил Вадим Швецов, - потому нам придется тесно участвовать во всех бизнес процессах завода, включая выбор новой платформы и возможного партнера».</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Будем ожидать разработок будущей стратегии развития УАЗа, а тем временем <strong>купить авто в Санкт-Петербурге с пробегом</strong> отечественной марки вы можете в <strong>автосалоне Адмирал-Моторс</strong> на ул. Оптиков в Приморской районе. У нас большой ассортимент <strong>поддержанных автомобилей</strong>.</p>\n<div style="text-align: justify; "></div>\n<p style="text-align: justify; "> </p>', 1, 13, '2012-10-14 04:39:48', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-14 04:39:48', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 751, 'УАЗа, автосалоне адмирал-моторс, купить авто в санкт-петербурге с пробегом, поддержанных автомобилей', 'Генеральным директором УАЗа  стал Вадим Швецов', 1, 457, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(207, 274, 'SsangYong-Actyon-obladaet-novymi-optsiyami', 'ssangyong-actyon', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Кроссовер <strong>SsangYong</strong> Actyon получил целый пакет опций «Comfort».', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По данным российского представительства компании <strong>SsangYong</strong>, в состав данного пакета входят следующие системы безопасности: BAS (система экстренного торможения), ESP (электронная система стабилизации), HSA (система помощи при трогании на подъеме), ARP (система предотвращения опрокидывания автомобиля).</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Помимо этого, вариант «Comfort» Actyon дополнительно оснащается шторками безопасности, боковыми подушками безопасности, сеткой и шторкой  багажника.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Comfort» обеспечивает самую оптимальную безопасность для пассажиров и водителя  и доступен для любого совмещения типа двигателя, трансмиссии и привода. Стоимость этого пакета опций составляет 50 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Персонал <strong>автосалона Адмирал-Моторс</strong> считает, что главное в автомобиле это безопасность пассажиров и водителя. Поэтому мы продаем только безопасные<strong> авто с пробегом</strong>. Убедитесь в этом сами, посетив наш автосалон или <strong>сайт по продаже автомобилей</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-14 04:44:30', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-14 04:44:30', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 750, 'SsangYong, автосалона адмирал-моторс, авто с пробегом, сайт по продаже автомобилей', 'Кроссовер SsangYong Actyon оснащен пакетом опций «Comfort»', 1, 556, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(208, 275, 'Kia сeed_SW второго поколения появилась в продаже', 'kia-eed-sw-vtorogo-pokoleniya-poyavilas-v-prodazhe', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Kia </strong>Motors сообщила о старте продаж <strong>Kia ceed_sw </strong>следующего поколения.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения компании, с 1 ноября 2012 года он будет доступен для покупки во всех дилерских центрах <strong>Kia </strong>на российской территории. После выхода на российский рынок в 2007 году модели <strong>ceed_sw </strong>первого поколения было реализовано более 40 тыс. автомобилей, что представило модель в лидеры своего класса.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Дизайн нового <strong>ceed_sw</strong> представляет  эмоциональный и стремительный стиль. По своим пропорциям он напоминает трехдверный спортивный хэтчбек <strong>Kia pro_ceed</strong>, при этом считается одним из лучших в классе показателей по объемности салона. Как и удачно стартовавший <strong>ceed </strong>хэтчбек,  <strong>ceed_sw</strong> второго поколения предложит потребителю широкий выбор инновационного оборудования, которое только можно было найти в автомобилях класса премиум.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> Множественные технологические новинки снабдят водителя и пассажиров максимальным комфортом и удобством. Экономичный и мощный бензиновый двигатель объемом 1.6 литра в 129 л.с., русскоязычная навигационная система и адаптивные ксеноновые фары помогут без труда добраться до места назначения в любое время суток, интеллектуальная система парковки самостоятельно припаркует автомобиль по прибытию, а панорамная крыша разрешит увидеть все краски мира.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Второе поколение <strong>ceed_sw</strong> представлено в пяти уровнях оснащения: Comfort, Classic, Prestige, Luxe и Premium по цене от 679 тыс. 900 руб.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Заманчивое предложение <strong>Kia </strong>Motors о <strong>Kia ceed_sw</strong> следующего поколения. Не менее заманчивые предложения предлагает <strong>автосалон Адмирал-Моторс</strong>, в котором вы можете <strong>купить поддержанный автомобиль в Санкт-Петерубрге</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-15 07:42:01', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-15 07:42:01', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 749, 'Kia, ceed_sw, Kia ceed_sw, Kia pro_ceed, ceed, автосалон адмирал-моторс, купить поддержанный автомобиль в санкт-петерубрге', 'Стартовали продажи Kia ceed_sw следующего поколения', 1, 646, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(209, 276, 'Домашний кинотеатр появился в модели Cadillac SRX 2013 года', 'domashnij-kinoteatr-poyavilsya-v-modeli-cadillac-srx-2013-goda', '<p><span style="white-space: pre;"> </span>Интерактивная система CUE нового <strong>Cadillac</strong> SRX 2013-модельного года дополняется развлекательным пакетом для пассажиров задних сидений автомобиля.</p>', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">По информации российского представительства компании </span><strong style="text-align: justify;">Cadillac</strong><span style="text-align: justify;">, наряду с DVD-плеером и Blu-Ray состав новинки пополнится доработанным с учетом пожеланий маленьких пассажиров игровым комплектом, который стилизован под игровой джойстик с дистанционным пультом управления. Помимо этого, система будет иметь функцию раздельного просмотра медиафайлов на 8-дюймовых складных дисплеях.</span></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Развлекательно-информационная система Cadillac CUE поддерживает связь сразу с десятью подключенными мобильными устройствами – MP3-плеерами или телефонами – через Bluetooth; передача данных возможна также с помощью SD-карт или USB-портов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Нововведением для пассажиров задних сидений также является возможность подключения к системе CUE мобильных устройств и управление списками воспроизведения. Опционально можно приобрести специальный кабель, который позволят просматривать видеофайлы из медиаустройств.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Blu-Ray-плеер разрешает пассажирам наслаждаться видео в дороге такого же высокого качества, как у себя дома. Пассажир, располагающийся на переднем сиденье, и водитель могут смотреть видео на сенсорном 8-дюймовом экране системы CUE в центральной консоли в момент стоянки автомобиля. За счет воспроизведения звука через динамики аудиосистемы автомобиля, Cadillac SRX становится домашним кинотеатром на колесах.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Функция раздельного просмотра разрешает одному пользователю, к примеру, играть в видеоигры на одном экране, а другому смотреть фильм или слушать радио на втором экране.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ну, теперь <strong>Cadillac SRX</strong> оснащен по полному. А наши <strong>авто с пробегом</strong> уже оснащены всеми необходимыми вам опциями функциями. Так что вы без проблем можете <strong>купить авто с пробегом</strong> в <strong>автосалоне Адмирал-Моторс</strong> в Санкт-Петербурге.</p>\n<p> </p>', 1, 13, '2012-10-15 07:45:49', 792, '', '2012-10-15 07:49:44', 792, 0, '0000-00-00 00:00:00', '2012-10-15 07:45:49', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 4, 748, 'Cadillac, Cadillac SRX, авто с пробегом, купить авто с пробегом, автосалоне адмирал-моторс', 'Теперь домашний кинотеатр есть в   Cadillac SRX 2013-модельного года', 1, 786, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(210, 277, 'Уровень продаж в мире автомобилей Audi в сентябре 2012 года вырос на 13,6%', 'uroven-prodazh-v-mire-avtomobilej-audi-v-sentyabre-2012-goda-vyros-na-136', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Марка автомобилей <strong>Audi </strong>в сентябре этого года по всему миру продала около 136 тыс. 600 автомобилей — это на 13,6% больше, чем в 2011 году.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации российского представительства компании, за первые три квартала 2012 года целых 1 млн. 97 тыс. 500 покупателей выбрали автомобили <strong>Audi</strong>. В итоге, марка с четырьмя кольцами показала рост продаж на 12,8% по сравнению с аналогичным периодом 2011 года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В сентябре постоянный рост наблюдался в Китае (+20,5%) и в США (+26,5%). Помимо этого, продажи  <strong>Audi</strong> AG за сентябрь месяц выросли на 7,4% на рынке Европы, несмотря на трудную экономическую ситуацию в некоторых частях региона. В итоге марка Audi сохранила тенденцию увеличения продаж во всех регионах мира.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В сентябре текущего года высокие темпы роста продаж есть и в России: марка продала 3 тыс. 150 автомобилей, показав рост в 57,3% в сравнении с аналогичным периодом 2011 года. С начала 2012 года по сентябрь - 25 тыс. 442 российских потребителя стали обладателями новых автомобилей <strong>Audi</strong> - это больше на 42,9%.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В России такие цифры связаны с высоким спросом на модели автомобилей класса SUV. К примеру, <strong>Audi Q3</strong>, с момента премьеры, которой не прошло и года, уже лидер продаж среди всех моделей марки, лишь чуть-чуть опережая показатели <strong>Audi Q5</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если вы всегда предпочитали марку <strong>Audi </strong>то <strong>купить авто в Санкт-Петербурге с пробегом</strong> вам нужно обязательно в <strong>автосалоне Адмирал-Моторс</strong>. Ведь только у нас осенние скидки на <strong>авто с пробегом</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-16 04:16:38', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-16 04:16:38', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 747, 'Audi, Audi Q3, Audi Q5, купить авто в санкт-петербурге с пробегом, автосалоне адмирал-моторс, авто с пробегом', 'Audi реализует свои автомобили с большой скоростью', 1, 508, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(211, 278, 'Продажи BMW Group в сентябре побили все рекорды', 'prodazhi-bmw-group-v-sentyabre-pobili-vse-rekordy', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В сентябре текущего года концерн <strong>BMW </strong>Group добился увеличения продаж во многих регионах мира, еще раз побив все рекорды месячных продаж.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации официального сообщения автоконцерна, по всему миру было продано 177 тыс. 716 автомобилей марок <strong>BMW</strong>, <strong>Rolls-Royce</strong> и <strong>MINI</strong>. Спрос подрос на 11,6% в сравнении с таким же периодом прошлого года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Помимо этого, это лучшие три квартала этого года для компании — продажи повысились на 8,3% в сравнении с тем же периодом 2011 года. С начала 2012 года концерн <strong>BMW </strong>Group установил цифру - 1 млн. 335 502 тыс. мотоцикла и автомобиля.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В сентябре увеличение продаж автомобилей <strong>BMW </strong>по всему миру стал равен 14,3% - это 146 тыс. 843 автомобилей и мотоциклов. Спрос на <strong>BMW </strong>первой серии оставался стабильно высоким, и в сентябре было продано 23 тыс. 583 автомобиля – это на 51,1% больше в сравнении с 2011 годом.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сентябрь 2012 года для модели <strong>MINI</strong> стал самым удачным, так как было продано 30 тыс. 562 автомобиля, рост на 0,6%. С января по сентябрь включительно реализовано рекордное число автомобилей <strong>MINI </strong>— 223 тыс. 214 единица, что на 7,2% больше в сравнении с мировыми продажами за такой же период 2011 года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В сентябре подразделение Motorrad <strong>BMW </strong>добилось новых вершин розничных продаж — 9 тыс. 215 штук по сравнению с 8 тыс. 612 в 2011 году, рост на 7%. Но за период с января по сентябрь включительно отмечается некоторая убыль поставок мотоциклов и макси-скутеров всего 85 тыс. 944 единицы (–1,1 %).</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если вы всегда предпочитали марку <strong>BMW</strong>, то <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> вам нужно обязательно в <strong>автосалоне Адмирал-Моторс</strong>. Ведь только у нас осенние скидки на <strong>авто с пробегом</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-16 04:19:40', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-16 04:19:40', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 746, 'BMW, Rolls-Royce, MINI, купить поддержанный автомобиль в санкт-петербурге, автосалоне адмирал-моторс, авто с пробегом', 'BMW добился увеличения продаж в сентябре это на 11,6% больше', 1, 540, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(212, 279, 'Скоро в Москве пройдет Mersedes-Benz Fashion Week Russia - 25-я неделя моды', 'skoro-v-moskve-projdet-mersedes-benz-fashion-week-russia-25-ya-nedelya-mody', '<p />\n<p style="text-align: justify;" />В октябре с 18 по 22 число в Москве пройдет 25-я юбилейная Неделя моды под названием <strong>Mercedes-Benz</strong> Fashion Week Russia.', '</p>\n<p style="text-align: justify;">По информации российского представительства автоконцерна, специальным гостем станет топ-модель Джоан Смоллс. 25-я юбилейная Неделя Моды откроется на новой площадке, расположенной в ЦВЗ «Манеж». Ее участники представят коллекции сезона Весна – Лето 2013.</p>\n<p style="text-align: justify;">Как правило, наряду с хедлайнерами российской модной индустрии, Неделя Моды покажет ряд известных международных имен. Настоящей сенсацией будет показ Освальда Боатенга, британского дизайнера, обещающий помимо коллекции мужских костюмов, показать впервые в своей карьере женскую коллекцию.</p>\n<p style="text-align: justify;">Свои коллекции привезет в Москву еще один известный дизайнер Джайлс Дикон. За долгие годы работы в fashion-индустрии на его счету открытие бренда Giles, многочисленные звания «Дизайнера года», а также сотрудничество с  такими брендами, как Bottega Veneta, Louis Vuitton, Gucci и Emanuel Ungaro.</p>\n<p style="text-align: justify;">Особенным гостем <strong>Mercedes-Benz</strong> Fashion Week Russia станет одна из самых перспективных звезд мирового подиума Джоан Смоллс. Мировые авторитетные СМИ относят ее к «совершенно новой гвардии супермоделей».</p>\n<p style="text-align: justify;">Как обычно, Mercedes-Benz Fashion Week еще и площадка для демонстрации новых моделей автомобилей Mercedes-Benz. Публики будет показан легендарный автомобиль <strong>Mercedes-Benz</strong> G-Класса – G 63 AMG, усовершенствованный в 2012 году.</p>\n<p style="text-align: justify;">У входа в «Манеж» гостей будет встречать - совершенно новый GL 63 AMG, мировая премьера которого состоялась на Московском Международном Автосалоне. GL 63 AMG совмещает в себе комфортабельность салона первого класса, с динамикой спортивного седана AMG, что делает его уникальным в своем сегменте.</p>\n<p style="text-align: justify;">Всем любителям моды и автомобилей <strong>Mercedes-Benz</strong> обязательно следует посетить Неделю Моды. Также не стоит проходить мимо нашего <strong>автосалона Адмирал-Моторс</strong> если вы в ближайшее время собираетесь <strong>купить авто с пробегом в Санкт-Петербурге</strong>. У нас есть услуга, по которой вы сможете <strong>купить б у автомобиль в кредит</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-17 03:41:48', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-17 03:41:48', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 745, 'Mercedes-Benz, автосалона адмирал-моторс, купить авто с пробегом в санкт-петербурге, купить б у автомобиль в кредит', 'Неделя моды с Mercedes-Benz', 1, 695, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(213, 280, 'Усовершенствованную коробку передач установили на Lada Granta и Kalina', 'usovershenstvovannuyu-korobku-peredach-ustanovili-na-lada-granta-i-kalina', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>АвтоВАЗ начал производить автомобили с усовершенствованной механической коробкой передач.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в пресс-службе автоконцерна, ей оснащаются<strong> Lada Granta</strong> (за исключением версии "стандарт") и <strong>Lada Kalina </strong>первого поколения. Обновленная МКП более надежна, а главное – за счет оригинального механизма выбора передач существенно увеличилось качество их переключений. Помимо этого, МКП получила тросовый привод заменяя жесткие тяги, что увеличивает виброкомфорт автомобиля.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> Обновление механической трансмиссии АвтоВАЗ начал еще в сентябре 2012 года, когда компания перешла на многоконусный синхронизатор второй и первой передачи. Этот узел МКП стал долговечнее и надежнее, переключение теперь производится мягче.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> В новой коробке передач с заводским индексом "2181" употреблено верхнее расположение механизма выбора передач. За счет этого качество переключений не зависит от температуры масла в картере. Плюс снижен объем заливки масла до 2,2 литра, теперь используется полусинтетическое масло. Сам механизм выбора передач был разработан группой компаний Schaeffler.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> Импортер тросового привода - японская фирма Atsumitec. Аналогичные специальные троса, способные и тянуть и толкать, используются на большинстве иномарок подобного класса.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> Высокое качество работы обновленной МКП подтвердили эксперты фирмы "Рикардо", протестировавшие новый узел. Положительную оценку узлу также дали и инженеры Альянса Renault-Nissan.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Опробовать усовершенствованную МКП можно будет еще не скоро. А пока вы можете <strong>продать поддержанный автомобиль в СПб</strong> (Санкт-Петербург) или <strong>купить авто с пробегом</strong> в <strong>автосалоне Адмирал-Моторс</strong>.</p>\n<p style="text-align: justify;"> </p>\n</p>', 1, 13, '2012-10-17 03:45:01', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-17 03:45:01', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 744, 'Lada Granta, Lada Kalina, продать поддержанный автомобиль в спб, купить авто с пробегом, автосалоне адмирал-моторс', 'Усовершенствованной механической коробкой передач скоро будет устанавливаться на автомобили  Lada Granta и Lada Kalina', 1, 908, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(214, 281, 'LADA Samara - завершается производство вслед за классикой', 'lada-samara-zavershaet-proizvodstvo-vsled-za-klassikoj', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>АвтоВАЗ приготовился к завершению производства автомобиля <strong>LADA Samara</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«В 2012 году прекратили производство «семерки» и «четверки». 2012 год, вполне возможно, будет последним и для семейства автомобилей Samara. В итоге останутся только модели, обеспечивающие нам долю продаж в 25% к 2020 году», - рассказывает Артем Федосов, вице-президент по маркетингу и продажам ОАО «АвтоВАЗ»,  «АвтоСреде» .</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Представитель концерна подмечает: «Считаю, нам вполне по силам получить еще 5% рынка с такими бестселлерами как <strong>Kalina</strong>, <strong>Granta </strong>и <strong>Priora</strong>. <strong>Largus </strong>также в этом должен нам помочь. По информации российского производителя, в сентябре этого года в России реализовано 4 тыс. 342 автомобиля <strong>LADA Samara</strong> – это на 36,6% меньше в сравнении с августом этого же года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На сегодня трехдверные хэтчбеки Samara реализуются по цене от 272 тыс. 300 руб., автомобили модели 2114 - от 284 тыс. 300 рублей. Самая дорогая версия «самарского» седана стоит 292 тыс. 300 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если вы фанат отечественных автомобилей, то не расстраивайтесь, в <strong>автосалоне Адмирал-Моторс</strong> вы еще успеете <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> сейчас марки <strong>LADA Samara</strong>, через год или пять лет.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-18 09:30:23', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-18 09:30:23', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 743, 'LADA Samara, Kalina, Granta, Priora, Largus, автосалоне адмирал-моторс, купить поддержанный автомобиль в санкт-петербурге', 'АвтоВАЗ завершает производство автомобиля LADA Samara', 1, 806, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(215, 282, 'Акцию от Hyundai на преобретение хетчбэка и универсала i30', 'ktsiyu-ot-hyundai-na-preoretenie-khetchbehka-i-universala-i30', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>«Хендэ Мотор СНГ» объявил о промо-акции, по ней потребителям предоставляется скидка на модель i30 в кузовах хетчбэк и универсал.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в пресс-службе компании, до 31 декабря этого года на модель в двух вариантах предоставляется скидка до 50 тыс. рублей. Акция включает все топовые комплектации модели <strong>i30</strong>: CITIVISION  и STYLE  для хетчбэка, VISION  и EXPLORE  для универсала.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В комплектации CITIVISION потребителю предлагаются все средства комфорта и безопасности: семь подушек безопасности, светодиодные дневные ходовые огни, система адаптивного головного освещения на ксеноновых лампах, двухзонный климат-контроль с ионизатором воздуха, задние датчики парковки, камера заднего вида, навигационная система с 7-дюймовым экраном и др. Цена модели в комплектации CITIVISION с учетом скидки составит 899 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В комплектации STYLE части интерьера хетчбэка обшиты искусственной кожей, поставлен электромеханический ручной тормоз, электрорегулировки сиденья водителя, система Flex steering, регулятор усилия на руле, дефлекторы обдува пассажиров задних сидений. Цена модели с учетом скидки - 859 000 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Универсал в комплектациях VISION  и EXPLORE  тоже имеет большой набор опций. Стоимость на эти наиболее оснащённые комплектации вместе со скидкой составляет 899 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>У нас, в <strong>автосалоне Адмирал-Моторс</strong>, данные комплектации марки <strong>Hyundai</strong> можно купить еще дешевле, так как у нас представлены <strong>авто с пробегом</strong>. <strong>Покупка автомобиля с пробегом</strong> вашей мечты – станет выгодной покупкой.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-18 09:34:27', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-18 09:34:27', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 742, 'i30, автосалоне адмирал-моторс, Hyundai, авто с пробегом, покупка автомобиля с пробегом', 'Промо-акция от Hyundai на покупку модель i30 в кузовах хетчбэк и универсал', 1, 656, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(216, 283, 'Toyota Yaris находится в «десятке» европейских бестселлеров', 'toyota-yaris-nakhoditsya-v-desyatke-evropejskikh-bestsellerov', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span><strong>Toyota Yaris</strong> по результатам сентября вошла в «десятку» самых реализуемых в Европе моделей, став единственным в ТОП-10 представителем с ростом продаж.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В «Toyota Europe» рост продаж своего европейского бестселлера соединяют с запуском производства версии гибрид Toyota Yaris, которое запустилось на заводе во Франции в апреле этого года. По информации «Автостат», Yaris стал первым гибридом в самом массовом В-сегменте в Европе, а к 2020 году компания планирует оснастить гибридными установками весь свой модельный ряд, показанный на европейском рынке.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации исследовательской компании JATO Dynamics, в сентябре 2012 года самой продаваемой моделью в Европе является -<strong> Volkswagen Golf </strong>продажи которого составили 38 тыс. 669 машин (-21,4%). На втором месте обосновалась - <strong>Ford Fiesta</strong>, реализация которой снизилась на 13,5% по сравнению с прошлым годом до 31 тыс. 468 единиц. <strong>Opel</strong>/<strong>Vauxhall Corsa</strong> заняла третью строчку – продажи составили 30 тыс. 581 машину (-12,2%).</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На четвертом месте <strong>Ford Focus</strong> – 24 тыс. 61 реализованный автомобиль (-24,5%). Ну и замыкает «пятерку» лидеров - новый <strong>Peugeot 208</strong>, который приобрели себе 23 тыс. 225 европейских покупателя. В ТОР-10 также вошли <strong>Opel/Vauxhall Astra</strong> (22 тыс. 92 шт., -19%), <strong>Renault Clio</strong> (19 тыс. 988 шт., -29,3%), <strong>Volkswagen Polo</strong> (20 тыс. 571 шт., -35,3%), Toyota Yaris (17 тыс. 933 шт., +68,6%), <strong>Nissan Qashqai</strong> (18828 шт., -6,3%).</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Самым продаваемым автомобилем в Европе стал <strong>Volkswagen Golf </strong>как выяснилось. А в <strong>автосалоне Адмирал-Моторс</strong> наиболее продаваемые <strong>автомобили с пробегом</strong> импортного производства. У нас вы можете выбрать автомобиль на любой вкус и цвет и тут же <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-19 04:34:09', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-19 04:34:09', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 741, 'Toyota Yaris, Volkswagen Golf, Ford Fiesta, Opel Corsa, Ford Focus, Peugeot 208, Opel Astra, Renault Clio, Volkswagen Polo, Nissan Qashqai, автосалоне адмирал-моторс, автомобили с пробегом, купить поддержанный автомобиль в санкт-петербурге', 'Toyota Yaris на девятом месте в десятке самых продаваемых автомобилей этого года', 1, 851, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(217, 284, 'Subaru Forester tS начал продаваться в России', 'subaru-forester-ts-nachal-prodavatsya-v-rossii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Новый <strong>Subaru</strong> Forester tS стал доступен для заказа прямо через дилерские салоны.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В связи с этим, компания ООО Субару Мотор, являющийся официальным дистрибьютором Subaru в России и в Беларуси, прекращает прием заявок на покупку автомобилей SUBARU Forester tS путем online-бронирования. Как сообщили в компании, все ранее сделанные заявки online приняты и будут обработаны.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Forester tS - это автомобиль, сочетающий в себе качества практичного и комфортного кроссовера с драйвом и динамикой спортивного болида. Весь потенциал автомобиля может быть осуществлен исключительно при условии совместной работы всех 4-х колес. Проблема современных кроссоверов заключается в сложности организации этой работы.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Модели данного сегмента по традиции менее маневренны, что, как правило, дает задержку при сцеплении колес задней оси с дорожным покрытием. Данная задержка неблагоприятно влияет не только на поведение самого автомобиля, но и на находящихся внутри него: водитель не будет чувствовать, полный контроль, а пассажиры задних сидений будут чувствовать себя крайне неудобно.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Поэтому, наиболее важным фактором при разработке Forester tS стало - снабжение автомобиля стабильным поведением его задней части. Для этого автомобиль был оснащен рычагом и поперечной тягой задней подвески для реализации улучшенной курсовой управляемости и  устойчивости.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Subaru </strong>Forester tS – спортивный болид с большим потенциалом. <strong>Купить авто с пробегом</strong> марки <strong>Subaru </strong>вы должны именно в <strong>автосалоне Адмирал-Моторс</strong>, или оформить заявку на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-19 04:39:02', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-19 04:39:02', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 740, 'Subaru, купить авто с пробегом, автосалоне адмирал-моторс, сайт по продаже автомобилей', 'Subaru Forester tS теперь можно купить в официальном дилерском центре', 1, 575, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(218, 285, 'В Москве на Неделе Моды покажут Volvo V40 Cross Country', 'v-moskve-na-nedele-mody-pokazhut-volvo-v40-cross-country', '<p><span style="white-space: pre;"> </span>По информации российского представительства компании <strong>Volvo</strong>, мероприятие проходит два раза в год в Гостином Дворе при поддержке Правительства Москвы.</p>', '<p><span style="white-space: pre;"> </span>На этом мероприятии можно не только узнать о тенденциях и новинках моды, но и познакомиться с последними новшествами <strong>Volvo</strong> Cars.</p>\n<p> </p>\n<p><span style="white-space: pre;"> </span><span style="text-align: justify;">Основная премьера XXVIII сезона Недели Моды — стильный кроссовер </span><strong style="text-align: justify;">Volvo </strong><span style="text-align: justify;">V40 Cross Country. Обновленная модель класса C показывает уникальное сочетание экспрессивной элегантности и высокой проходимости, последних технологий в области автомобильной безопасности и яркой динамики.</span></p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span><strong>Volvo </strong>V40 Cross Country лидер в своем сегменте по комплекту инновационных систем, устанавливаемых в автомобиле: система контроля полосы движения плюс функция автоматического усилия на рулевом колесе, первая в мире подушка безопасности для обеспечения защиты пешеходов, уникальная система легкой парковки, система обнаружения транспортных средств, движущихся в поперечном направлении сзади автомобиля, активные фары дальнего света, система информирования о дорожных знаках и т.д.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>В Гостином Дворе также будут представлены другие модели Volvo — V60, S60, V60, S80, V60.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>Всем любителям марки <strong>Volvo </strong>и моды обязательно нужно попасть на эту Недели Моды. А если не успеваете, то заходите к нам в <strong>автосалон Адмирал-Моторс</strong> где насладитесь не только элегантными <strong>авто с пробегом</strong>, но и стильными и роскошными <strong>поддержанными автомобилями</strong>. В любом другом случае просто зайдите на наш <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> и взгляните на ассортимент, и вы не останетесь равнодушными.</p>\n<p> </p>', 1, 13, '2012-10-20 04:28:44', 792, '', '2012-10-20 04:35:52', 792, 0, '0000-00-00 00:00:00', '2012-10-20 04:28:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 739, 'Volvo, автосалон адмирал-моторс, авто с пробегом, поддержанный автомобиль, сайт по продаже автомобилей', 'Неделе Моды в Москве продемонстрирует  Volvo V40 Cross Country', 1, 667, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(219, 286, 'АвтоВАЗ будет производить двигатели и коробки передач для Renault и Nissan', 'vtovz-budet-proizvodit-dvigateli-i-korobki-peredach-dlya-renault-i-nissan', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Альянс <strong>Renault</strong>-<strong>Nissan </strong>предлагает производство двигателей, шасси и коробок передач для автомобилей Nissan и Renault российской сборки.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Об этом рассказал <strong>Renault </strong>Бруно Анселен, операционный директор региона "Евразия" группы. "В Тольятти, у нас в планах производство двигателей, шасси и коробок передач для автомобилей LADA и для автомобилей <strong>Nissan </strong>и <strong>Renault</strong>, которые Альянс выпускает в России. Качество компонентов растет", - подметил он.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как сообщают в пресс-центре ''''АвтоВАЗа'''', на предприятии все готовятся к производству новых моделей <strong>LADA</strong>: график обновления нового модельного ряда потребует еще большей мобильности.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>"Доказательство партнеров по Альянсу Renault-Nissan повысить долю в АвтоВАЗе раскрывает перед нами огромный доступ к ноу-хау и новейшим технологиям, к уникальному долговременному опыту лучших автостроителей мира. Отношение к нам государства и акционеров помогало нам не только в сложные времена, но и - что очень важно – они дают уверенность в будущем", - сообщил президент компании Игорь Комаров.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Мы за развитие АвтоВАЗа, за привнесение современных технологий и новшеств. Поэтому в нашем <strong>автосалоне Адмирал-Моторс</strong> всегда есть в наличие <strong>поддержанные автомобили</strong> выпущенным на АвтоВАЗе. Зайдите на наш <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> в Интернете и сами убедитесь в этом.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-20 04:32:29', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-20 04:32:29', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 738, 'Renault, Nissan, LADA, автосалоне адмирал-моторс, поддержанные автомобили, сайт по продаже автомобилей', 'Альянс Renault-Nissan теперь будут производить двигатели, шасси и коробки передач', 1, 718, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(220, 287, 'В сентябре 2012 года мировые поставки SKODA выросли на 3,3%', 'v-sentyabre-2012-goda-mirovye-postavki-skoda-vyrosli-na-33', '<p> </p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>Мировые поставки <strong>SKODA </strong>AUTO в сентябре достигли 83 тыс. 900 автомобилей  это на 3,3% больше показателя за аналогичный период прошлого года.</p>', '<p> </p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>По информации российского представительства автоконцерна, за первые девять месяцев  этого года компании удалось продать 717 тыс. 200 автомобилей, что на 7,9% выше по сравнению с таким же периодом 2011 года.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>«Даже в непростые экономические условия, в сентябре этого года нам удалось держать взятый курс. Ситуация в некоторых странах Европы остается трудной, но нам удается держать свои позиции и повышать долю рынка <strong>SKODA </strong>в этих регионах. Мы абсолютно уверены, что после появления лифтбека Rapid в октябре 2012 года успех будет сопутствовать нам и в дальнейшем», – заметил Вернер Эйхорн, член Совета директоров, а также ответственный за маркетинг и продажи.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>С каждым годом автомобилей приобретают все больше, население растет, также как и потребность в автомобилях. Но не все могут позволить себе <strong>купить новый<span style="white-space:pre"> </span> автомобиль</strong>, поэтому мы предлагаем <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong>.</p>\n<div style="text-align: justify; "></div>\n<p> </p>', 1, 13, '2012-10-21 05:24:44', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-21 05:24:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 737, 'SKODA, купить автомобиль, купить поддержанный автомобиль в санкт-петербурге', 'Мировые поставки SKODA AUTO в сентябре достигли 83 тыс. 900 автомобилей', 1, 603, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(221, 288, 'Vortex Tingo FL с «робот» трансмиссией появился в продаже', 'vortex-tingo-fl-s-robot-transmissiej-poyavilsya-v-prodazhe', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Рестайлинговые внедорожники TINGO теперь можно преобрести в версии с коробкой передач «робот». ', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации пресс-службы ТагАЗ (Таганрогского автомобильного завода), стоимость этой комплектации автомобиля равна 614 тыс. 900 р., что делает его одним из самых доступных кроссоверов с АКПП. Первые автомобили с роботизированной коробкой Tingo FL уже поступили в салоны дилеров ТагАЗ.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Список опций входящих в комплектацию AT6 стоимостью 614 900 р. (неполный):</p>\n<p style="text-align: justify;">\n<ul>\n<li>Кондиционер,</li>\n<li>ABS,</li>\n<li>Подушка безопасности водителя,</li>\n<li>Электростеклоподъемники всех дверей,</li>\n<li>Подушка безопасности пассажира,</li>\n<li>Электропривод зеркал сбоку,</li>\n<li>Гидроусилитель руля,</li>\n<li>Электрообогрев зеркал сбоку,</li>\n<li>Иммобилайзер,</li>\n<li>Электрокорректор фар,</li>\n<li>Алюминиевые легкосплавные диски 215/65 R16,</li>\n<li>Заднее складное сиденье 60:40,</li>\n<li>Подогрев сидений спереди,</li>\n<li>Центральный замок с дистанционным управлением,</li>\n<li>Задний парктроник,</li>\n<li>Сигнализация,</li>\n<li>Передние и задние противотуманные фары,</li>\n<li>USB-разъем,</li>\n<li>Мультифункциональный руль,</li>\n<li> 4 динамика,</li>\n<li>CD – проигрыватель на 1 диск.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Внутреннее зеркало заднего вида: альтиметр, компас, барометр.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Подметим, продажи рестайлинговой версии внедорожника начались в июне с версии МТ5 Lux. Стоимость в данной версии равна 584 тыс. 900 руб. Позже стала доступна комплектация МТ4 Comfort с ценой 559 тыс. 900 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В настоящее время линейку комплектаций дополнила версия с «робот» трансмиссией AT6 Lux. Автомобиль как обычно оснащается двигателем 1,8 л. мощностью 132 л. и передним приводом.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Достаточно дешевая цена на рестайлинговый внедорожник, теперь он будет доступен многим. Также как и ассортимент <strong>авто с пробегом</strong> автосалона Адмирал-Моторс. При <strong>покупке поддержанного автомобиля</strong> вас ждет приятная скидка.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-21 06:14:29', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-21 06:14:29', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 736, 'авто с пробегом, автосалона адмирал-моторс, покупке поддержанного автомобиля', 'Рестайлинговые внедорожники TINGO доступен с коробкой передач «робот»', 1, 626, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(222, 289, 'На Toyota Alphard в новых комплектациях начался прием заказов', 'na-toyota-alphard-v-novykh-komplektatsiyakh-nachalsya-priem-zakazov', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Уполномоченные партнеры и официальные дилеры ООО «Тойота Мотор» начинали принимать заказы на новые комплектации <strong>Toyota </strong>Alphard – «Люкс» и «Престиж Плюс».', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации компании, продажи новой для рынка России модели стартовали в феврале 2012 года, ознаменовав выход <strong>Toyota </strong>в сегмент минивэнов премиум-класса. Автомобиль отлично подходит для встречи делегаций и трансфера корпоративных клиентов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Комплектация «Престиж Плюс» оснащена:</p>\n<p style="text-align: justify;">\n<ul>\n<li>аудиосистемой с поддержкой CD/WMA/MP3/DVD на 8 динамиков,</li>\n<li>камерой заднего вида с динамической разметкой, </li>\n<li>навигационной системой с 8'''' цветным LCD экраном,</li>\n<li>Bluetooth,</li>\n<li>USB разъемами,</li>\n<li>трехзонный климат-контролем.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В «Люкс» комплектации капитанские кресла OTTOMAN второго ряда оснащены:</p>\n<p style="text-align: justify;">\n<ul>\n<li>электрорегулировкой в 4 направлениях, </li>\n<li>функцией памяти, </li>\n<li>подогревом,</li>\n<li>вентиляцией.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый формат воспроизведения Blue-ray доступен на мониторах задних пассажиров, плюс лампы индивидуального освещают салон. Обивка сидений изготовлена из перфорированной кожи. Особенностью этой комплектации, несомненно, является - наличие двух люков в задних и передних частях автомобиля.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Мощный бензиновый 3,5-литровый двигатель автомобиля, агрегированный с шестиступенчатой АКПП с ручным режимом переключения передач, имеет высокие показатели динамики. Максимальная мощность при 6200 об./мин. равна 275 л.с., а крутящий момент при 4700 об./мин. составляет 340 Н-м. Стандарт токсичности целиком соответствует нормам Евро IV.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Предлагаться купить <strong>Toyota </strong>Alphard в следующих цветах кузова на выбор: жемчужно-белый, черный и серебристый металлик. Сейчас автомобиль представлен в трех комплектациях – «Престиж», «Люкс» и «Престиж Плюс»  стоимостью 2 млн. 495 тыс., 2 млн. 782 тыс. и 2 млн. 568 тыс. рублей соответственно.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобиль представительского класса отлично подойдет для деловых встреч и делегаций. А для свободного вождения <strong>купите поддержанный автомобиль в СПб</strong> (Санкт-Петербурге) марки <strong>Toyota </strong>за приемлемую цену. У нас в наличии более семи <strong>автомобилей с пробегом</strong> данной марки.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-22 03:52:46', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-22 03:52:46', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 735, 'Toyota, купить поддержанный автомобиль в спб, автомобиль с пробегом', 'Наконец-то можно заказать Toyota Alphard в комплектациях «Люкс» и «Престиж Плюс»', 1, 684, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(223, 290, 'В январе 2013 года будет представлен Chevrolet Corvette следующего поколения', 'v-yanvare-2013-goda-budet-predstavlen-chevrolet-corvette-sleduyushhego-pokoleniya', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Бренд <strong>Chevrolet </strong>сообщил о дебюте Corvette седьмого поколения - 13 января следующего года в Детройте.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобильная марка для запуска обратного отсчета обнародовала новый логотип в виде двух  скрещенных флагов для нового Corvette 2014. По информации российского представительства компании, данный логотип был показан в Роуд Атланта, так как здесь Corvette отпраздновал победу в чемпионате American Le Mans Series GT 2012.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Члены команды <strong>Chevrolet </strong>победили в зачете производителей, а в командном зачете победила - команда Corvette Racing. Томми Милнер и Оливер Гейвин одержали победу в зачете пилотов завоевав четыре победы в девяти гонках Petit Le Mans, перед финалом ALMS сезона 2012 года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Подобные чемпионаты делают Corvette Racing успешнейшей командой в истории ALMS начиная с 2001 года: 77 побед в классе, 8 в зачете пилотов и 9 в зачете производителей. Помимо этого, с 2001 года команда одержала 7 побед в гонках «24 часа Ле-Мана».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для Corvette седьмого поколения новый логотип выполнен более технично в угловатой форме, с пропорциями, напоминающими облик нового автомобиля. Плюс, она детальней «прорисована», с большой глубиной цвета и насыщенными и яркими оттенками. Перед выбором окончательного варианта дизайна, было рассмотрено свыше 100 версий логотипа.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новая эмблема будет устанавливаться на новом <strong>Chevrolet </strong>Corvette 2014 модельного года, премьера которого состоится 13 января в Детройте. Показ модели Corvette будет проходить для аккредитованных представителей СМИ.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Будем с нетерпением ждать премьеры нового <strong>Chevrolet </strong>Corvette, одновременно выбирая для себя модели <strong>авто с пробегом</strong> в <strong>автосалоне Адмирал-Моторс</strong>. На нашем <strong><a href="cars/" title="Сайте по продаже автомобилей">сайте по продаже автомобилей</a></strong> представлены <strong>поддержанные автомобили</strong> проверенные временем.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-22 03:55:58', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-22 03:55:58', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 734, 'Chevrolet, авто с пробегом, автосалон адмирал-моторс, сайт по продаже автомобилей, поддержанные автомобили', '', 1, 690, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(224, 291, 'Первый автодром международного уровня заработал в Москве', 'pervyj-avtodrom-mezhdunarodnogo-urovnya-zarabotal-v-moskve', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>12 октября этого года начала свое функционирование Академия вождения AMG в Москве.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сейчас российские фанаты марки Mercedes-AMG смогут пройти обучение в России «Moscow Raceway» на первом автодроме международного уровня. По информации российского представительства компании <strong>Mercedes-Benz</strong>, AMG была основана еще в 2007 году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В программе обучения есть тренинги категории Emotion, испытания на легендарных гоночных трассах таких, как Хоккенхаймринг, «Северная петля» Нюрнбургринга и других,  специальные зимние мероприятия в Швеции. Настоящим событием обучающей программы является курс Masters, где все участники смогут выступить в роли пилотов автомобиля SLS AMG GT3, являющегося гоночной версии «крылатого» суперкара.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Обучающие тренинги проходят в разных странах мира – в США, Европе, Индии, Китае. С октября текущего года на карте Академия вождения AMG появилась еще одна точка – Россия.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В первые дни открытия автодрома «Moscow Raceway» представители российских СМИ и первые клиенты Академии смогут пройти базовый курс обучения под руководством немецких инструкторов. А в следующем году планируется проведение 4-х сессий Академии вождения с мая по октябрь.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Теперь поклонники марки  <strong>Mercedes-Benz</strong> смогут воплотить свои мечты, и прокатится с лучшими немецкими инструкторами, а также пройти полный курс обучения. В нашем<strong> автосалоне Адмирал-Моторс</strong> вы также можете пройти тест-драйв на понравившемся вам <strong>авто с пробегом</strong>, а уже после <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-23 05:17:00', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-23 05:17:00', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 733, 'Mercedes-Benz, автосалоне адмирал-моторс, авто с пробегом, купить поддержанный автомобиль в санкт-петербурге', 'Первый автодром «Moscow Raceway» Академия вождения AMG в Москве наконец-то открылся', 1, 576, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(225, 292, 'Россияне должны научиться культурно утилизовывать автохлам', 'rossiyane-dolzhny-nauchitsya-kulturno-utilizovyvat-avtokhlam', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>На развитие утилизации в сфере автомобильной промышленности обращены положения об утилизационном сборе, действующие в России с первого сентября 2012 года.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Списки участков, где можно будет утилизировать автомобиль, должны быть отправлены до 31 октября в Минпромторг. И после этого начнется работа.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации газеты «Ведомости», в отличие от импортеров, уплачивающих сбор деньгами, местные автопроизводители гарантируют безопасную утилизацию выпущенных на этой территории машин (в ином случае тоже платят сбор). Таким компаниям нужно иметь сеть точек по приему авто с пробегом, утративших потребительские свойства, охвачивая все субъекты России, и города с населением свыше 500 тыс. человек. Расходы по утилизации и приему автохлама производители должны брать на себя.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В начальном списке пунктов приема «Камаза» в основном дилеры, однако в некоторых регионах пришлось искать партнеров. Для утилизации компания может применять мощности завода «Ремдизель»  и региональных сервисных центров, плюс привлекать сторонние организации. У «АвтоВАЗа» в проекте списка участков приема — около 300 дилерских центров.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>У <strong>Toyota </strong>российских мощностей по утилизации нет, но могут появиться: Toyota Tsusho недавно сообщила, что хочет основать в Подмосковье перерабатывающий завод.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для устойчивого роста авторынка России в утилизационную инфраструктуру нужно вложить не менее $2 млрд., подсчитал Стэнли Рут, руководитель автомобильной практики PwC. Цифра эта получается так. В прошлом  году в России было 35,5 млн. автомобилей. Если авторынок России достигнет среднеевропейского уровня утилизации, это 6% от автопарка в год, вероятнее всего, так как треть машин в стране старше 15 лет, то России уже в эти годы необходимо будет ежегодно утилизировать не менее 2 млн. автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если наладится утилизация, то легко будет сдать <strong>поддержанный автомобиль</strong>, утративший потребительские свойства. А у нас, в <strong>автосалоне Адмирал-Моторс</strong>, вы можете <strong>продать поддержанный автомобиль в Санкт-Петерубрге</strong>, который еще не утратил потребительские свойства.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-23 05:19:59', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-23 05:19:59', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 732, 'Toyota, поддержанный автомобиль, автосалоне адмирал-моторс, продать поддержанный автомобиль в санкт-петерубрге', 'Утилизационная инфраструктура в России наладится в ближайшее время', 1, 628, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(226, 293, 'Бензин из воздуха научились делать британские ученые', 'benzin-iz-vozdukha-nauchilis-delat-britanskie-uchenye', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Ученым из британской компании Air Fuel Synthesis удалось изготовить бензин с помощью технологии, использующей для производства топлива - воздух, водород и углекислый газ.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По утверждению компании, она произвела за 2 месяца 5 л. топлива с помощью данной установки, которая создает топливо из паров воды и диоксида углерода. Из анонса, полученный бензин не отличается от обычного по своим потребительским свойствам, - сообщает РБК.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В компании полагаются, что в течение 2-х лет им удастся построить установку, способную производить тонну такого бензина в день. Кроме этого авторы идеи планируют реализовывать авиационное «зеленое» топливо, чтобы авиаперевозки стали более экологичными.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Несмотря на то что процесс получения синтетического бензина сейчас находится на начальном этапе развития и для него нужны большие затраты электроэнергии, персонал Air Fuel Synthesis полагает, что в конечном результате они смогут применять для получения топлива энергию из восстанавливаемых источников, таких как приливные электростанции или ветряные мельницы.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По словам лаборатории моделирования гидротермальных и гидрохимических процессов  в Институте аналитической химии и геохимии им. В.И. Вернадского, доктора химических наук Бориса Рыженко,  получить бензин из водорода и углекислого газа невозможно из-за противоречия этого процесса законом термодинамики, согласно которым энергия реакции должна быть не положительной, а отрицательной.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Бензин из воздуха не за горами, так что самое время запастись <strong>поддержанным автомобилем</strong> на нашем <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>. Огромный ассортимент и различные услуги автосалона Адмирал-Моторс не оставят вас незаинтересованными.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-24 02:57:23', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-24 02:57:23', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 731, 'поддержанный автомобиль, сайт по продаже автомобилей', 'Бензин из воздуха теперь не фантастика а самая настоящая реальность', 1, 462, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(227, 294, 'Крупнейшим экспортером стала китайская Lifan Industry', 'krupnejshim-ehksporterom-stala-kitajskaya-lifan-industry', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Группа компаний <strong>Lifan </strong>Industy Group состоит в TOP-100 лучших частных экспортеров Китая.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В ранее опубликованном рейтинге за прошлый год, составленном Китайским таможенным управлением, группа компаний <strong>Lifan </strong>Industrial Group из провинции Чунцин занимала 25 строчку. По информации российского представительства компании <strong>Lifan </strong>Motors, ежегодно в ТОP-100 включаются компании, которые зарегистрированы на сайте государственной таможни Китая.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>За 2011 год компания значительно увеличила объем поставок на экспорт. Это разрешило компании поменять 88 место на 25 строчку в списке экспортеров ТOP 500, изданном Министерством Торговли Китая в 2011 году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Подчеркнем, <strong>Lifan </strong>Industry Group — крупнейший автопроизводитель Китая, отличный известный и за его пределами, включая Россию, где <strong>Lifan </strong>является популярнейшим китайским автомобильным брендом. Группа компаний получила множества наград, в частности, в прошлом году <strong>Lifan </strong>получила звание - одной из самых известнейших экспортных марок Китая.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Крупнейший автопроизводитель Китая не остался незамеченным в <strong>автосалоне Адмирал-Моторс</strong>. В котором вы можете <strong>купить авто с пробегом в Санкт-Петербурге</strong> или <strong>продать поддержанный автомобиль</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-24 03:01:02', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-24 03:01:02', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 730, 'Lifan, автосалон адмирал-моторс, купить авто с пробегом в санкт-петербурге, продать поддержанный автомобиль', 'Группа компаний Lifan Industy Group заняла 25 строчку в TOP-100 лучших частных экспортеров Китая', 1, 649, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(228, 295, 'Автомобили марки Volvo будут «оповещать» друг друга о пробках', 'vtomobili-marki-volvo-budut-opoveshhat-drug-druga-o-probkakh', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span><strong>Volvo </strong>Car Corporation осуществляет необычный проект, в ходе него создаются средства «общения» между автомобилями для увеличения безопасности дорожного движения.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, <strong>Volvo </strong>вступила в консорциум CAR 2 CAR Communication Consortium, для того чтобы разработать единую платформу для «общения» между автомобилями в течение следующих 5 лет.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобили будут «общаться» по беспроводной сети, по поводу этого консорциуму была выделена отдельная частота, и применять существующие системы, к примеру, навигационную систему GPS.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Так, например, передатчик в светофоре оповещает водителя, с какой скоростью нужно ехать, чтобы по пути был только зеленый свет. Сделав остановку на красный сигнал светофора, водитель может узнать, сколько времени еще ждать до начала движения.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Специальная система предупреждает водителя о близости спецмашины, это позволяет ему заранее уступить дорогу для проезда автомобиля экстренной автомобилях службы. Данная функция полезна в ночное и вечернее  время, когда на спецмашинах стремятся не включать сирены, чтобы не тревожить жителей. Также это удобно, если водитель громко слушает музыку.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Датчики, поставленные на спецтехнике, заранее предупреждают водителя о дорожных работах. Также система поможет узнать о путях объезда, изменении скоростного режима и о том, какое расстояние необходимо преодолеть до конца ремонтируемой зоны.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Неисправные транспортные средства или медленно движущиеся передают предупреждение остальным участникам движения. Если водитель получает такую информацию заблаговременно, это позволяет ему сократить вероятность ДТП и избежать неприятных сюрпризов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Система остерегает о заторах на маршруте. В итоге, водители подготовлены к торможению, что соответственно сокращает риски столкновений.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Система оповещает о плохих погодных условиях, например. Система CAR 2 CAR сообщает о временных знаках или стационарных ограничения скорости. Светофор взаимодействует с автомобилями и оповещает водителя, не увидевшего красный свет светофора, световым или звуковым сигналом. К тому же эта система способна предупредить иных участников движения о нарушителе.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Volvo в плане технологий далеко ушла вперед. А <strong>автосалон Адмирал-Моторс</strong> тоже не останавливается на достигнутом и постоянно расширяет ассортимент <strong>авто с пробегом в Санкт-Петербурге</strong>. У нас вы можете<strong> купить поддержанный автомобиль</strong> по разумной цене и в отличном состоянии.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-25 04:48:32', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-25 04:48:32', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 729, 'Volvo, автосалон адмирал-моторс, авто с пробегом в санкт-петербурге, купить поддержанный автомобиль', 'Система CAR 2 CAR на Volvo сделает за вас всю грязную работу', 1, 542, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(229, 296, 'Hyundai показала новый кроссовер HB20X', 'hyundai-pokazala-novyj-krossover-hb20x', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>На международном автосалоне в Сан-Пауло компания <strong>Hyundai </strong>показала эксклюзивную модель марки HB20X, разработанную специально для Бразилии.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, этот кроссовер официально поступит в автосалоны в 2013 году. Автомобиль - часть эксклюзивной серии автомобилей специально для Бразилии, под именем НВ, означающее Hyundai Brasil.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В сентябре месяце Hyundai запустила продажи хетчбэк  HB20 - первого автомобиля серии НВ. Появление седана предвидится в первом квартале 2013 года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кроссовер HB20X, разработанный на основе хетчбэка НВ20, полностью отвечает запросам потребителей, предпочитающих активный образ жизни. Модель реализована в новой дизайн концепции марки - «струящиеся линии». Фары необычной формы имеют черный обвод, передний бампер кроссовера полностью обновлен.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Высококачественный салон HB20X различается от HB20 немного измененным блоком педалей, новым блоком климат-контроля, хромированной отделкой дверных ручек, а также двойной голубой прострочкой на сидениях и рулевом колесе.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>HB20X оснащен 128-сильным 1,6-литровым двигателем и обладает одним из лучших показателей производительности в своем классе. Потребители могут выбрать МКПП или автоматическую коробку передач.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кроссовер HB20X марки <strong>Hyundai</strong> обязательно понравится бразильцам, а россияне предпочитают <strong>покупать поддержанные автомобили в Санкт-Петербурге</strong> в <strong>автосалоне Адмирал-Моторс</strong>. Зайдите на наш <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> и вам сразу захочется стать обладателям автомобиля мечты.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-25 04:51:23', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-25 04:51:23', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 728, 'Hyundai, покупать поддержанные автомобили в санкт-петербурге, автосалоне адмирал-моторс, сайт по продаже автомобилей', 'Эксклюзивную модель марки HB20X теперь будет радовать бразильцев', 1, 859, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(230, 297, 'Автомобиль Honda - самый угоняемой в России', 'vtomobil-honda-samyj-ugonyaemoj-v-rossii', '<p><span style="text-align: justify; ">В топе, составленном ГК Цезарь Сателлит, лидирующие места как обычно достались автомобилям японских марок:</span></p>', '<ul>\n<li><strong>Хонда</strong> (<strong>Accord</strong>, <strong>Honda CR-V</strong>), </li>\n<li><strong>Тойота</strong> (<strong>Camry</strong>, <strong>Toyota LC</strong>), </li>\n<li><strong>Лексус </strong>(<strong>Lexus LX</strong>), </li>\n<li><strong>Мазда </strong>(<strong>Mazda 3</strong>), </li>\n<li><strong>Мицубиси </strong>(<strong>Mitsubishi Outlander XL</strong>). </li>\n</ul>\n<p><span style="white-space: pre;"> </span><span style="text-align: justify;">Если сравнивать с первым полугодием текущего года значительных изменений в предпочтениях угонщиков не обозначилось: как правило, самой большей популярностью пользуются автомобили японского производства, - сообщает finamauto.ru.</span></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Лидером из года в год остаются автомобили марки <strong>Хонда</strong>, остальные фавориты угонщиков поменяли позиции. <strong>Лексус </strong>с 5-го места теперь на втором, <strong>Мазда </strong>теперь на четвертом, потеснив <strong>Мицубиси </strong>на последнее место в пятерке. Еще рано подводить результаты, но специалисты Цезарь Сателлит думают, что делать выводы об угонных тенденциях этого года уже можно.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Важно отметить увеличенный интерес к «японкам» у угонщиков, это связано и с ростом популярности в среднем ценовом сегменте у автолюбителей, и с активным спросом на поддержанные детали. «Под разбор» угоняются, как правило, машины старше 3-х лет, иномарки 2012, 2011 года выпуска, по традиции, наряду с дорогими немецкими автомобилями попадают в категорию «заказников».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Можно также учесть рост числа угонов, реализовываемых путем мошеннических действий. Конкретно, внедорожники немецких автопроизводителей, к примеру, <strong>БМВ </strong>(<strong>BMW X6, Х5</strong>) и <strong>Мерседес </strong>(<strong>Mercedes ML</strong>), а также автомобили <strong>БМВ </strong>представительского класса.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>А самой <strong>продаваемым автомобилем</strong> в <strong>автосалоне Адмирал-Моторс</strong> является автомобили марки <strong>Мазда</strong>. О новых поступлениях этих и других <strong>поддержанных автомобилях</strong> вы можете узнать на нашем <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<p> </p>', 1, 13, '2012-10-26 05:27:25', 792, '', '2012-10-26 05:33:10', 792, 0, '0000-00-00 00:00:00', '2012-10-26 05:27:25', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 3, 727, 'Хонда Accord, Honda CR-V, Тойота Camry, Toyota LC, Лексус, Lexus LX, Мазда, Mazda 3, Мицубиси, Mitsubishi Outlander XL, БМВ, BMW X6, Х5, Мерседес, Mercedes ML, продажа автомобилей, автосалон адмирал-моторс, поддержанных автомобилях, сайт по продаже автомобилей', 'Угонщики предпочитают японские автомобили', 1, 750, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(231, 298, 'На основе LADA Granta разработали полурамный пикап', 'na-osnove-lada-granta-razrabotali-poluramnyj-pikap', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>АвтоВАЗ показал восемь автомобилей <strong>LADA</strong>, включая новую <strong>LADA Kalina</strong>, на 17-й специализированой выставке MOBI 2012, проходившей в Краснодаре в конце октября.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Одна из новшеств от <strong>LADA </strong>– полурамный пикап, выполненный на базе<strong> LADA Granta</strong>, премьерованный тольяттинской фирмой «Вис-Авто». По информации пресс-центра автоконцерна, серийные автомобили (<strong>LADA Priora</strong>, <strong>LADA Kalina</strong>, <strong>LADA Largus</strong> и LADA Granta c «автоматом») показывает официальный дилер - ОАО «Кубань-Лада».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>ГК «Супер-Авто» представляет мелкосерийный <strong>LADA Largus</strong> - «Такси»: у машины полная гамма необходимого оборудования и специальная окраска. В рамках MОBI 2012 данный автомобиль вместе с <strong>LADA Kalina</strong> и <strong>LADA Granta</strong> участвует в конкурсе «Современное такси – Краснодару». Путем народного голосования определятся наиболее удобные, востребованные и качественные машины, которые появятся в таксопарках города.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Заметим, н овая <strong>LADA Kalina</strong> была представлена на Московском автосалоне – автомобиль понравился и гостям MOBI 2012. В планах у АвтоВАЗа - старт продаж данных автомобилей летом следующего года. У обновленной <strong>LADA Kalina</strong> - оригинальный дизайн экстерьера и салона, более расширенный опциональный состав, новые варианты шасси и силового агрегата, способствующее более активному вождению.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Купить поддержанные отечественные автомобили</strong> LADA вы можете в <strong>автосалоне Адмирал-Моторс</strong>. У нас вы можете также <strong>продать поддержанный автомобиль в СПб</strong> (Санкт-Петербурге).</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-26 05:33:40', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-26 05:33:40', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 726, 'LADA, LADA Kalina, LADA Granta, LADA Priora, LADA Kalina, LADA Largus, купить поддержанные  автомобили, автосалоне адмирал-моторс, продать поддержанный автомобиль в спб', 'Полурамный пикап на базе LADA Granta показан тольяттинской фирмой «Вис-Авто»', 1, 765, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(232, 299, 'Chevrolet покажет на SEMA Show версию Malibu «с зарядом»', 'chevrolet-pokazhet-na-sema-show-versiyu-malibu-s-zaryadom', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>На выставке SEMA Show 2012 <strong>Chevrolet </strong>покажет самые широкие за всю историю возможности персонализации и производительности автомобилей.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобильное ежегодное тюнинг-шоу открывается 30 октября в Лас-Вегасе. По информации в российском представительстве марки, новый концепт заряженного <strong>Malibu </strong>Impala и Turbo 2014 будет показан на выставке.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>С турбомотором 2,0 л. с мощностью 259 л.с. автомобиль Malibu Turbo 2013 разгоняется до 100 км/ч за 6,3 сек. и развивает максимальную скорость в 250 км/ч. Концепт <strong>Malibu </strong>Turbo Performance выделяется агрессивным внешним видом; кузов покрыт Berlin Blue Tricoat, отделка интерьера в 2-х цветах Jet Black/Navy Blue. Концепт оснащен стойками HiPer Strut передней подвески и особенными пружинами задней подвески, составными тормозными перфорированными дисками, многопоршневыми тормозными суппортами и системой впуска охлаждаемого воздуха.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Абсолютно новая Impala 2014 – это флагманский полноразмерный седан от <strong>Chevrolet </strong>с  серьезной переработкой: более стильный и технологичный. Полностью переработан дизайн интерьера, управляемые воздушные заслонки решетки радиатора, скульптурные боковины кузова, 18-дюймовые колесные диски, ксеноновые фары, дневные ходовые и светодиодные огни  идут стандартно в комплектации LTZ.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В пакет активной безопасности входят: система OnStar, 10 подушек безопасности, другие системы, как адаптивный круиз-контроль, система помощи при экстренном торможении, система предупреждения о выходе из занимаемой полосы, система предупреждения о попутном столкновении, система контроля «слепых» зон, система помощи при езде задним ходом, система помощи при парковке и камера заднего обзора.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В 2012 году на выставке SEMA <strong>Chevrolet </strong>будут представлены концепты <strong>Spark</strong>, <strong>Cruze </strong>и <strong>Sonic</strong>. Эти автомобили включают в себя:</p>\n<p style="text-align: justify;">\n<ul>\n<li>Spark Sinister. Концепт с агрессивным внешним видом совмещает в себе элементы дизайна экстерьера, взятые у гоночных автомобилей, с кузовом в черном цвете.</li>\n<li>Spark Z-Spec. Кконцепт оснащен фирменным пакетом аксессуаров,  придающим Spark особый характер.</li>\n<li>Sonic Z-Spec 1. Концепт седана Sonic со спортивным уклоном совмещает в себе эстетику Z-Spec и прекрасные ходовые качества; оснащен тормозной системой большей мощности и увеличенной системой выпуска. </li>\n<li>Cruze Upscale. Компактный седан имеет лакокрасочное покрытие кузова Burnished Brandy   Metallic и уникальный дизайн экстерьера. В отделке интерьера применяется кожа коричневого цвета. </li>\n<li>Sonic Z-Spec 2.5. Концепт седана Sonic Z-Spec 2.5 с хорошей динамикой, дополненная агрессивным обликом, за счет аксессуаров Chevrolet Accessories и Chevrolet Performance.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Посетить выставку ради лицезрения новых концептов от <strong>Chevrolet </strong>стоит. Также вам обязательно нужно посетить <strong>автосалон Адмирал-Моторс</strong> чтобы увидеть весь ассортимент <strong>поддержанных автомобилей</strong> и если понравится <strong>купить авто с пробегом в СПб</strong> (Санкт-Петербург).</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-27 06:13:28', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-27 06:13:28', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 725, 'Chevrolet, Malibu, Spark, Cruze, Sonic, автосалон адмирал-моторс, поддержанных автомобилей, купить авто с пробегом в спб', 'Chevrolet представит новый концепт заряженного Malibu Impala и Turbo 2014 и концепты Spark, Cruze и Sonic', 1, 665, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(233, 300, 'Концепт Volkswagen Taigun теперь оснащен новым двигателем TSI', 'kontsept-volkswagen-taigun-teper-osnashhen-novym-dvigatelem-tsi', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В Бразилии на международном автосалоне марка <strong>Volkswagen </strong>впервые показала концепт компактного кроссовера Taigun.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, с точки зрения техники, новый концепт Taigun близок к самой современной группе - New Small Family. Длина Taigun - 3859 мм., высота – 1570 мм., ширина – 1728 мм. Увеличенная колесная база (2470 мм.), короткие свесы – все это придает кузову элегантные пропорции.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Четырехместный концепт-кар оборудован новым 1,0-литровым двигателем TSI с мощностью 81 кВт/110 л.с., который обеспечивает  высокий крутящий момент 175 Н•м при 1500 об/мин.  Двигатель с турбонаддувом и непосредственным впрыском топлива концепта Taigun расширяет линейку бензиновых трехцилиндровых двигателей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Концепт-кар, с весом 985 кг, развивает максимальную скорость до 186 км/ч и разгоняется за 9,2 сек. В смешанном цикле расход топлива составляет около 4,5 л./ 100 км.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>За счет высокого крутящего момента и большого дорожного просвета <strong>Volkswagen </strong>Taigun, оснащенный шинами размерностью 205/50 R17 и 17-дюймовыми легкосплавными колёсными дисками, хорошо подходит для путешествий по бездорожью. Кроме того, автомобиль оборудован электронной системой поддержания курсовой устойчивости (ESC), дополняя систему активной безопасности.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Volkswagen </strong>марка, наиболее продаваемая в Европе и в России, поэтому концепт <strong>Volkswagen </strong>Taigun не будет исключением. У нас вы можете <strong>купить авто в Санкт-Петербурге с пробегом</strong> любой марки отечественного и импортного производства.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-27 06:19:02', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-27 06:19:02', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 724, 'Volkswagen, купить авто в санкт петербурге с пробегом', 'Volkswagen впервые показала концепт компактного кроссовера Taigun', 1, 945, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(234, 301, 'Комплектации нового Honda CR-V представлены для российского рынка', 'komplektatsii-novogo-honda-cr-v-predstavleny-dlya-rossijskogo-rynka', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>После официальной российской премьеры кроссовера CR-V спустя два месяца компания <strong>Honda </strong>показала комплектации 4-го поколения модели.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации российского представительства марки, новый <strong>Honda CR-V</strong> будет доступен на  рынке России в двух комплектациях: Lifestyle и Elegance. Модель будет оснащена 2-х литровым 150-ти сильным двигателем 2,0 SOHC c максимальным крутящим моментом 190 Нм и c системой управления клапанами I-VTEC.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Комплектация Elegance будет доступна с 5-ти ступенчатой АКПП и 6-тиступенчатой МКПП. Базовая комплектация будет, оснащается двухзонным климат- и круиз-контролем, электроприводом, подогревом передних сидений,  и обогревом зеркал. В комплектациях с АКПП рулевое управление будет оборудовано подрулевыми лепестками переключения передач. Мультимедийное оборудование содержит в себе СD/MP3 магнитолу с управлением на руле, USB-разъем с поддержкой IPOD, 4 динамика, разъем AUX.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В базовую комплектацию входят центральный замок с дистанционным управлением, 8 подушек безопасности, иммобилайзер. За активную безопасность отвечают:</p>\n<p style="text-align: justify;">\n<ul>\n<li>Система предупреждения об экстренном торможении,</li>\n<li>Система ABS,</li>\n<li>VSA - Система курсовой устойчивости,</li>\n<li>EBD - Система распределения тормозных усилий,</li>\n<li>TSA - Система стабилизации прицепа,</li>\n<li>EPS - Адаптивный электроусилитель руля,</li>\n<li>DWS - Индикатор давления в шинах.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Комплектация Lifestyle будет предлагаться с 6-тиступенчатой МКПП и 5-ти ступенчатой АКПП. Здесь к ранее перечисленному добавляется: электропривод складывания зеркал, поясничный подпор сиденья переднего пассажира и водителя, датчик дождя, передними противотуманными фарами. Для безопасной парковки эта комплектация имеет камеру заднего вида, задние и передние датчики парковки, автонаклон зеркал при езде задним ходом. CD/MP3 магнитола оснащена 6 динамиками, HFT - функцией беспроводного подключения телефона.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>4-е поколение <strong>Honda CR-V</strong> предлагается в 9-ти цветовых гаммах.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Honda CR-V</strong> 4-го поколения наконец-то доступна для приобретения. А в <strong>автосалоне Адмирал-Моторс</strong> вы можете <strong>купить поддержанный автомобиль в СПб</strong> (Санкт-Петербурге) марки <strong>Honda </strong>по привлекательной цене.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-28 05:01:40', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-28 05:01:40', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 723, 'Honda, Honda CR-V, автосалоне адмирал-моторс, купить поддержанный автомобиль в спб', 'Honda CR-V 4-го поколения предвосхищает все ожидания', 1, 700, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(235, 302, 'Автомобили Honda тоже будут «общаться» с другими автомобилями', 'vtomobili-honda-tozhe-budut-obshhatsya-s-drugimi-avtomobilyami', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Honda </strong>показала свою интеллектуальную систему общения транспортных средств на 19-м Всемирном Конгрессе 2012 по транспортным интеллектуальным системам в Вене под названием Car and Motorcycle Intelligent System.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в официальном сообщении компании, участники конгресса смогут также лично проверить новые модели мотоцикла ITS Honda motorcycle (NC700X) и автомобиля Honda’s ITS. С помощью системы автомобили смогут «общаться» друг с другом, сообщая скорость, местоположение друг другу и придорожным ИТС-станциям, что позволит им получать информацию о движении, которая сообщит о:</p>\n<p style="text-align: justify;">\n<ul>\n<li>дорожных работах, </li>\n<li>неисправных автомобилях впереди,</li>\n<li>опасном направлении,</li>\n<li>о плохой погоде,</li>\n<li>о пробке впереди,</li>\n<li>о необходимости экстренного торможения,</li>\n<li>о приближении мотоциклиста. </li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Необходимо отметить, что <strong>Honda </strong>подписала Меморандум о взаимопонимании с другими автопроизводителями об этой технологии ИТС, которая станет реальностью уже в ближайшем будущем. Совместные проекты <strong>Honda</strong> в Европе, США и Японии уже продемонстрировали потенциал ИТС-системы.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ну что ж, <strong>Honda </strong>радует нас своими событиями, как отмечали в предыдущей новости о новинках кроссовера CR-V 4-го поколения. Всем любителям автомобилей <strong>Honda </strong>обязательно нужно попасть в <strong>автосалон Адмирал-Моторс</strong>, где вы сможете тут же <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> или <strong>продать авто с пробегом</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-28 05:04:57', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-28 05:04:57', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 722, 'Honda, автосалон адмирал-моторс, купить поддержанный автомобиль в санкт-петербурге, продать авто с пробегом', 'Интеллектуальную систему общения транспортных средств от Honda -  разрешит все непредвиденные ситуации', 1, 584, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(236, 303, 'Импорт машин с пробегом в России поднялся на 74,9%', 'import-mashin-s-probegom-v-rossii-podnyalsya-na-749', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Авторынок России занимает второе место в Европе вслед за Германией.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации директора департамента сельскохозяйственного машиностроения и автомобильной промышленности Минпромторга РФ Тимура Микая, Россия имеет высокий потенциал для развития продаж и производства легковых автомобилей, так как уровень автомобилизации в стране равен 243 авто на 1 тыс. жителей. Этот показатель вдвое меньше, чем средний показатель по Европе – 477 автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации Минпромторга РФ, в январе-сентябре на рынке России продано 2 млн. 223,5 тыс. легковых автомобилей – это на 17,7% больше, чем в 2011 году. Самым быстрорастущим сегментом является импорт <strong>подержанных машин</strong>, возросший на 74,9% до 159,1 тыс. штук.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как подмечает аналитическое агентство «Автостат», продажа новых импортных иномарок составляет 629,1 тыс. машин (+6,9%). По поводу произведенных в России легковых автомобилей - продажи иномарок за 9 месяцев возросли на 37,2% до 1 млн. 8,4 тыс. единиц. А вот продажа автомобилей отечественных брендов понизилась на 10,2% до 426,8 тыс. штук.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В <strong>автосалоне Адмирал-Моторс</strong> вы всегда можете <strong>купить подержанный автомобиль</strong> отечественных брендов так и импортных брендов. Достаточно зайти на наш<strong><a href="cars/" title="Сайт по продаже автомобилей"> сайт по продаже автомобилей</a></strong> и ознакомиться с ассортиментом <strong>авто с пробегом</strong>, а также со всеми предоставляемыми услугами.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-29 07:03:59', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-29 07:03:59', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 721, 'подержанная машина, автосалон адмирал-моторс, купить подержанный автомобиль, сайт по продаже автомобилей, авто с пробегом', 'Импорт авто с пробегом в России поднялся на 74,9%', 1, 444, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(237, 304, 'Более 57,6 тыс.  подписей собрала ФАР за отмену транспортного налога', 'bolee-576-tys-podpisej-sobrala-fr-za-otmenu-transportnogo-naloga', '<p> </p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Инициативу по сбору подписей за отмену транспортного налога Федерации Автовладельцев России (ФАР) поддержали более 57,6 тыс. человек.</p>', '<p> </p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Сбор подписей за аннуляцию «не обоснованного и не справедливого транспортного налога, существующий в РФ, противоречит здравому смыслу» еще идет. С призывом к водителям подсобить инициативу ФАР обратилась в середине октября 2012 года и за две недели масса автовладельцев откликнулись на него.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Для внесения инициативы по пересмотру и отмене системы взимания Транспортного налога нужно собрать 100 тыс. подписей. Всех желающих принять участие в акции необходимо внимательно ознакомиться с инструкцией для автовладельцев, которые участвуют в акции линией защиты и гражданского неповиновения на сайте far-msk.ru.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Эта проблема относится к 30 млн. автовладельцам, которых незаконно обязывают каждый год платить этот налог.</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Федерация Автовладельцев России призывает отменить Транспортный налог, потому, что он:</p>\n<p style="text-align: justify; ">\n<ul>\n<li>Необоснованный. Сегодня Транспортный налог -  это имущественный налог, значит должен рассчитываться исходя из цены, а не лошадиных сил. Касаемо этого разногласия мы обращаемся в Конституционный суд с выражением: Является ли законным данный налог?</li>\n<li>Незаконный. В 2009 году мы достигли того, чтобы транспортный налог был заменен увеличением акцизов на топливо, но власть как обычно обманула своих граждан. Было принято решение увеличить акцизы на топливо на 3 рубля, что увеличило бы поступления в бюджет в 7 раз. Но, акцизы подняты уже дважды, а транспортный налог не только не аннулируется, но и поднимается до максимума.</li>\n<li>Несправедливый. По факту получается, что налоги оплачивают, не исходя из нанесенного ущерба и применения дорог общего пользования, а исходя из того, что владельцы грузовых автомобилей оплачивают меньший налог чем мотоциклисты, а владельцы дорогих авто меньший чем владельцы более мощных, но не дорогих автомобилей.</li>\n</ul>\n</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>Ну что ж, будем, надеется на лучшее, что соберутся все необходимые подписи, и транспортный налог наконец-то отменят. Но транспортный налог не может остановить потребителей как от <strong>покупки поддержанных автомобилей</strong>, так и от<strong> продажи авто с пробегом</strong>. Поэтому <strong>автосалон Адмирал-Моторс</strong> предлагает <strong>покупать поддержанные автомобили Санкт-Петербурга</strong> именно у них.</p>\n<p style="text-align: justify; "> </p>\n<p> </p>', 1, 13, '2012-10-29 07:07:11', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-29 07:07:11', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 720, 'покупка поддержанных автомобилей, продажи авто с пробегом, автосалон адмирал-моторс, покупать поддержанные автомобиля санкт-петербурга', 'Осталось набрать 42,4 тыс. подписи для отмены транспортного налога', 1, 467, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(238, 305, 'Компания BMW отзывает около 45 тыс. автомобилей 7 серии', 'kompaniya-bmw-otzyvaet-okolo-45-tys-avtomobilej-7-serii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>BMW </strong>отзывает порядка 45 тыс. автомобилей 2005-2008 годов выпуска, это связано с неисправностью в работе коробки передач.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отзыву подлежат автомобили <strong>BMW 7 серии</strong> в количестве 45 тыс. 500 единиц. Конкретно, будут отозваны <strong>BMW 745LI</strong> и <strong>BMW 745I</strong> 2005 г. выпуска, <strong>BMW 750LI</strong> и <strong>BMW 750I</strong> 2006-2008 г. выпуска, <strong>BMW 760LI</strong> 2005-2008 г. выпуска, а также <strong>BMW 760I </strong>2005-2006 г. выпуска, - сообщает ИА «Прайм».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Все эти автомобили, оборудованы системой управления коробкой передач и электронным ключом с помощью инновационной технологии Shift-by-Wire, автоматически переводящей трансмиссию в режим парковки путем нажатии кнопки включения-выключения для глушения двигателя.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Но в результате дефекта при вторичном нажатии кнопки включения-выключения двигателя в момент короткого промежутка времени система Shift-by-Wire может перевести коробку передач в нейтральный режим, а не в режим парковки что, в свою очередь, не предотвратит откат автомобиля назад.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В ином случае, при использовании обычного ключа зажигания, при выборе неправильной передачи вслед за глушением автомобиля система не разрешила бы водителю вынуть ключ из замка зажигания. Одновременно с этим при применении электронного ключа водителю автомобиля нет необходимости производить подобных действий, из-за этого понять, в какой конкретно режим была переведена коробка передач вслед за глушением двигателя, также невозможно.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отзыв данных автомобилей начнется в ноябре 2012 года, хотя соответствующее программное обеспечение для ликвидации дефекта будет доступно в марте следующего года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Да, печальное событие, что же делать, пока ваш автомобиль будет находиться в ремонте? Решение простое – <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> в <strong>автосалоне Адмирал-Моторс</strong> по смешной цене.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-30 08:09:37', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-30 08:09:37', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 719, 'BMW, BMW 7 серии, BMW 745LI, BMW 745I, BMW 750LI, BMW 750I, BMW 760LI, BMW 760I, купить поддержанный автомобиль в санкт-петербурге, автосалоне адмирал-моторс', 'BMW отзывает порядка 45 тыс. автомобилей 2005-2008, а пока ваш автомобиль будет в ремонте предлагаем купить поддержанный автомобиль в Санкт-Петербурге', 1, 454, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(239, 306, 'Chevrolet запустил серийную версию седана Sonic Dusk', 'chevrolet-zapustil-serijnuyu-versiyu-sedana-sonic-dusk', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Серийная версия седана Sonic Dusk запущена маркой <strong>Chevrolet</strong>, именно она привнесет премиальные элементы в класс компактных автомобилей.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации российского представительства автоконцерна, версия Sonic Dusk, представленная как флагман компактных автомобилей <strong>Chevrolet</strong>, будет показана на выставке SEMA, открывающейся 30 октября в Лас-Вегасе. На ней будет представлена серийная версия, которая оснащена пакетом Ground Effects, в котором есть крупные легкосплавные 18-дюймовые колесные диски с никелированным покрытием перламутрового оттенка (Pearl Nickel) и задний спойлер.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новинка оборудована информационно-развлекательной системой MyLink, с помощью которой можно регистрировать смартфон, а при использовании USB-разъема или интерфейса Bluetooth воспроизводить звуковые файлы через аудиосистему автомобиля,  хранящиеся в смартфоне.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобиль Sonic Dusk получил двигатель с турбонаддувом Ecotec 1.4L, мощностью 138 л. с. (103 кВт), крутящим моментом 200 Нм. от 1850 до 4900 об/мин. Обширный диапазон оборотов для максимального крутящего момента – это отличительная особенность двигателей с турбонаддувом, которая способствует улучшению ходовых качеств автомобиля.  6-ступенчатая коробка передач работает в паре с двигателем. Также для серийных моделей доступны как 6-ступенчатые АКПП так и МКПП.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобиль Sonic 2013 модельного года в списке <strong>Chevrolet </strong>займет место между новым Cruze и Spark. Это единственный компактный автомобиль, который производится в США; в стандартной комплектации которого идет десять подушек безопасности - это лучший показатель в классе, который также является лидером по вместимости салона.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Спортивный Sonic RS будет укомплектован эксклюзивной информационно-развлекательной системой MyLink  с 7-дюймовым сенсорным цветным дисплеем, аудиосистемой с помощью которой можно подключить смартфон для просмотра видео, прослушивания музыки, фото и совершения вызовов, используя гарнитуру hands-free.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Хотелось бы протестировать данный автомобиль и почувствовать на себе все его преимущества.  В <strong>автосалоне Адмирал-Моторс</strong> можно легко протестировать понравившуюся вам модель <strong>поддержанного автомобиля</strong>. А после <strong>купить авто б / в кредит</strong> под смешные проценты.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-30 08:14:01', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-30 08:14:01', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 718, 'Chevrolet, автосалоне адмирал-моторс, поддержанный автомобиль, купить авто б / в кредит', 'Седан Sonic Dusk с отличительной особенностью двигателей с турбонаддувом, запущен Chevrolet', 1, 787, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(240, 307, 'Теперь Mazda будет выпускать модель исключительно для России', 'teper-mazda-budet-vypuskat-model-isklyuchitelno-dlya-rossii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Корпорации <strong>Mazda</strong> планирует - выпускать именно для России новую модель автомобиля.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«На данный момент есть планы создавать для выпуска России специальный продукт —на заводе во Владивостоке выпускать модель или модификацию ориентированную на Россию», - заявил Такаси Яманучи в интервью «Эксперту», председатель совета директоров и президент <strong>Mazda </strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Детали, о том какая будет модель, сегмент рынка и где она будет продвигаться, Такаси Яманучи не раскрыл. Он сообщил только, что «по нашему мнению, наиболее перспективные на автомобильном рынке России сегменты — седаны С-класса и кроссоверы».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>К тому же он подметил, что во Владивостоке концерн начал производить кроссовер <strong>СХ-5</strong>, а в 2013 году приступит к производству новой <strong>Mazda6</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Европейский и российский рынки в целом в настоящее время очень похожи, но мы считаем, что в ближайшем будущем в России будет продаваться больше полноприводных автомобилей и дизелей с АКПП и седанов. К примеру, касаемо новой Mazda6, а в России наиболее популярны эти машины в кузове седан, в Европе - универсал», - резюмировал Такаси Яманучи.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ну что, придет время, посмотрим, что это будут за автомобили. А пока не тратьте время и посетите наш <strong>автосалон Адмирал-Моторс</strong> где представлены различные марки <strong>поддержанных автомобилей</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-31 03:25:45', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-31 03:25:45', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 717, 'Mazda, Mazda6, СХ-5, автосалон адмирал-моторс, поддержанных автомобилей', 'Mazda будет выпускать именно для России новую модель автомобиля - вот это новость', 1, 561, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(241, 308, 'Maruti Suzuki представила новый Alto 800', 'maruti-suzuki-predstavila-novyj-alto-800', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Индийское подразделение Maruti <strong>Suzuki</strong> India  компании <strong>Suzuki </strong>Motor Corporation показала свою новую модель Alto 800.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Alto 800 - компактный автомобиль оснащенный двигателем объемом 0,8 л. По информации в Suzuki Motor, российском представительстве автоконцерна, модель значительно отличается от предыдущей версии Alto, 2000-года выпуска.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый Alto 800 стал более экономичным и потребляет меньше топлива на 15%  и на 13% для автомобилей, функционирующих на сжатом природном газе. Новую модель выделяет просторный салон с двумя версиями отделки и более спортивный дизайн.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Показатели крутящего момента двигателя Alto 800 пересмотрены для того, чтобы обеспечить больше комфорта при езде по городу, а подвеска и трансмиссия были адаптированы под особенности индийского рынка.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В компании подметили, что <strong>Suzuki Alto 800</strong> отлично подойдет молодым людям, как первый автомобиль. Maruti Suzuki представляет Alto 800 как свою основную модель и намеревается наращивать ее продажи.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Alto 800 собирается на заводе Gurgaon Maruti <strong>Suzuki</strong>. В планах компании -  экспорт этой модели и в другие страны.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отличная новость, хочется в реальности ощутить, что нам приготовила Maruti <strong>Suzuki </strong>India  в своем новом автомобиле. <strong>Автосалон Адмирал-Моторс</strong> предлагает ознакомиться с моделями <strong>поддержанных автомобилей</strong>, протестировать их, а также узнать все об услугах автосалона на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-10-31 03:31:18', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-10-31 03:31:18', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 716, 'Suzuki, Suzuki Alto 800, автосалон адмирал-моторс, поддержанных автомобилей, сайт по продаже автомобилей', 'Новая модель Alto 800, с момента выхода последней модели прошло около 12 лет', 1, 827, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(242, 309, 'Началась реализация «люксовых» LADA Granta', 'nachalas-realizatsiya-lyuksovykh-lada-granta', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>31 октября 2012 года начались продажи автомобилей «люксовой»<strong> LADA Granta</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Розничная рекомендованная цена на новую <strong>LADA Granta</strong> "люкс" равна 406 тыс. 100 руб. В люксовую комплектацию входят "автомат", 1,6-литровый 16-клапанный двигатель с мощностью 98 л.с., кондиционер, электроусилитель руля, ABS+BAS, мультимедиасистема (FM, Bluetooth, SD-карта, 7-дюймовый дисплей с TouchScreen, Handsfree), парктроник, подушки безопасности переднего пассажира и водителя, индикация не пристегнутого ремня безопасности водителя, подголовники задних сидений, сигнализация с дистанционным управлением противоугонная, электростеклоподъемники задних и передних дверей, бортовой компьютер, электрообогрев и электропривод наружных зеркал, противотуманные фары, обогрев передних сидений, теплопоглощающие стекла, литые 14-дюймовые диски колес, под цвет кузова корпуса боковых зеркал и наружных ручек дверей, тканевые вставки в обивках дверей, контейнер для очков,  серебристые элементы панели приборов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В планах у АвтоВАЗа и другие версии <strong>LADA Granta</strong> в "люксовом" варианте. Как сообщили в пресс-центре автоконцерна, измененные версии будут отличаться набором опций.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По наличию различного рода устройств и по цене, можно сказать хорошее соотношение цены и качества. Аналогично можно сказать и о<strong> поддержанных автомобилях</strong> <strong>автосалона Адмирал-Моторс</strong> с полным ассортиментом которого вы можете ознакомиться на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-01 05:17:29', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-01 05:17:29', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 715, 'LADA Granta, поддержанный автомобиль, автосалона адмирал-моторс, сайте по продаже автомобилей', 'Люксовая LADA Granta теперь доступна каждому', 1, 552, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(243, 310, 'Цены на нефтепродукты поднялись на 3,5% в октябре', 'tseny-na-nefteprodukty-podnyalis-na-35-v-oktyabre', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>По информации Алтайкрайстата, цена на нефтепродукты за октябрь месяц 2012 года выросла на 3,5%.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По официальной статистике, дизельное топливо в октябре стало дороже на 2,8%, бензин АИ-92 – вырос на 3,7%., АИ-95 увеличился на 3,4%.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В итоге, индекс  потребительских цен в октябре в Алтайском крае стал равен 100,7%, за период с начала 2012 года – 106,3%. Темпы прироста в текущем году потребительских цен в 1,5 раза выше, чем в 2011 году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Конечно, любое повышение цен пусть на продукты питания или на бензин вызывает у нас недовольство, ну а куда деваться? Пересаживаться на общественный транспорт и попадать в давку в утренний и вечерний часы пик? Конечно, нет. Просто <strong>купите поддержанный автомобиль в Санкт-Петербурге</strong> импортного производства который расходует мало топлива, и наслаждайтесь ездой.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-01 05:19:58', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-01 05:19:58', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 714, 'купите поддержанный автомобиль в санкт-петербурге, купите поддержанный автомобиль в спб, купите авто с пробегом в санкт-петербурге', 'Цена на нефтепродукты повышается чуть ли не каждый день', 1, 437, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(244, 311, 'Начались продажи нового Kia Sorento в России', 'nachalis-prodazhi-novogo-kia-sorento-v-rossii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>С 1 ноября текущего года в России во всех дилерских центрах <strong>Kia</strong> начался прием заказов на новый внедорожник<strong> Kia Sorento</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Внедорожник предлагается в 4-х вариантах с 2,2-литровым дизелем 197 л.с. и в 8-ми вариантах с бензиновым двигателем 175 л.с., объемом 2,4 л. Оба двигателя могут комплектоваться 6-тиступенчатой МКПП или АКПП.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Бензиновые версии могут быть выбраны как с полным, так и с передним приводом. Стоимость на новый <strong>Sorento</strong> стартует с 1 млн. 69 тыс. 900 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве марки, <strong>Sorento</strong> популярен во всем мире. С тех пор как он вышел на рынок в 2009 году его продажи превзошли 620 тыс. автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый <strong>Sorento </strong>разработан на принципиально новой платформе и лучше предшественника по массе параметров. Свои внедорожные качества и надежность Новый <strong>Sorento </strong>доказал, пройдя порядка 13 500 км. из Владивостока до Москвы без поломок.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В <strong>автосалоне Адмирал-Моторс</strong> и на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> есть множество видов поддержанных внедорожников начиная с <strong>Land Rover Freelander Hard Top</strong> и заканчивая <strong>Nissan Qashqai</strong>. Мы <strong>продаем авто с пробегом в Санкт-Петербурге</strong> по отличным б/у ценам.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-02 07:07:39', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-02 07:07:39', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 713, 'Kia, Kia Sorento, Sorento, автосалоне адмирал-моторс, сайт по продаже автомобилей, Land Rover Freelander Hard Top, Nissan Qashqai, продажа авто с пробегом в санкт петербурге', 'Новый внедорожник Kia Sorento доступен для заказа в России в дилерских центрах', 1, 699, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(245, 312, 'Для ликвидации последствий урагана «Сэнди» Chevrolet отдала Красному Кресту свои автомобили', 'dlya-likvidatsii-posledstvij-uragana-sehndi-chevrolet-otdala-krasnomu-krestu-svoi-avtomobili', '<p> </p>\n<p style="text-align: justify;"> </p>\n<p><span style="white-space: pre;"> </span>Компания <strong>Chevrolet</strong> передала в дар Американскому Красному Кресту около 50 грузовых микроавтобусов и полноразмерных пикапов для ликвидации последствий урагана «Сэнди», обрушевшегося на восточное побережье США.</p>', '<p> </p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве марки, дочернее предприятие GM - компания OnStar, раздала для каждого из переданных в помощь машин 300 бесплатных минут мобильной связи с применением системы OnStar Hands-Free Calling, а также 3 месяца бесплатного использования услуги OnStar Directions & Connections с навигацией по шагам.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Чуть ранее подразделение GM Foundation выделила 250.000$ на программу Красного Креста по быстрому реагированию на катастрофы в любой точке страны. Эти пожертвования предназначены для предоставления временного жилья, психологической поддержки и питания жертвам катастроф. Денежные средства также идут на обеспечение деятельности ликвидаторов, закупки препаратов и донорской крови, обработки запросов членов семей, которые находятся за пределами зоны бедствия, а также для транспортировки пострадавших в более безопасные районы.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Ураган «Сэнди» нанес значительный урон, восточному побережью США, затронув западный регион, потому возвращение жителей к обычной жизни займет достаточно много времени. Существенная финансовая и транспортная помощь компании GM и подразделением GM Foundation, окажет  нам помощь в обеспечении пострадавших временным жильем, заботой и питанием», - подметил Нил Литвак, директор по развитию Американского Красного Креста.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Автосалон Адмирал-Моторс</strong> призывает всех, кто может пожертвовать любые средства для ликвидации последствий урагана и помощи пострадавшим. Мы будем вести цикл новостей по теме урагана «Сэнди» на нашем<strong> <a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<p> </p>', 1, 13, '2012-11-02 07:10:52', 792, '', '2012-11-02 07:14:34', 792, 0, '0000-00-00 00:00:00', '2012-11-02 07:10:52', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 712, 'Chevrolet, автосалон адмирал-моторс, сайт по продаже автомобилей, продажа авто с пробегом в спб', 'Американскому Красному Кресту для ликвидации последствий урагана «Сэнди» и помощи пострадавшим Chevrolet передала в дар около 50 грузовых микроавтобусов и полноразмерных пикапов', 1, 596, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(246, 313, 'Будущим владельцам показали сбор автомобиля Kia Rio', 'budushhim-vladeltsam-pokazali-sbor-avtomobilya-kia-rio', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Будущие потребители <strong>Kia Rio </strong>побывали на заводе по производству <strong>Rio </strong>в Санкт-Петербурге.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве компании Kia, впервые завод распахнул двери для своих клиентов. Гостями завода как не банально стали будущие владельцы<strong> Kia Rio</strong>, ожидающие поступления автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Интерес был столь высок, потому собрать 120 человек на экскурсию удалось за несколько часов. Экскурсовод поведал посетителям, что завод является единственным из  аналогичных зарубежных предприятий на территории РФ, который обладает оборудованием для реализации полного цикла сборки автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В цехе штамповки — гордости автозавода — публика стали свидетелями того, как лист металла как по волшебству превращается в панель автомобиля. Особенное внимание было уделено социальной ответственности производителя в вопросах окружающей среды и экологии. В рамках экскурсии гостям также показали несколько этапов контроля качества выпускаемой продукции.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Не менее интересно побывать в нашем <strong>автосалоне Адмирал-Моторс в Санкт-Петербурге</strong>, где собраны самые разнообразные модели <strong>поддержанных автомобилей</strong> со всего мира. Здесь и <strong>Chrysler 300M</strong>, <strong>Dodge Calider</strong>, <strong>Hyundai Starex</strong> и др.<strong> авто с пробегом</strong>. У нас вы можете <strong>купить авто б у в кредит</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-02 07:38:43', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-02 07:38:43', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 711, 'Kia Rio, Kia, автосалоне адмирал-моторс в санкт-петербурге, поддержанный автомобиль, Chrysler 300M, Dodge Calider, Hyundai Starex, авто с пробегом, купить авто б у в кредит', 'Будущие потребители Kia Rio побывали на заводе по производству Rio в Санкт-Петербурге', 1, 614, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(247, 314, 'Уже 68% водителей европейской части России поменяли резину на зимнюю', 'uzhe-68-voditelej-evropejskoj-chasti-rossii-pomenyali-rezinu-na-zimnyuyu', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В европейской части России, 68% жителей (исключая Юг), уже успели поменять резину на своих автомобили к зимнему сезону.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Такая информация приводятся в исследовании, которое было проведено Аналитическим центром компании «АльфаСтрахование». Здесь также отмечается, что около 25% владельцев авто планируют сделать это в ближайшее время, а 7% – вообще не планируют использовать зимние шины.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Уже 68% участников опроса заранее подготовились к изменению условий на дорогах и «переобули» свои автомобили. Еще не сменили, но собираются 25% опрошенных автомобилистов. А 7% респондентов, совсем не планируют использовать зимнюю резину по разным причинам.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Среди участников опроса, которые еще только собираются, рассказали о следующих причинах:</p>\n<p style="text-align: justify;">\n<ul>\n<li>11% автовладельцев, это 3,5%, используют всесезонные шины;</li>\n<li>72%, это 23%, пока не успели сделать этого: «мешает загруженность на работе», «не хватает времени», «не удается попасть в сервисный центр»;</li>\n<li>3% , это 1%, автомобилистов предпочитают не ездить на своей машине в зимний период.</li>\n<li>8% , это 2,5%, не считают использование зимней резины важной необходимостью;</li>\n<li>6% , это 2%, затруднились назвать конкретные причины.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Исследование проводилось в Центральном, Приволжском и Северо-Западном федеральном округах в рамках опроса потенциальных клиентов и действующих компании, имеющих автомобили. Из 800 опрошенных респондентов, только 562 человека согласились принять участие в данном исследовании, на основе их ответов была составлена итоговая статистика.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Множество  автовладельцев уже подготовилось к зиме, а вы уже сменили резину? В<strong> автосалоне Адмирал-Моторс</strong> в комплекте некоторых <strong>авто с пробегом</strong> есть и зимняя резина. Поэтому, <strong>купив поддержанный автомобиль в Санкт-Петербурге</strong> вы даже сэкономите.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-02 07:41:24', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-02 07:41:24', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 710, 'автосалон адмирал-моторс, авто с пробегом, купить поддержанный автомобиль в санкт петербурге', '68% автовладельцев Центрального, Приволжского и Северо-Западного федеральных округов, уже переодили резину на зимнюю', 1, 538, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(248, 315, 'За 495 тыс. долларов продали Aston Martin Пола Маккартни', 'za-495-tys-dollarov-prodali-aston-martin-pola-makkartn', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Легендарный Aston Martin DB5, принадлежавший бывшему участнику британской группы "Битлз" Полу Маккартни, продали за 307,5 фунтов стерлингов (495 тыс. долларов) с аукциона.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Популярность данного автомобиля и известность его бывшего владельца действительно стоят друг друга». Астон Мартин DB5 – это одна из самых легендарных машин в мире, когда-то она принадлежала самому Джеймсу Бонду. А Пол Маккартни -  участник самой легендарной группы 60-х годов прошлого века", - сообщил Питер Уоллман, представитель организатора торгов RM Auctions.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации ИТАР-ТАСС, основатель "Битлз" приобрел темно-синий спорткар еще в далеком 1964 году в преддверии мирового турне "ливерпульской четверки". Потом салон этого автомобиля был обтянут черной кожей и укомплектован самой современной магнитолой по тем временам. Маккартни, проехав на ней около 65 тыс. км., продал ее в 1970 году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Интересный факт, аналогичной моделью "Астон Мартин DB5" владел еще один экс-битл в свое время  - ныне покойный Джордж Харрисон. Его автомобиль был также  продан с аукциона, но в 2007 году за 464 тыс. долларов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Да действительно Астон Мартин DB5 легендарный автомобиль. Конечно в <strong>автосалоне Адмирал-Моторс</strong> нет подобных легендарных автомобилей, но есть интересные модели <strong>авто с пробегом</strong>, на которые вы должны хотя бы посмотреть. Это легко сделать на нашем <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-02 08:03:36', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-02 08:03:36', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 709, 'автосалоне адмирал-моторс, авто с пробегом, сайт по продаже автомобилей', 'Aston Martin DB5 Пола Маккартни продали с аукциона за 307,5 фунтов стерлингов', 1, 556, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(249, 316, 'Под игрушечную машинку Hot Wheels стилизовали Chevrolet Camaro', 'pod-igrushechnuyu-mashinku-hot-wheels-stilizovali-chevrolet-camaro', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В начале 2013 года в продаже появится <strong>Chevrolet Camaro</strong> стилизованный под игрушку Hot Wheels® Edition.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Впервые в истории полноразмерный серийный автомобиль Hot Wheels® можно будет купить! Оранжевый гоночный трек и пакет Blister в комплектацию не входят, - подмечают в российском представительстве компании <strong>Chevrolet</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Премьера Camaro Hot Wheels® Edition состоялась 29 октября этого года на выставке SEMA в Лас-Вегасе. Ограниченная партия Camaro Hot Wheels® Edition появится уже в первом квартале следующего года — и как обычно с коллекционными игрушками, по подобию и образу которых был разработан автомобиль,— кто не успел, тот опоздал.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В образе Camaro Hot Wheels® Edition любители Hot Wheels узнают свою любимую «игрушку»: колесные диски с красной окантовкой, кузов автомобиля в насыщенном синем цвете металлик и с графическими акцентами. В итоге получился самый настоящий Hot Wheels.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Camaro Hot Wheels® Edition с кузовом купе будет оснащаться двигателем V8 в паре с МКПП или автоматической трансмиссией. Автомобиль совмещает в себе элементы дизайна спортивного Camaro ZL1 и кузов, цвета Kinetic Blue с уникальной графикой.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Camaro Hot Wheels® Edition с МКПП оснащается двигателем 6,2 л. V8 с мощностью 432 л. с. и крутящим моментом 569 Н•м. Двигатель с автоматической трансмиссией будет развивать мощность 405 л. с. а крутящий момент +556 Н•м. Последний укомплектован системой отключения цилиндров.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Фанаты игрушечного автомобиля Hot Wheels теперь возрадуются. <strong>Автосалону Адмирал-Моторс</strong> тоже есть чем вас удивить – автомобиль <strong>Suzuki Ignis</strong> 2007 года выпуска предлагается по смешной цене в 327 тыс. рублей. И еще несколько  моделей <strong>поддержанных автомобилей</strong> участвуют в акции, какие именно вы можете узнать на нашем <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<p style="text-align: justify;"> </p>\n</p>', 1, 13, '2012-11-02 08:05:33', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-02 08:05:33', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 708, 'Chevrolet Camaro, Chevrolet, автосалон адмирал-моторс, Suzuki Ignis, поддержанный автомобиль, сайте по продаже автомобилей', 'Chevrolet Camaro стилизуют под игрушку Hot Wheels® Edition и начнут продажу в 2013 году', 1, 998, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(250, 317, 'Определены пункты утилизации автохлама', 'opredeleny-punkty-utilizatsii-avtokhlama', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В Минпромторг компании, имеющие гарантии по безопасной утилизации собираемых в России автомобилей, прислали списки пунктов приема утилизационного автохлама.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Наличие пунктов приема — это одно из требований положения об утилизационном сборе на машины, действующего с 1 сентября 2012 г. Местные производители, в отличие от импортеров, уплачиваемых сбор деньгами,  гарантируют безопасную утилизацию производимых в России машин (в ином случае аналогично импортерам платят сбор).</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации "Ведомости", большее количество пунктов в списке «АвтоВАЗа» — 266 единиц. Пункты  приема автохлама созданы на базе дилеров <strong>Lada</strong>, которые участвуют в госпрограмме утилизации 2010-2011 гг. Каждый такой пункт располагает площадкой для временного хранения транспортных средств подлежащих утилизации.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Затраты на работу пунктов будут компенсироваться и «АвтоВАЗом», и компаниями, изъявившими желание пользоваться их услугами. Здесь говорится о предприятиях, выпускающих автомобили <strong>Lada </strong>и иные транспортные средства на их базе, — это «Чеченавто», Ижевский автозавод, «Супер-Авто», «Бронто», «ВИС-Авто» и «Джи Эм — АвтоВАЗ».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Порядка 200 пунктов приема — в списке <strong>Ford </strong>Sollers: они разработаны на базе дилеров и партнеров, которые имеют лицензию на утилизацию (в регионах, где у компании отсутствуют дилеры). Представитель <strong>Renault</strong>, у которого около 120 пунктов сообщил, что пункты приема автохлама созданы исключительно на базе дилеров — <strong>Renault</strong> и партнеров по альянсу в России: «АвтоВАЗа» и <strong>Nissan</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>У «Камаза» таких пунктов 115 штук, они созданы на базе сервисно-технических центров, дилеров и привлеченных организаций-утилизаторов. У группы «ГАЗ» список пунктов составляет 100 единиц, главным образом на базе дилеров. У <strong>Volkswagen </strong>-  90 единиц  приема автохлама, которые созданы на базе партнерских организаций. У <strong>Toyota </strong>порядка 86 пунктов, большая часть которых разработана на мощностях дилеров.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Пункты приема — это только первый этап: сейчас создается проект постановления правительства. В нем будет, прописывается, то, каким образом будет поддерживаться развитие системы утилизации автомобилей в России, и как будет происходить сам процесс гарантированной утилизации.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ну а если ваш любимый старый автомобиль еще функционирует, то вы сможете <strong>продать авто с пробегом в Санкт-Петербурге</strong> в <strong>автосалоне Адмирал-Моторс</strong>. У нас же вы сможете к<strong>упить поддержанный автомобиль</strong> по минимальной цене.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-05 06:51:33', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-05 06:51:33', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 707, 'Lada, Ford, Renault, Nissan, Volkswagen, Toyota, продать авто с пробегом в санкт петербурге, автосалон адмирал-моторс, упить поддержанный автомобиль', 'Утилизация автохлама продолжается!!!', 1, 997, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(251, 318, 'Honda сообщила об особенностях нового Accord', 'honda-soobshhila-ob-osobennostyakh-novogo-accord', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span> Самым богато оснащенным и технически совершенным седаном за всю 37-милетнюю историю жизни модели является девятое поколение легендарного <strong>Honda Accord</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в официальном сообщении компании Honda, в базовую комплектацию нового <strong>Hоnda Accord</strong> входит: датчики дождя и света, система безключевого доступа Smart Entry, стекла с электроприводом, центральный замок, регулируемая по вылету и высоте рулевая колонка, круиз-контроль, двухзонный климат-контроль, кожаная отделка руля, подогрев передних сидений, 8-дюймовый мультимедийный WVGA дисплей, аудиосистему стандарта CD/AM/FM 160 Вт.  с четырьмя динамиками и сенсорным экраном, управление круиз-контролем  и аудио на руле, порт для внешних USB/iPod® устройств, передние противотуманные фары, литые диски, систему Eco Assist™.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобиль, по желанию клиента, можно оснастить спутниковой навигацией с функцией распознавания голосовых команд, системой предотвращения столкновений (CMBS), адаптивным круиз-контролем (ACC), сенсорной панелью управления аудио-системой (ODMD), беспроводным интерфейсом Bluetooth® HandsFreeLink®. Также в добавочное оборудование может входить: сидение водителя с регулировками в 8-ми направлениях и функцией памяти, сидение пассажира с регулировками в 4-х направлениях и электроприводом, кожаная отделка сидений с перфорацией, люк с электроприводом, подогрев задних сидений, зеркало заднего вида с автозатемнением.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отдельно стоит отметить инновационную систему LaneWatch™ контроля за слепыми зонами Honda, которая на сегодня не имеет полноценных аналогов. Впервые в мире данная система установлена на новый <strong>Honda Accord</strong> 2013 модельного года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для безопасной парковки потребители получат камеру заднего вида (помимо базовой комплектации), которая дополняется 8-ю датчиками парковки.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Это и многое другое входит в базовую комплектацию <strong>Honda Accord</strong>. У нас в автосалоне есть <strong>поддержанные автомобили</strong> <strong>Honda Accord</strong>, вполне по доступным ценам. Пусть вам не хватить денег на новый автомобиль вы всегда можете себе позволить <strong>купить авто с пробегом в СПб</strong> (Санкт-Петербурге).</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-05 06:54:49', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-05 06:54:49', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 706, 'Hоnda Accord, поддержанный автомобиль купить, купить авто с пробегом в спб', 'Honda Accord девятого поколения -  самый богато оснащенный и технически совершенный седан', 1, 577, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(252, 319, 'На компактные модели Toyota установит новый бесступенчатый вариатор', 'na-kompaktnye-modeli-toyota-ustanovit-novyj-besstupenchatyj-variator', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Тойота </strong>создала Super CVT-i - новый бесступенчатый вариатор.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации из заявления компании, новый бесступенчатый вариатор разрешит достичь плавного ускорения и высокой топливной экономичности. Данная трансмиссия была впервые установлена на автомобили <strong>Toyota Corolla </strong>для рынка Японии в июне текущего года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как подметили в компании, планируется установка новейшего вариатора и на остальные компактные модели.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Также в <strong>Toyota </strong>сообщили, что создана новая 8-ступенчатая автоматическая коробка передач для переднеприводных автомобилей, обеспечивающая быстрый и плавный разгон и  превосходную топливную экономичность.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>При этом новая АКПП сопоставима по весу и размерам с 6-ступенчатой автоматической трансмиссией. Новая 8-ступенчатая автоматическая коробка передач была впервые установлена в августе этого года на кроссоверы <strong>Lexus RX 350 F Sport</strong> для рынка Северной Америки.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Toyota радует новыми разработками, и мы тоже не отстаем. <strong>Автосалон Адмирал-Моторс</strong> с радостью предлагает купить<strong> авто с пробегом в Санкт-Петербурге</strong> по аукционной стоимости: <strong>Toyota Avensis II</strong> 2007 года выпуска по красивой цене – 505 тыс. рублей.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-06 05:22:55', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-06 05:22:55', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 705, 'Тойота, Toyota Corolla, Toyota, Lexus RX 350 F Sport, автосалон адмирал-моторс, авто с пробегом в санкт петербурге, Toyota Avensis II', 'Тойота создала  новый бесступенчатый вариатор, а в автосалоне Адмирал-Моторс беспрецедентная акция', 1, 871, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(253, 320, 'На Азиатско-Тихоокеанском чемпионате по ралли SKODA одержала победу', 'na-ziatsko-tikhookeanskom-chempionate-po-ralli-skoda-oderzhala-pobedu', '<p><span style="white-space: pre;"> </span>Завершающий этап Азиатско-Тихоокеанского чемпионата по ралли, прошедший в Китае принес компании <strong>SKODA </strong>первое место в зачете производителей.</p>', '<p> </p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>Стефан Прево  и Крис Аткинсон  одержали победу в зачете пилотов. Вслед за завоеванием титулов в Чемпионате Европы и Чемпионате IRC и победы в Чемпионате APRC, <strong>SKODA</strong> показала свое высшее превосходство сразу на двух континентах.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, для Стефана Прево и Криса Аткинсона, австралийско-бельгийского экипажа MRF, участвующих в ралли на автомобиле <strong>SKODA Fabia Super 2000</strong>, победа в этом чемпионате стала первой. Придя вторыми на Ралли в Китай, они заняли первое место в чемпионате.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>Азиатско-Тихоокеанский чемпионат включал шесть этапов (от конечной до начальной точки пути) – Китай, Япония, Малайзия, Австралия, Новая Каледония и Новая Зеландия. Экипаж Аткинсон-Прево выиграл этапы ралли в Австралии и Новой Зеландии и занял вторые места на других этапах (исключая Японию).</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span>MRF - австралийско-индийская команда, ее поддержка осуществляется индийским импортером SKODA. Другой экипаж команды – Гленн Макнил, Австралия и Гаурав Гилл, Индия – победил на Ралли в Новой Каледонии.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span><strong>SKODA </strong>выиграла третий раз подряд титул чемпиона IRC в 2012 году, а заводской экипаж на <strong>SKODA Fabia Super 2000</strong> получил третью победу подряд в зачете пилотов, начиная от Микко Марккула и Юхо Ханнинен в 2010 году, Ола Флоэне и Андреас Миккельсен в прошлом и в 2012 годах.</p>\n<p style="text-align: justify; "><span style="white-space: pre;"> </span><strong>SKODA </strong>как всегда показала свое высшее превосходство. <strong>Автосалон Адмирал-Моторс</strong> постоянно радует своих клиентов новыми сезонными акциями на <strong>поддержанные автомобили в Санкт-Петербурге</strong>. Ознакомиться с текущей акцией вы можете на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<p style="text-align: justify; "> </p>\n<p> </p>', 1, 13, '2012-11-06 05:26:11', 792, '', '2012-11-06 05:30:10', 792, 0, '0000-00-00 00:00:00', '2012-11-06 05:26:11', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 704, 'SKODA, SKODA Fabia Super 2000, автосалон адмирал моторс, поддержанные автомобили в санкт-петербурге, сайт по продаже автомобилей', 'SKODA завоевала первое место в зачете производителей на чемпионате APRC', 1, 580, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(254, 321, 'Opel осваивает рынок Турции', 'opel-osvaivaet-rynok-turtsii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Для компании <strong>Opel </strong>Турция - это один из самых важных среди растущих рынков, - сообщается в заявлении автоконцерна.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В 2012 году компания намерена реализовать более 50 тыс. автомобилей, за счет этого доля рынка бренда, по плановым расчетам, возрастет с 6% до 6,3 %. Эта цифра будет самой высокой с 2005 года. Автомобиль <strong>Opel</strong> в Турции - один из основных импортеров и самых популярных брендов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Такой рост продаж основывается на популярности бренда, широкой сети дилерских центров и большом выборе продуктов. Помимо этого, в скором времени популярность <strong>Opel </strong>у турецких клиентов возрастет еще выше за счет линейке новых, более экологичных  и экономичных двигателей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Турецкая премьера нового <strong>Opel ADAM</strong> пройдет в Стамбуле. Кроме этого элегантного  и компактного городского автомобиля Opel также покажет седан <strong>Astra</strong> и субкомпактный SUV Mokka. В то же время, свои новинки в Стамбуле покажут более 200 автомобильных брендов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По планируемым данным, проходящий раз в два года международный автосалон в Турции, посетят порядка 1 млн. человек. Автосалон в Турции станет третьим по величине в 2012 году после Парижского и Женевского.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Opel </strong>захватывает растущие части рынки по всему миру. Как и<strong> автосалон Адмирал-Моторс</strong>, который является самым популярным в Санкт-Петербурге по<strong> продаже авто с пробегом</strong> и<strong> покупке поддержанных автомобилей</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-07 05:25:15', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-07 05:25:15', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 703, 'Opel, Opel ADAM, Opel Astra, автосалон адмирал-моторс, продаже авто с пробегом в спб, покупке поддержанных автомобилей спб', 'Opel  захватывает растущие части рынки, включая Турцию', 1, 597, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(255, 322, 'Стоимость и комплектация универсала Hyundai i40 - теперь известны', 'stoimost-i-komplektatsiya-universala-hyundai-i40-teper-izvestny', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания «Хендэ Мотор СНГ» рассказала о ценах на новую модель <strong>Hyundai </strong>i40 в комплектации универсал.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, <strong>Hyundai </strong>i40 будет доступен в 6 комплектациях с двумя типами двигателей: 7-литровым дизельным, выдающим 136 л.с. и бензиновым объемом 2,0 литра с мощностью 149,6 л.с. Модель оснащена только 6-ти ступенчатой АКПП.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>С каждым двигателем можно выбрать любую из 3-х комплектаций: Comfort, Premium и Style. Все модели укомплектованы необходимыми средствами безопасности, двухзонным климат-контролем, подрулевыми переключателями КПП, датчиками дождя, сеткой  и шторкой багажника, подогревом и электроприводом наружных зеркал, передними и задними датчиками парковки, блоком управления магнитолой на руле,  а также  светодиодными задними фонарями и маршрутным компьютером.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Комплектация Comfort оснащена оптитронной панелью приборов с монохромным дисплеем, RDS радио с 6 динамиками, аудиосистемой с CD/MP3 магнитолой, подогревом передних сидений, функцией антизапотевания лобового стекла, легкосплавными 16-дюймовыми дисками. Рычаг трансмиссии уже в базовой комплектации отделан кожей. Цена на бензиновую комплектацию – 1 млн. 69 тыс. руб., на дизельную – 1 млн. 119 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Стоимость версии Style равна 1 млн. 308 тыс. рублей - с дизельным, 1 млн. 258 тыс. рублей с бензиновым мотором. За эту цену клиентам предлагаются ксеноновые лампы головного света, электрорегулировка сидений, подогрев руля, подогрев заднего ряда сидений, система «Smart key» -  это доступ без ключа с кнопкой START/STOP, колесные легкосплавные диски диаметром 17 дюймов, круиз-контроль, камера заднего вида, система рейлингов в багажнике, визуальный помощник парковки, навигационная система NAVTEQ и кожаный салон.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Системы адаптивного освещения и автоматической парковки, память настроек сидений водителя, панорамная крыша, электропривод двери багажника, спортивные сидения переднего пассажира и водителя с вентиляцией, аудиосистема Infinity входят в специальную комплектацию Premium. Hyundai i40. Специальная версия будет доступна по цене 1 млн. 378 тыс. руб. с бензиновым и 1 млн. 428 тыс. рублей с дизельным двигателем.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отличные универсалы от <strong>Hyundai </strong>и такая же их стоимость. На <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> <strong>автосалона Адмирал-Моторс</strong> <strong>купить авто с пробегом в Санкт-Петербурге</strong> бренда <strong>Hyundai </strong>можно по цене 615 тыс. рублей (<strong>Hyundai Santa Fe I</strong>, 2007) почти в 2,5 раза дешевле.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-07 05:28:07', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-07 05:28:07', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 702, 'Hyundai, сайт по продаже автомобилей, автосалон адмирал-моторс, купить авто с пробегом в санкт-петербурге, Hyundai Santa Fe I', 'Новая модель Hyundai i40 в комплектации универсал стоит от 1 млн. 69 тыс. руб.', 1, 642, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(256, 323, 'Специальная акция от Mitsubishi на Pajero Sport, ASX и L200', 'spetsialnaya-aktsiya-ot-mitsubishi-na-pajero-sport-asx-i-l200', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Mitsubishi </strong>Motors догоняя других автопроизводителей объявила специальную цену на свои модели.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в «РОЛЬФ Импорт», официальный дистрибьютор в России <strong>Mitsubishi </strong>Motors, в течение ноября 2012 года во всех дилерских центрах будет действовать выгодное предложение на автомобили Pajero Sport, ASX, L200. <strong>Mitsubishi Pajero</strong> Sport будет реализовываться по цене на 100 тыс. руб. ниже, чем обычная цена 1 млн. 299 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Такие же условия действуют и на автомобиль <strong>Mitsubishi </strong>L200. «РОЛЬФ Импорт» умножает скидку до 80 тыс. рублей на модель <strong>Mitsubishi</strong> ASX  на все версии с двигателем 1.8 CVT. Цена комплектации 1.8 Invite CVT в ноябре составит 849 тыс. 990 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Подчеркнем, в салонах российских дилеров <strong>Audi </strong>также проходит акция, в рамках нее внедорожник <strong>Audi Q7</strong> тоже предлагается по специальной цене. А АвтоВАЗ также не отстает и предлагает акцию на <strong>LADA Samara</strong> –скидка в 20 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ну и так к слову, <strong>автосалон Адмирал-Моторс</strong> с радостью предлагает посмотреть и на наши скидки на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> или прямо на месте. Специальные цены на автомобили<strong> Suzuki Ignis</strong>, <strong>Toyota Avensis II</strong>, <strong>Volkswagen Golf III</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-08 05:22:27', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-08 05:22:27', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 701, 'Mitsubishi, Mitsubishi Pajero, Audi, Audi Q7, LADA Samara, автосалон адмирал-моторс, сайт по продаже автомобилей, Suzuki Ignis, Toyota Avensis II, Volkswagen Golf III', 'Специальная цена на автомобили Mitsubishi ниже на 100 тыс. рублей', 1, 2424, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(257, 324, 'Комплектация автомобиля Audi Q7 расширена', 'komplektatsiya-avtomobilya-audi-q7-rasshirena', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В дилерских салонах <strong>Audi </strong>на территории России продолжается акция, в рамках нее клиенты могут купить популярный внедорожник <strong>Audi Q7</strong> по выгодной цене.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в компании <strong>Audi </strong>Russia, предложение совмещает в себе преимущества пакетов отделки и добавочного оборудования Comfort и Basis Plus. В рамках этой акции автомобили укомплектованы:</p>\n<p style="text-align: justify;">\n<ul>\n<li>электрорегулировкой передних сидений, вместе с опорой поясничного отдела;</li>\n<li>обивкой Cricket сидений кожей (дизайн-пакет);</li>\n<li>парковочным ассистентом с наличием датчиков спереди и сзади;</li>\n<li>зеркалом вида сзади и с автоматическим затемнением;</li>\n<li>информационной системой для водителя с цветным дисплеем;</li>\n<li>пневматической адаптивной подвеской;</li>\n<li>рулевой травмобезопасной колонкой с электрорегулировкой по углу наклона и вылету;</li>\n<li>комфортным ключом;</li>\n<li>пакетом добавочного освещения салона;</li>\n<li>электроприводом крышки багажника.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В рамках спецпредложения Basis Plus и Comfort для заказа доступны следующие варианты:</p>\n<p style="text-align: justify;">\n<ul>\n<li>Audi Q7 3.0 TFSI с 333 л.с. стоимостью 3 млн. 44 тыс. руб.;</li>\n<li>Audi Q7 3.0 TFSI с 272 л.с. стоимостью 2 млн. 704 тыс. руб.;</li>\n<li>Audi Q7 4.2 TDI с 340 л.с. стоимостью 3 млн. 617 тыс. рублей;</li>\n<li>Audi Q7 3.0 TDI с 245 л.с. стоимостью 2 млн. 700 тыс. руб.</li>\n</ul>\n</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Автосалон Адмирал-Моторс</strong> хочет напомнить о своих спецпредложениях на <strong>покупку поддержанных автомобилей</strong> на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-08 05:25:50', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-08 05:25:50', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 700, 'Audi, Audi Q7, автосалон адмирал-моторс, покупка поддержанных автомобилей, сайт по продаже автомобилей', 'Акция на автомобиль Audi Q7 порадует любого', 1, 467, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(258, 325, 'Suzuki сменить профиль на продажу мотовездеходов и мотоциклов в США', 'suzuki-smenit-profil-na-prodazhu-motovezdekhodov-i-mototsiklov-v-ssh', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">Американская корпорация </span><strong style="text-align: justify;">Suzuki </strong><span style="text-align: justify;">Motor (ASMC) официально сообщила о намерении прекратить реализацию автомобилей в США.</span></p>', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">По информации сообщения автоконцерна, дочерняя компания ASMC в Соединенных Штатах Америки закроет бизнес по реализации автомобилей и сосредоточится на своем бизнесе по продаже мотовездеходов, мотоциклов и лодочных моторов. Сооотвественно ASMC остановит дистрибуцию своих автомобилей на континентальной части США.</span></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отталкиваясь от своего долгосрочного бизнес-план, компания ASMC подумала, что не в состоянии сохранить рентабельность своего бизнеса по реализации автомобилей. Много факторов сыграла свою роль: рыночная конъюнктура, экономические условия, совместно с обменным курсом, модели автомобилей Suzuki, реализуемые в США (ориентированные на компактные автомобили), планируемые объемы продаж ASMC и жесткое законодательство США в связи с безопасностью и защитой окружающей среды.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Одновременно с этим ASMC увидела возможность сохранения увеличения и рентабельности объемов продаж в своем бизнесе по продаже мотоциклов, лодочных моторов и мотовездеходов. В итоге, ASMC приняла следующее решение: свернуть нерентабельный бизнес по реализации автомобилей и направить все свои операционные ресурсы в область своего бизнеса по продаже мотовездеходов, мотоциклов и лодочных моторов, развернуть данный бизнес и оперативно усовершенствовать его рентабельность во всех секторах, обладающих перспективой высокой прибыли.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Suzuki </strong>прекращает продажу автомобилей только в США, и для вас нет никаких ограничений. Здесь на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> вы всегда можете <strong>купить авто с пробегом в Санкт-Петербурге</strong>. <strong>Автосалоны СПб</strong> (Санкт-Петербург) <strong>авто с пробегом</strong> под названием Адмирал-Моторс рады обслуживать в любое время.</p>\n<p> </p>', 1, 13, '2012-11-09 05:40:42', 792, '', '2012-11-09 05:49:29', 792, 0, '0000-00-00 00:00:00', '2012-11-09 05:40:42', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 3, 699, 'Suzuki, сайт по продаже автомобилей, купить авто с пробегом в санкт-петербурге, автосалоны спб авто с пробегом', 'Suzuki  прекращает продажу автомобилей в США, а автосалон Адмирал-Моторс рассказывает о новых выгодных услугах по покупке авто б у в кредит', 1, 581, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(259, 326, 'Volvo снизила время до 1,5 часов для зарядки аккумуляторов', 'volvo-snizila-vremya-do-1-5-chasov-dlya-zaryadki-akkumulyatorov', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Сотрудники <strong>Volvo </strong>Car Corporation создали новое устройство, предназначенное для быстрой зарядки электромобилей.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации автоконцерна, первое в мире 3-хфазное зарядное устройство довольно компактно, и это позволяет его установить в <strong>Volvo </strong>C30 Electric для последующих тестов. Устройство обладает мощностью 22 киловатта и может подзарядить аккумуляторы всего за полтора часа, что в 6 раз быстрее известных систем.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Если у вас нет времени оставить автомобиль на 1.5 часа зарядки, то всего 30 минут зарядки хватит, чтобы проехать 80 км. Водитель может подзаряжать аккумуляторы до нескольких раз в день. Что означит, что совокупный запас хода на сутки заметно увеличивается, при этом расходы эксплуатацию будут существенно ниже по сравнению с обыкновенным автомобилем», – подметил Леннарт Стегланд вице-президент <strong>Volvo </strong>Car Corporation  по электрическим силовым установкам.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>С помощью устройства быстрой подзарядки аккумуляторов разработанных компанией <strong>Volvo </strong>вы сможете больше проводить время с семьей и отдаваться любимой работе. <strong>Продажа авто</strong> с таким устройством скоро будет запущено на поток. <strong>Продать авто в СПб</strong> (Санкт-Петербурге), который вы хотите заменить на новый, можно обратившись на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-09 05:45:44', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-09 05:45:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 698, 'Volvo, продажа авто, продать авто в спб, сайт по продаже автомобилей', '3-хфазное зарядное устройство от Volvo всего  1.5 часа для полной зарядки', 1, 613, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(260, 327, 'Неделя качества в АвтоВАЗе', 'nedelya-kachestva-v-vtovze', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">Начиная с 8 и по 15 ноября включительно АвтоВАЗ проводит неделю качества. Уже третий год подряд она стартует во Всемирный День качества.</span></p>', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">По информации из-за заявления автоконцерна,  АвтоВАЗ вместе с Альянсом Renault-Nissan сделал существенный шаг в осуществлении программы увеличения качества автомобилей LADA и в усовершенствовании и стандартизации всех процессов. Введение комплекса инструментов и мероприятий, включая детальный анализ автомобилей </span><strong style="text-align: justify;">LADA </strong><span style="text-align: justify;">в период перед продажей и в течение первого полугодия после реализации уже привело к понижению дефектности на автомобилях по сравнению с 2011 годом. Независимые исследования, по методике "Эстель", показали следующий результат: удовлетворенность и качество потребителей автомобилей </span><strong style="text-align: justify;">LADA </strong><span style="text-align: justify;">равны показателям моделей Альянса в таком же сегменте.</span></p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Неделя качества открылась совместной конференцией Альянса <strong>Renault</strong>-<strong>Nissan </strong>и АвтоВАЗа. Директор по качеству, вице-президент АвтоВАЗа - Лоран Фофана подметил: "АвтоВАЗ проводит неделю качества уже третий год подряд. И сейчас мы можем говорить: планируемые в 2011 году результаты достигнуты. Это, безусловно, не конечная наша цель – мы ставим новые более амбициозные планы. Следующий год крайне важен для нас - АвтоВАЗ становится мультибрендовым автопроизводителем: мы будем производить автомобили и для наших партнеров Nissan и Renault".</p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span> Во время недели качества АвтоВАЗ проведет и другие мероприятия:</p>\n<p style="text-align: justify; "> </p>\n<ul>\n<li>12 ноября – состоится встреча с поставщиками Альянса и АвтоВАЗа. </li>\n<li>9 ноября - сотрудники Альянса и АвтоВАЗа проведут встречу со студентами учебных заведений Тольятти. Они поведают молодым специалистам о перспективах в карьере в компаниях-партнерах.</li>\n<li>15 ноября - участники "круглого стола" посетят испытательный полигон АВТОВАЗа в Сосновке. Побывают на масштабном тест-драйве <strong>LADA Kalina</strong> Sport c обновленной тросовой МКПП и <strong>LADA Granta </strong>с "автоматом".</li>\n<li>14 ноября – состоится "круглый стол" с членами интернет-сообществ фанатов автомобилей <strong>LADA</strong> и с таксомоторными компаниями. </li>\n</ul>\n<p> </p>\n<p style="text-align: justify; "><span style="white-space:pre"> </span>Неделя качества должна проводиться во всех заводах производящих автомобили, так как это всегда помогает в усовершенствовании качества выпускающих автомобилей. В нашем автосалоне мы постоянно проводим дни качества, что помогает нам отследить качество <strong>покупаемых авто с пробегом</strong>, а также <strong>продаваемые поддержанные автомобили</strong>.</p>\n<p> </p>', 1, 13, '2012-11-10 08:32:51', 792, '', '2012-11-10 08:36:12', 792, 0, '0000-00-00 00:00:00', '2012-11-10 08:32:51', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 697, 'LADA, Renault, Nissan, LADA Granta, LADA Kalina, покупка авто с пробегом, продажа поддержанных автомобилей', 'Всемирный День качества поддерживает АвтоВАЗ проводя неделю качества', 1, 783, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(261, 328, 'Эксклюзивное партнерство Opel с E.O.F.T заключено', 'ehksklyuzivnoe-partnerstvo-opel-s-eoft-zaklyucheno', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Компания <strong>Opel </strong>будет эксклюзивным автомобильным партнером масштабнейшего Европейского фестиваля экстремальных фильмов (E.O.F.T.) 2012–2013 годов.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации сообщения компании, <strong>Opel </strong>дает шанс всем желающим освоить квалификацию для того чтобы принять участие в экстремальных тест-драйвах. Достаточно зарегистрироваться на сайте Test Drive Of Your Life. Одержавшие победу смогут участвовать в тест-драйве субкомпактного кроссовера Mokka в живописной обстановке или обновленного городского эксклюзивного автомобиля Opel ADAM.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Фестиваль будет проходить в девяти странах: Австрии, Германии, Швейцарии, Нидерландах, Италии, Франции, Бельгии, Люксембурге и Великобритании. Его посетят свыше 100 тыс. человек по прогнозам организаторов. На фестивале предусмотрен показ серии короткометражных фильмов, таких как фристайл на ледниках Аляски или маунтинбайкинг в швейцарских Альпах, вообщем все связанное с экстремальными видами спорта.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Теперь у фестиваля экстремальных фильмов есть спонсор. А для тех, кто любит экстремальные виды спорта, а точнее экстремальное вождение, то стоит поспешить к нам <strong>автосалон Адмирал-Моторс</strong> и <strong>купить авто в Санкт-Петербурге с пробегом</strong> для своего экстрима.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-10 08:36:15', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-10 08:36:15', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 696, 'Opel, автосалон Адмирал-Моторс, купить авто в санкт-петербурге с пробегом', 'Opel спонсирует Европейский фестиваль экстремальных фильмов', 1, 485, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(262, 329, 'Продана 100 тыс. LADA Granta в России', 'prodana-100-tys-lada-granta-v-rossii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В новосибирском автосалоне «Автоцентр» 8 ноября был продан 100-тысячная<strong> LADA Granta</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>100-тысячным покупателем автомобиля стала Юлия Зуева, 35-летняя учительница музыки из города Новосибирск. Комплектация автомобиля "норма" с "автоматом".  Обладатель "юбилейной" <strong>LADA Granta</strong> в подарок получила сертификат на бесплатное техническое обслуживание автомобиля по регламенту.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации пресс-центра АвтоВАЗа, п родажа 100-тысячной <strong>LADA </strong>– стала знаковым событием для Сибирского федерального округа в сфере автомобильного бизнеса, сегодня реализация здесь <strong>LADA</strong> показывает положительную динамику. С начала текущего года в СФО дилерские центры <strong>LADA </strong>продали более 37 тыс. автомобилей – это на 6% больше, чем в 2011 году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Также АвтоВАЗ отправил на экспорт порядка 8 тыс. автомобилей<strong> LADA Granta</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>LADA Granta</strong> популярна в России. А в нашем <strong>автосалоне Санкт-Петербурга</strong> тоже популярны отечественные<strong> поддержанные автомобили</strong>. <strong>Купить авто в СПб</strong> (Санкт-Петербурге) за наличные и в кредит.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-11 05:55:41', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-11 05:55:41', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 695, 'LADA Granta, LADA, автосалон санкт-петербурга, поддержанный автомобил в спб, купить авто в спб', '100-тысячная LADA Granta досталась 35-летней учительницй музыки из Новосибирска', 1, 531, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(263, 330, 'Медосмотр водителей и порядок его проведения зарегламентирован Минздравом', 'medosmotr-voditelej-i-poryadok-ego-provedeniya-zareglamentirovan-minzdravom', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Минздрав России разместил на своем сайте проект приказа «Об утверждении порядка проведения предрейсовых (предсменных) и послерейсовых (послесменных) медицинских осмотров».', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В нем ратифицируется порядок проведения предрейсовых (предсменных) медицинских осмотров и ряд медицинских противопоказаний для допуска к смене. Также назначена форма заключения по итогам предрейсового (предсменного) медицинского осмотра и порядок его проведения.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Согласно распоряжению, предрейсовые (предсменные) медосмотры проводятся перед стартом рабочей смены (рейса) для выявления признаков влияния вредных и (или) опасных факторов производства, состояний и заболеваний, которые препятствуют выполнению трудовых обязанностей, в том числе наркотического, алкогольного или иного токсического опьянения и иных остаточных явлений такого опьянения. Они проводятся по средствам работодателя.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Обязанности по организации проведения предрейсовых (предсменных) медицинских осмотров работников и поручаются работодателю. Ответственность за качественное проведение данных медицинских осмотров возлагается на медицинскую организацию.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Водителям нужно будет пройти осмотр врача-терапевта и функционально-лабораторные исследования (артериального давления, измерение температуры тела и частоты пульса и т.д.). Обязательные экспресс-тесты на установление факта применения алкоголя и других психоактивных, включая наркотические вещества.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Если будут выявлены признаки употребления алкоголя и иных психоактивных, включая наркотические вещества, работник направляется в сопровождении представителя работодателя и медицинского работника в специализированную организацию для медицинского освидетельствования на установление факта принятия алкоголя и иных психоактивных веществ, включая  наркотические.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ужесточенное проведение медицинского осмотра водителя не отнимут права <strong>купить авто в Санкт-Петербурге </strong>(СПб) <strong>с пробегом</strong>. <strong>Автосалоны СПб авто с пробегом</strong> всегда предложат вам огромный ассортимент.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-11 05:58:34', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-11 05:58:34', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 694, 'купить авто в санкт петербурге  с пробегом, автосалоны спб авто с пробегом', 'Минздрав предупреждает о нововведении в медосмотре водителей', 1, 522, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(264, 331, 'Ford Ranger получил звание - лучший пикап в мире', 'ford-ranger-poluchil-zvanie-luchshij-pikap-v-mire', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Новый <strong>Ford </strong>Ranger заслужил признание лучшего пикапа в мире и удостоился премии международного пикапа 2013.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации официального заявления компании <strong>Ford</strong>, абсолютно все члены жюри передали свои голоса исключительно новому Ford Ranger и присудили ему самые высокие баллы, тем самым высоко оценив эксплуатационные характеристики автомобиля, грузоподъемность, ряд высококлассных двигателей, безопасность и буксировочные способности.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый <strong>Ford</strong> завоевал награду в звании «Международный пикап 2013» неспроста, а пройдя серьезные тесты на полигоне в Бедфордшире, набрав там 47 баллов, что выше общего количества баллов, автомобилей занявших 2-е и 3-е места Isuzu D-Max и <strong>Volkswagen </strong>Amarok соотвественно. Вручение награды произошло в Дублине на выставке коммерческого транспорта.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый <strong>Ford </strong>Ranger можно преобрести в России с двойной или полуторной кабиной, полноприводным  с двигателями на выбор: 3,2 л. и 200 л.с) и дизельными 2,2 л. и 150 л.с. с механической КПП, либо с автоматической трансмиссией. Также можно сделать заказ на бензиновую версию 2,5 л. и 166 л.с. Стоимость автомобиля начинается от 1 млн. 186 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Приобрести поддержанный <strong>Ford </strong>в кузове хэтчбек или седан можно в нашем <strong>автосалоне Адмирал-Моторс в Санкт-Петербурге</strong> (СПб). <strong>Продажа поддержанных авто</strong>, <strong>покупка авто с пробегом</strong>, <strong>авто б у в кредит</strong>, страхование поддержанного автомобиля в СПб – любая услуга на ваш выбор.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-12 08:25:40', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-12 08:25:40', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 693, 'Ford, Volkswagen, автосалон адмирал-моторс в санкт-петербурге, продажа поддержанных авто, покупка авто с пробегом, авто б у в кредит', 'Лучшим пикапом в мире стал новый Ford Ranger', 1, 857, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(265, 332, 'Компании Тойота ровно 75-лет', 'kompanii-tojota-rovno-75-let', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В 2012 году компании «<strong>Тойота</strong>» ровно 75 лет со дня основания.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Третьего ноября 1937 года начал действовать в Японии завод Коромо – это первое производственное автомобильное предприятие <strong>Тойота</strong>. По информации автоконцерна, более века тому назад Сакичи Тойода создал Toyoda Automatic Loom Works текстильное предприятие. В 1926 г. Сакичи Тойода основал первый ткацкий автоматический станок для глобального производства ткани.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Его сын Киичиро Тойода – стал продолжать дело отца, и в то же время проявлял интерес в автомобильной промышленности. В 1929 г. он посетил США и увидел процесс производства машин своими глазами. И в это время Киичиро Тойода понял, что хочет заниматься созданием автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как только он возвратился в Японию тут же собрал команду инженеров. И в 1930-е годы совместно с Рисабуро Тойода, возглавляющим текстильной компании, создали на базе предприятия департамент по разработке автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В годы перед войной Киичиро Тойода был вынужден поменять первоначальные планы по разработке легковых автомобилей и производить грузовики для военных нужд. И в 1935 г. была завершена разработка самой первой модели - грузовика Model G1.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Затем, было налажено массовое производство этих моделей, и Киичиро Тойода вернулся к работе над легковыми авто. Прототип такого автомобиля Model AA был разработан в 1936 г. Тогда же автомобильный бренд обрел современное название – Toyota. Модель AA стала самым первым автомобилем, выпущенным <strong>Toyota</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>А уже 28 августа 1937 г. была основана Компания <strong>Тойота </strong>Мотор, известная с 1982 года и поныне как «<strong>Тойота </strong>Мотор Корпорэйшн». Первым президентом которой стал Рисабуро Тойода.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Вот такой небольшой экскурс в историю создания компании <strong>Toyota</strong>. А мы напоминаем, что <strong>купить поддержанный автомобиль в СПб</strong> (Санкт-Петербурге) бренда <strong>Toyota </strong>или любого другого вы всегда можете в  <strong>автосалоне Адмирал-Моторс</strong> .</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-12 09:34:44', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-12 09:34:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 692, 'Тойота, купить поддержанный автомобиль в спб, автосалон адмирал-моторс в спб', 'Компании «Тойота» празднует 75 лет', 1, 1878, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(266, 333, 'Эксперимент с платными парковками за неделю в Москве принес свыше 700 тыс. рублей', 'ehksperiment-s-platnymi-parkovkami-za-nedelyu-v-moskve-prines-svyshe-700-tys-rublej', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>За неделю действия эксперимента по внедрению платных парковок за использование автостоянки в центре Москвы скоплено более 700 тыс. руб.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации руководителя казенного государственного учреждения "Администратор парковочного московского пространства" Мариничев Сергей, тяжестей в эти дни было немало: кредитные карты не принимались паркоматами из-за неустойчивой связи, а телефоны, на них власти возлагали наибольшие надежды - не отправляли сообщения. Парковочные карты номинал 500 и 1000 руб. показывали на балансе ноль.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>"Одно дело - эксперимент в лабораторных условиях, другое дело - запуск в "боевом" режиме", - разъяснил Сергей Мариничев "РГ" отвечая на вопрос, по каким причинам нельзя было выяснить все это заранее. Невероятно, что в центре Москвы могут возникнуть сложности со связью. Как и масса абонентов с включенным запретом на использование платных сервисов. Но, в конце концов, дело пошло. Главное, что автомобили в пилотной зоне по-настоящему поехали быстрее, к чему, собственно, и стремилась администрация.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Платежи посредством сотовых телефонов потихоньку отлаживаются. Компания МТС включила платный номер 7757 в "белый список", следовательно, если у абонента включен запрет на использование платных услуг, то за парковку он сможет расплатиться. В ближайшие дни транспортники полагаются на договор с "МегаФоном" и "Билайном".</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Процедура регистрации в "Московском парковочном пространстве" упрощена, в ближайшем будущем выйдет обновленное мобильное приложение. Для постоянства платежей по картам в паркоматах поменяют модемы, и они смогут связываться с любым оператором. Власти твердо уверенны в одном: "Наличные мы принципиально принимать не будем".</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Смс-сообщения и квитанции о несостоявшихся платежах чиновники рекомендуют сохранять. С прошлой недели также начали выписывать штрафы – каждый день "парконы" фиксируют по 200-300 фактов неоплаты парковочного места. С смс или чеком, пока идет тестирование, можно прийти в транспортный департамент, и по итогам рассмотрения штраф могут снять.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для проживающих в Москве придется раскошеливаться ежедневно. А в <strong>Санкт-Петербурге покупка авто с пробегом</strong> в <strong>автосалоне Адмирал-Моторс</strong> или прямо на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> станет по-настоящему отличным приобретением.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-13 08:08:16', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-13 08:08:16', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 691, 'санкт петербург покупка авто с пробегом, автосалоне адмирал-моторс, сайт по продаже автомобилей', 'Платные парковки в Москве уже приносят деньги', 1, 378, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(267, 334, 'Chevrolet дает рекомендации по покупке машины для молодых автолюбителей', 'chevrolet-daet-rekomendatsii-po-pokupke-mashiny-dlya-molodykh-avtolyubitelej', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>При покупке личного первого автомобиля юные водители обращают внимание на стиль,  динамику и информационно-развлекательные системы.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Родители в свою очередь, покупая автомобиль для своего ребенка, первоочередно думают о безопасности, надежности и эксплуатационных расходах транспортного средства. Желание непременно сесть за руль спортивного мощного автомобиля понятно, но молодым водителям, не обладающим достаточным опытом вождения, лучшим решением будет начать с городского небольшого автомобиля, - информация из сообщения компании <strong>Chevrolet</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Пять советов от <strong>Chevrolet </strong>при покупке автомобиля для молодых автолюбителей:</p>\n<p style="text-align: justify;"><ol>\n<li>Безопасность. Выбранный автомобиль должен быть укомплектован: подушками безопасности, натяжителями ремней безопасности и антиблокировочной системой. Современные модели оснащаются боковыми шторками безопасности и добавочными подушками безопасности для защиты таза и грудной клетки, а также системой электронной стабилизации курсовой устойчивости. Делайте свой выбор в пользу автомобилей, получивших 4-5 звезд от независимой организации Euro NCAP, занимающейся оценкой безопасности автомобилей.</li>\n<li>Оборудование. Список стандартного оборудования, должен отвечает потребностям вашего ребенка. Обратите внимание на такие аспекты как: объем багажного отделения, спутниковая навигация, удобное сиденье и т.д.</li>\n<li>Уровень расхода топлива. Придерживайтесь понятия - «Больше» не всегда значит «лучше». Соответственно чем больше автомобиль по габаритам, тем больше он расходует топлива. </li>\n<li>Предпродажная проверка. Оцените техническое состояние автомобиля перед покупкой, воспользовавшись услугами квалифицированного технического специалиста и совершите предпродажную проверку, чтобы узнать об исправности тормозной системы и других компонентов автомобиля. </li>\n<li>Затраты. Перед приобретением автомобиля разъясните молодому водителю обо всех возможных финансовых аспектах — от страхования до ежемесячных расходов. </li>\n</ol></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Аналогичны советы при <strong>покупке поддержанного автомобиля в Санкт-Петербурге</strong>. Неважно покупаете ли вы за наличные или <strong>автомобиль б / в кредит</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-13 08:14:50', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-13 08:14:50', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 690, 'Chevrolet, покупке поддержанного автомобиля в санкт-петербурге, автомобиль б / в кредит', 'Рекомендации от Chevrolet по покупке авто для молодых автолюбителей', 1, 756, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(268, 335, 'Популярнейшая модель автомобиля в России - Lada Kalina', 'populyarnejshaya-model-avtomobilya-v-rossii-lada-kalina', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В РФ по продажам за первые десять месяцев 2012 года среди новых легких коммерческих автомобилей и легковых лидирует<strong> Lada Kalina</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации Комитета автопроизводителей Ассоциации европейского бизнеса (АЕБ), за первые десять месяцев текущего года «АвтоВАЗ» реализовал 107 тыс. 69 авто этой марки, если сравнивать с аналогичным показателем 2011 года, то он был ниже на 12%. При этом за октябрь месяц продано 10 тыс. 556 автомобилей модели <strong>Lada Kalina</strong>. Однако это на 9% меньше, чем показатель октября прошлого года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Второе почетное место занимает <strong>Lada Priora </strong>с продажами в 105 тыс. 362 автомобиля этой модели. Аналогично, как и с<strong> Lada Kalina</strong>, ниже на 12%. За прошедший месяц продано 10 тыс. 843 <strong>Lada Priora</strong>, общий спрос за год на <strong>Lada Priora</strong> упал на 6% , нежели показателя октября 2011 г. в 11 тыс. 563 машин.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В тройку лидеров попала еще одна представительница семейства -<strong> Lada Granta</strong>. Этих автомобилей с начала 2012 года в России продано порядка 95 тыс. 162 единиц, а конкретно в октябре – 14 тыс. 197 единиц. Сравнительных данных с 2011 годом в рейтинге АЕБ нет, так как модель довольно новая и недавно вышла на рынок России.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На четвертом место в списке АЕБ оказался <strong>Hyundai Solaris</strong> с продажами составляющими - 94 тыс. 308 автомобилей за 10 месяцев. В отличие от отечественных автомобилей продажи иномарки в сравнении с январем-октябрем прошлого года выросли на 23%. При этом за октябрь месяц продано 9 тыс. 878 машин, меньше на 4% показателя прошлого года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>И пятое место занимает <strong>Ford Focus</strong>. Его продажи: за первые 10 месяцев - 75 тыс. 63 единицы, рост  15% по сравнению с 2011 годом, за октябрь - 6 тыс. 731 единицы, меньше октября 2011 года на 21%.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>А самыми популярными автомобилями <strong>автосалона Адмирал-Моторс</strong> являются <strong>поддержанные автомобили </strong>марки <strong>Toyota</strong>, <strong>Ford</strong>, <strong>Renault</strong>. Эти марки автомобилей можно <strong>купить в Санкт-Петербурге</strong> по невысоким ценам в кредит или за наличные. Теперь вы знаете где можно <strong>купить авто с пробегом в СПБ</strong> (Санкт-Петербурге).</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-14 05:06:16', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-14 05:06:16', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 689, 'Lada Kalina, Lada Priora, Lada Granta, Hyundai Solaris, Ford Focus, автосалон адмирал-моторс, купить поддержанный автомобили, Toyota, Ford, Renault, купить в санкт-петербурге, купить авто с пробегом в спб', 'Lada Kalina на первом месте в рейтинге АЕБ по продаже в России', 1, 787, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(269, 336, 'На территории РФ стартовал прием заказов на новый автомобиль - Toyota Auris', 'na-territorii-rf-startoval-priem-zakazov-na-novyj-avtomobil-toyota-auris', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>С 13 ноября 2012 года все уполномоченные партнеры и официальные дилеры <strong>Toyota</strong> на территории РФ начинали принимать заказы на новый <strong>Toyota Auris</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации компании «Тойота Мотор», автомобиль, который пользуются успехом у европейской аудитории, стал наиболее динамичным, дерзким, лучше укомплектованным и более экономичным в плане эксплуатации. Сборка модели осуществляется на заводе <strong>Toyota </strong>Motor Manufacturing UK.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новый <strong>Auris </strong>существенно усовершенствован по всем направлениям – отделка салона, дизайн экстерьера, а также оснащение. Обновленная конструкция шасси и оптимизация веса позволили усовершенствовать ходовые характеристики, а бензиновые двигатели объемом 1,6 и1,33 л. отличаются высокой эффективностью и малым уровнем шума.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Преимущества общего снижения высоты автомобиля на 55 мм.  и низкой посадки передней части кузова <strong>Auris</strong>, заключатся в усовершенствованной аэродинамике. Эти качества способствуют понижению уровня потребления топлива и выбросов углекислого газа. Помимо этого, благодаря применению высокопрочной стали удалось добиться оптимизации веса  и повышении жесткости кузова на 10%.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Линейка двигателей включает 2 бензиновых двигателя с передовыми системами <strong>Toyota </strong>Optimal Drive, Valvematic и Dual VVT-i, обеспечивающих идеальный баланс между ходовыми характеристиками и наслаждением от вождения с одной стороны и с другой  стороны экономией топлива и малым уровнем выбросов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Новая модель доступна на рынке России в 5 комплектациях – Комфорт Плюс, Комфорт, Элеганс, Престиж и Спорт, каждая из которых обладает своими характерные признаками как в экстерьере, так и во внутренней отделке. Все уровни комплектаций расширены и теперь охватывают больше опций, чем в предыдущих версиях.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для автомобиля <strong>Auris </strong>будут предлагаться выбрать 9 цветов кузова, из которых три  цвета: серый металлик, бронзовый металлик и светло-синий металлик - являются новыми.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Цена на автомобиль c 1.33-л. бензиновым двигателем и с 6-ступенчатой МКПП в комплектации «Комфорт» равна 735 тыс. руб., c 1.6-л. бензиновым двигателем с 6-ступенчатой механической КПП в комплектации «Комфорт Плюс» стоимость равна 774 тыс. рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ну что ж, теперь можно заказать замечательный новый автомобиль <strong>Toyota Auris</strong>. А у нас на <strong><a href="cars/" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> вы всегда можете <strong>купить авто в Санкт-Петербурге с пробегом</strong> марки  <strong>Toyota </strong>моделей <strong>Camry VI</strong>, <strong>Highlander I</strong>, <strong>Avensis </strong>и другие.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-14 05:10:28', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-14 05:10:28', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 688, 'Toyota Auris, Toyota, Auris, сайт по продаже автомобилей, купить авто в cанкт-gетербурге с пробегом, Toyota  Camry VI, Toyota Highlander I, Toyota Avensis', 'Уже можно заказать новый  Toyota Auris у официальный дилеров', 1, 672, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(270, 337, 'С 2013 года доверенность на автомобиль станет не нужна', 's-2013-goda-doverennost-na-avtomobil-stanet-ne-nuzhna', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>С января следующего года водителям не будет нужды приобретать доверенность на управление транспортным средством (ТС).', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>От имени МВД правительство дало положительный отзыв на требование комитета Госдумы по законодательству конституционному об отмене пункта 2.1.1 из Правил дорожного движения (ПДД). Ведомство  Колокольцева Владимира согласно исключить из ПДД требования водителя иметь в наличии и по требованию полиции отдавать для проверки документ, который подтверждает право управления и владения автомобилем.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В этом документе сообщается о планировании МВД соответствующего постановления правительства, исключающее требование из правил.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации «Известий» о сообщении первого зампреда комитета по законодательству конституционному, курирующего автотематику в Госдуме Лысакова Вячеслава, премьер Д. Медведев уже подписал документ, который предполагает подобные изменения. Решение правительства будет оглашено в ближайшие дни.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отмену доверенности также поддержали и в профсоюзе автомобилистов. По словам главы Межрегионального профсоюза водителей-профессионалов  Котова Александра, нередко случались случаи абсурдности с оформлением доверенностей с юридической точки зрения. Он подмечает, что в Европе давно отказались от данного документа.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отличная новость, как правило, в 90%  случаях у водителя автомобиля нет с собой доверенности по ряду причин. Это еще один плюс в пользу <strong>покупки поддержанного авто в Санкт-Петербурге</strong>. <strong><a href="cars/" target="_blank" title="Сайт по продажи автомобилей">Сайт по продажи автомобилей</a></strong> рад приветствовать вас в автосалоне Адмирал-Моторс.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-15 07:59:00', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-15 07:59:00', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 687, 'покупка поддержанного авто в санкт петербурге, сайт по продажи автомобилей, автосалое адмирал моторс в спб', 'С наступлением нового года в доверенности на автомобиль не будет необходимости', 1, 846, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(271, 338, 'Компания Toyota отзывает порядка 3 млн. автомобилей на ремонт', 'kompaniya-toyota-otzyvaet-poryadka-3-mln-avtomobilej-na-remont', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Японский автоконцерн <strong>Toyota </strong>Corp. рассказал об отзыве рекордного за всю историю количества автомобилей на ремонт – это порядка 3 млн. машин, реализованных за рубежом и внутри страны.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации ИТАР-ТАСС, внутри Японии отзываются около 1,52 млн. машин, реализованных с августа 2000 по конец 2011 года. Операция распространяется на 13 моделей Toyota, вместе с самым популярным в Японии - гибридным "<strong>Приусом</strong>".</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>За рубежом ремонту подлежат порядка 1,25 млн. автомобилей. Из-за неподходящего качества ряда деталей в автомобилях в некоторых случаях может заклинить управление  руля. Обнаружены также недостатки, связанные с энергосистемами ряда гибридных машин.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Эта ситуация, судя по всему, принесет большой ущерб репутации "Тойоты", которая еще недавно объявила о глобальных планах по расширению своего производства по всему миру.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Да, после того репутация бренда Тойоты будет хорошо покалечена. Однако, помимо автомобилей этого бренда, есть и другие не менее популярные и даже более. Поэтому, не отчаивайтесь и <strong>покупайте авто с пробегом в СПб</strong> (Санкт-Петербурге) в автосалоне Адмирал-Моторс, или прямо на <strong><a href="cars/" target="_blank" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-15 08:02:22', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-15 08:02:22', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 686, 'Toyota, Приус, покупка авто с пробегом в спб, сайт по продаже автомобилей', 'Серьезный удар по репутации Toyota', 1, 553, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(272, 339, 'Компания Nissan начнет продавать экономные Datsun посредством своей дилерской сети', 'kompaniya-nissan-nachnet-prodavat-ehkonomnye-datsun-posredstvom-svoej-dilerskoj-seti', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Японская компания <strong>Nissan </strong>намеревается реализовывать автомобили возобновленного экономного бренда Datsun посредством своей собственной дилерской сети.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Главное требование Nissan к бренду <strong>Datsun </strong>– наличие у бренда своего шоу-рума, а СТО будет с маркой «Ниссан». Данное предложение, стать дилером  Datsun, было оглашено  всем дилерам Nissan. И если таковых, как в 2008 году кризисов не будет, то автомобили новой марки, естественно, будут востребованы», - рассказал Николай Девятов, исполнительный директор автосалона «Викинги» и официальный дилер Nissan в Самарской области  и Тольятти.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>О возвращении Datsun было сообщено еще в марте 2012 года. Чуть позже представители компании Nissan рассказали, что обе модели возрожденной марки в скором времени выйдут на российский рынок, а именно в 2014 году. По неофициальной информации, дизайн машин Datsun будет похож на внешний облик LADA Granta, у которой «японцы» также позаимствуют платформу. Производство моделей по плану будет организовано на АВТОВАЗе, где в конце 2012 года начнется серийная сборка нового седана <strong>Nissan Almera</strong>.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Это сравнительно хороший автомобиль, который будет собран на вазовской платформе, применяемой при производстве LADA Granta. А она в свою очередь - отработанная платформа. Мы надеемся, что на АвтоВАЗе подтянут качество и комплектующих, и в результате будет очень хороший автомобиль. Мы, непременно, готовы заняться реализацией этих автомобилей, в наличии уже есть проект шоу-рума, который в скоро обоснуется рядом с нашим салоном Nissan. Новый шоу-рум станет по-настоящему небольшим, всего три автомобиля», - сообщил Николай Девятов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Всех автолюбителей бренда Datsun, автосалон Адмирал-Моторс поздравляет с этим грандиозным событием. И предлагает посетить наш <strong><a href="cars/" target="_blank" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong> в Интернете для ознакомления с грандиозными распродажами.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-16 05:13:47', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-16 05:13:47', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 685, 'Nissan, Datsun, Nissan Almera, Адмирал-Моторс, автосалон', 'Бренд Datsun будет возобновлен', 1, 746, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(273, 340, 'Chevrolet покажет в Лос-Анджелесе Spark с электроприводом', 'chevrolet-pokazhet-v-los-ndzhelese-spark-s-ehlektroprivodom', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В конце ноября текущего года компания <strong>Chevrolet </strong>покажет электрическую версию популярного и компактного автомобиля Spark<strong> </strong>в Лос-Анджелесе на Международном автосалоне.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве компании, автомобиль Spark EV — это один из 13-ти просто обновленных или принципиально новых автомобилей Chevrolet, которые будут выпущены в продажу в 2013 году. Модель автомобиля Spark завоевала сердца американских автолюбителей своим выразительным дизайном, системой MyLink и просторным салоном.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В первые 3 месяца продаж авто <strong>Spark </strong>показывает прекрасные результаты, особенно на рынке Лос-Анджелеса. В виду этого успеха электромобиль с подзарядкой будет предлагаться с тем же комплектом оборудования, который сделал столь популярными бензиновые модификации, но с применением технологий и характеристик исключительно электрического привода.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Помимо модели Spark EV, на Международном автосалоне Chevrolet покажет новый, более функциональный вариант информационно-развлекательной системы MyLink, среднеразмерный седан <strong>Malibu</strong> Turbo, новый полноразмерный седан Impala, компактный мини-автомобиль Sonic RS, Camaro Hot Wheels® Edition и полноразмерный кроссовер Traverse.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В автосалоне Адмирал-Моторс вы можете <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> бренда Chevrolet следующих моделей: Epica, Tahoe, Cruse.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-16 05:16:53', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-16 05:16:53', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 684, 'Spark, Chevrolet, Malibu, Camaro, Epica, Tahoe, Cruse', 'Теперь автомобиль Spark выпускается в электро версии', 1, 627, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(274, 341, 'Количество автомобилей к 2035 году достигнет отметки в 1,7 млн. экземпляров', 'kolichestvo-avtomobilej-k-2035-godu-dostignet-otmetki-v-17-mln-ehkzemplyarov', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>По прогнозу Международного энергетического агентства (IEA), максимальный прирост мирового автопарка будет обеспечен Китаем, рынок которого существенно вырос за последние несколько лет. К примеру, в 2000 году в Китае на 1000 человек приходилось всего на всего 4 машины.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>К 2010 году эта отметка выросла до 40 авто/1000 жителей, а к 2035-му он, она планирует достигнуть - 310 машин. В итоге, через 23 года в Китае будет зарегистрировано порядка 400 млн. транспортных средств, на сегодня их зарегистрировано — 60 млн.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кроме этого, к 2035 году значительно разрастется автопарк Индии — с сегодняшних 14 миллионов он поднимется до 160 миллионов машин.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Эксперты делают прогнозы, что через 23 года около 3% всех реализованных автомобилей будет приходиться на автомобили, работающих на природном газе. Порядка 4% рынка займут автомобили с электроприводом и 20% — гибриды.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Также предвидится появление в мире 16 млн. км. новых дорог, которые обойдутся в общей сложности в 20 триллионов $ государству, - сообщает motor.ru.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ассортимент <strong>поддержанных автомобилей в Санкт-Петербурге</strong> постоянно разрастается, кто-то покупает новые автомобили, кто-то довольствуется <strong>покупкой авто с пробегом</strong>, но и те и другие всегда знают чего хотят.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-17 06:22:54', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-17 06:22:54', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 683, 'авто, автомобиль, санкт петербург', 'Максимальный прирост мирового автопарка будет обеспечен Китаем', 1, 495, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(275, 342, 'Бренду Porsche грозит сжатие объемов производства', 'brendu-porsche-grozit-szhatie-obemov-proizvodstva', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Уменьшение продаж на автомобильном рынке Европы, которая уже отразилась на объемах производства основных производителей, скажется и на компании Porsche.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Производитель из Цуффенхаузена может уменьшить количество смен на своем основном заводе, несмотря на увеличивающийся спрос на новое поколение спорткара 911. В компании Porsche зарегистрировали скачок спроса на новые модели компании на рынках США и Китая, причем агентство Reuters обозначает со ссылкой на информацию производителя, что данный скачок легко компенсирует 4,5% снижение продаж на рынке Европы. По итогам октября месяца продажи компании подросли на 24%.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как сообщают «Авто.Вести.Ру», в Porsche все же сократят число рабочих смен на своем заводе, расположенным в Цуффенхаузене, который перейдет на пятидневную рабочую неделю с начала следующего года. Сейчас завод работает 7 дней в неделю. Помимо этого, источники Reuters утверждают, что возможно будет сокращение и оставшиеся рабочие смены из восьмичасовых превратиться в семичасовые смены.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сейчас завод производит около 170 автомобилей в день. Нужно отметить, что сокращать производство на заводе в Лейпциге, по выпуску Panamera, Cayenne и по производству Macan, не будут.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Аналитики из HIS Automotive уверенны, что продажи спорткаров Porsche возможно упадут в 2013 году на 10,7%. Напротив, Маттиас Мюллер, руководитель компании, ранее заявлял, что допускается 10% расхождение реальных итогов с первоначальными прогнозами продаж. Представители компании также подчеркнули, что структура производства Porsche разрешает гибко реагировать на колебания спроса.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Пока кто-то сжимает производство автомобилей, <strong>автосалон Адмирал-Моторс в Санкт-Петербурге</strong> только расширяет ассортимент поддержанных автомобилей. <strong>Купить авто в Санкт-Петербурге</strong> стало еще легче достаточно зайти на <strong><a href="cars/" target="_blank" title="Сайт по продаже автомобилей">сайт по продаже автомобилей</a></strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-17 06:26:47', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-17 06:26:47', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 682, 'Сайт, Интернет, автомобиль, машина, авто', 'Porsche сокращает производство, однако спрос на его автомобили только повышается', 1, 555, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(276, 343, 'Автомобиль LADA Priora скоро преобразится', 'vtomobil-lada-priora-skoro-preobrazitsya', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>АвтоВАЗ в следующем году совершит фейслифтинг автомобиля <strong>LADA Priora</strong>.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Данный автомобиль АвтоВАЗ будет выпускать до 2015-2016 года, потом в серию пойдет новая Priora, изготовленная на личной новой платформе АвтоВАЗа под названием LADA B. Вскоре автомобиль может приобрести черты LADA XRAY», - сообщили «АвтоСреде»в пресс-службе автоконцерна.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В следующем году «Приора» обновит интерьер, а конкретно, поменяется стиль приборной панели. Помимо этого, уже в текущем году для модели запланировано штудирование сидений с измененной конструкцией. В них будет предусмотрена конструкция боковых подушек безопасности.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Предшествующий рестайлинг LADA Priora АвтоВАЗ выполнил весной прошлого года – который включал - новые бамперы, видоизмененные повторители поворотников, используемые в боковых зеркалах и решетку радиатора.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из истории: модель LADA Priora выпускается с 2007 года, сейчас рекомендованная АВТОВАЗом стоимость базовой версии флагманского седана равна 334 тыс. руб., хэтчбеки от 340 тыс., и доступнейшая комплектация универсала встанет в 361 тыс. 900 рублей. В автосалоне Адмирал-Моторс <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> автомобили LADA и ВАЗ можно начиная от 44 999 рублей.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-18 03:55:22', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-18 03:55:22', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 681, 'АвтоВАЗ, LADA, Priora, автомобиль, Адмирал-Моторс, Санкт-Петербург', 'Фейслифтинг автомобиля LADA Priora ожидается в 2013 году', 1, 546, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(277, 344, 'Во Всеволожске Руководство Ford остановило конвейер', 'vo-vsevolozhske-rukovodstvo-ford-ostanovilo-konvejer', '<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Ford Sollers рассказала об остановке завода в Ленинградской области, Всеволожске 16 и 17 ноября текущего года.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В частности, на работу не выходили все 3 смены, сообщил представитель компании Сергей Кириллов. С понедельника завод заработает в обыкновенном режиме. Кириллов разъяснил необходимость остановки завода из-за нехватки комплектующих, - информирует газета «Ведомости».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Завод выпускает <strong>Ford Mondeo</strong> и <strong>Ford Focus</strong>. «В начале прошлой недели появились проблемы с комплектующими для Ford Mondeo, — сообщает Кириллов. — В связи с этим мы перевели заводской конвейер на производство Focus, но и здесь поставщики оказались не готовы к изменениям в сроках поставки». «Запчасти Ford для Mondeo производит бельгийский завод Ford, находящийся в Генке, на котором сейчас идет забастовка против закрытия предприятия», — разъясняет гендиректор Алексей Этманов, Межрегионального профсоюза работников автопрома (МПРА), и бывший лидер завода от профсоюза во Всеволожске.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Но у профсоюза завода другое объяснение остановки конвейера. С начала прошлой недели на предприятии проходит итальянская забастовка, рассказал «Ведомостям» Александр Кашицын, представитель от МПРА на заводе. Профсоюз планировал устроить день донора, когда большинство работников уходит сдавать кровь, разъясняет Кашицын. Руководство завода об этом не было предупреждено, поэтому и было остановлено производство в этот день, убежден он.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кто прав, кто виноват, конечно, выясниться, однако убыток за сутки составляет около 200 автомобилей. <strong>Продажа авто в Санкт-Петербурге с пробегом</strong> осуществляется в автосалоне Адмирал-Моторс, где вы найдете необходимую модель автомобиля Ford  и другие.</p>\n<div style="text-align: justify;"></div>\n<p style="text-align: justify;"> </p>', 1, 13, '2012-11-18 03:58:05', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-18 03:58:05', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 680, 'Ford Mondeo, Ford Focus, Ford, продажа авто в санкт-петербурге с пробегом, автосалон адмирал-моторс', 'Ford Sollers во Всеволожске остановлен из-за массового донорства его работников', 1, 583, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(278, 345, 'Ford Focus снабдили инновационной системой для защиты кромок дверей', 'ford-focus-snabdili-innovatsionnoj-sistemoj-dlya-zashhity-kromok-dverej', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В ноябре месяце в салонах официальных дилеров <strong>Ford </strong>начались продажи Ford Focus 2013 модельного года, в этих автомобилях в качестве опции предлагается система для защиты кромок дверей.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, данная инновационная опция была разработана инженерами Ford специально для того, чтобы защищать от вмятин и царапин как сам автомобиль, котором она оснащена, так и автомобили находящиеся рядом, которые при неаккуратном открытии дверей могут пострадать. Использование системы позволит избежать добавочных затрат на ремонт личного автомобиля и неприятных ситуаций с владельцами рядом стоящих автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>При открытии двери система автоматически срабатывает. В двери скрыта защитная планка, изготовленная из упругого пластика, она выдвигается менее чем на 150 мм. в момент открытия двери, защищая кромку и лакокрасочное покрытие автомобиля от повреждений в случае удара. Планка предохраняет именно те места, которые наиболее склонны к повреждениям.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Система обеспечивает защиту двери спереди от удара о разные объекты в 90% случаев. Для двери сзади этот показатель равен 85%. Использование системы будет особенно полезным для автовладельцев, часто совершаемых поездки с детьми и вынужденными парковаться на тесных парковках рядом с торговыми центрами или офисами.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Защиты кромок дверей почти не увеличивает вес автомобиля, система не влияет на работу имеющихся узлов и величина защиты автомобиля от удара сбоку. Защитная планка автоматически убирается всего за 60 миллисекунд, что позволяет мгновенно захлопнуть дверь. Планка состоит из специального состава, основа которой резина, способного выносить тысячи циклов «открытие-закрытие, работая почти бесшумно. В случае активного пользования автомобилем владелец может самостоятельно заменить защитную планку, необходимо для этого вставить сменный блок в систему.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отличная система, которая позволит автовладельцев избежать множества неприятностей. При <strong>покупке поддержанного автомобиля</strong> тоже стоит обратить внимание на работоспособность всех систем входящих в комплектацию авто с пробегом. С комплектациями автомобилей вы можете ознакомиться на <a href="cars/" target="_blank" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-19 06:19:46', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-19 06:19:46', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 679, 'Ford, авто с пробегом, сайте по продаже автомобилей, интернет, сайт', 'Ford Focus 2013 модельного года поступил в продажу в салоны официальных дилеров', 1, 559, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(279, 346, 'Продажи в России легковых автомобилей Volkswagen увеличились вдвое', 'prodazhi-v-rossii-legkovykh-avtomobilej-volkswagen-uvelichilis-vdvoe', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>С начала 2012 года бренд <strong>Volkswagen </strong>- легковые автомобили реализовала 137 тыс. 243 машины, усовершенствовав результаты 2011 года на 50,8%.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, в октябре текущего года было продано 14 тыс. 263 автомобиля, это на 17,9% выше показателей 2011 года за такой же период.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Реализация марки Volkswagen на территории России продолжает твердо расти. Обширный модельный ряд разрешает каждому выбрать автомобиль по своему вкусу, мы в свою очередь, продолжим удивлять наших поклонников огромнейшим выбором комплектаций и опций», – прокомментировал итоги месяца руководитель автомобильного бренда Volkswagen в РФ, Вратислав Страшил.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Постоянным лидером продаж на рынке  России остается модель <strong>Volkswagen Polo </strong>седан: в октябре месяце покупателям отдано 6 тыс. автомобилей, по сравнению с 2011 годом с показателем в 4 тыс. 953 единицы. Всего с начала этого года было реализовано 55 тыс. 196 автомобилей данной модели.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Компактный внедорожник Tiguan как правило занимает 2-е место в рейтинге российских продаж. За октябрь было продано 2 тыс. 354 автомобиля, что на 304 автомобиля больше чем в прошлом году. А общее количество с начала года составило 25 тыс. 846 автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На 3-м месте по итогам октября стала модель <span style="text-decoration: underline;">Volkswagen Jetta</span>:  было реализовано 1 тыс. 580 единиц за месяц. А в период с января по октябрь покупателям - 11 тыс. 525 автомобилей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автомобили Volkswagen в <span style="text-decoration: underline;">автосалоне Адмирал-Моторс</span>, занимающимся <strong>продажей поддержанный автомобилей</strong> также пользуются популярностью автомобили данной марки, например, модели <strong>Volkswagen Golf III Variant</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-19 06:27:42', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-19 06:27:42', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 678, 'Volkswagen, Volkswagen Polo, Volkswagen Golf III Variant, продажа поддержанных автомобилей, автосалое адмирал-моторс', 'Бренд Volkswagen увеличил спрос на автомобили вдвое по сравнению с 2011 годом', 1, 823, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(280, 347, 'Украинский Bogdan довольно популярен в России', 'ukrainskij-bogdan-dovolno-populyaren-v-rossii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Автомобили Bogdan, выпускающиеся на черкасском заводе «Богдан Мотор», на территории России продаются намного лучше, нежели рынка Украины.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации, полученной «Автоцентром» из «Укравтопром», ассоциации автопроизводителей Украины, за 9 месяцев текущего года украинцы купили всего 425 седанов Lada 110 и Lada 111 универсал в количестве 316 единиц. На рынке России за тот же период автомобиль «Богдан» увеличил продажу на 4%, показатель составил 6 тыс. 248 машин. По результатам, порядка 90% продаж автомобилей марки Bogdan происходит в России.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из истории: апрель 2010 года для корпорации «Богдан» стал стартом в поставке в Россию автомобилей <strong>Lada </strong>111 и 110 под собственным брендом Bogdan, расширив их пикапом Bogdan 2310, созданным украинскими конструкторами на основе «десятого» семейства. С тех самых пор на рынке России было продано около 23 тыс. автомобилей Bogdan, 2/3 из которых – это седаны Bogdan 110. Помимо этого, в июле 2012 года «Богдан» начала продажи в России и новых фургонов Bogdan 2312.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Эксперты отмечают, что автомобили под брендом Bogdan в России – это нишевые продукты, и соперничества моделям «АвтоВАЗа» они не составляют. Мнение экспертов - легковые автомобили Bogdan 110 и 111 не лишают <strong>Lada Priora</strong> покупателей, так как они ориентированы на автомобилистов, ностальгирующих по старой доброй «десятке».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Таким образом, спрос на автомобили Bogdan непосредственным образом снижается, однако «Богдан» пока не склонен отказываться от производства. По информации «Автоцентра» в пресс-центре «АвтоВАЗа», в первые девять месяцев 2012 года поставки автокомплектов на «Богдан Моторс» понизились на 49,7% до 7657 штук, а вообщем в 2012 году планируется поставить в Украину свыше 10 тыс. разобранных серий.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>А в Санкт-Петербурге достаточно популярна <strong>покупка поддержанных автомобилей</strong>. Востребованы автомобили брендов BMW, Chevrolet и другие. В Адмирал-Моторсе вы можете <strong>купить авто б у в кредит</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-19 18:32:16', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-19 18:32:16', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 677, 'Lada, покупка поддержанных автомобилей, купить авто б у в кредит, Lada Priora, Lada 111, Lada 110', 'Украинский автомобильный бренд Bogdan больше популярен в России нежели у себя', 1, 693, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(281, 348, 'Hyundai Sonata теперь дешевле на 60 тыс. рублей', 'hyundai-sonata-teper-deshevle-na-60-tys-rublej', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>«Хендэ Мотор СНГ» сообщил об акции, в рамках нее клиентам предоставляется скидка в 60 тыс. рублей на седан Sonata.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из информации сообщения компании, акция распространяется на все комплектации <strong>Hyundai Sonata</strong>: Style, Classic, Style+Navi, Prestige+Navi, Prestige независимо от типа коробки передач и объема двигателя.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сегодня Sonata с 2-литровым бензиновым двигателем (149 л.с.), и шестиступенчатой МКПП в комплектации Comfort оценивается в 879 тыс. 900 рублей. Данная версия с автоматической коробкой передач обойдется потребителям в 934 тыс. 900 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Комплектация Style с более обширным набором опций и автоматической трансмиссией теперь продается за 1 млн. 38 тыс. 900 рублей. Стоимость Style+Navi по акции снизилась до 1 млн. 93 тыс. 900 рублей. Напомним, что Sonata в этой комплектации снабжается камерой заднего вида, Bluetooth и навигационной системой с DVD-проигрывателем.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Вариация Prestige, отличающаяся от Style наличием в комплектации электрорегулировок сидений пассажира спереди и памятью настроек кресла водителя, 18-дюймовых колесных дисков, панорамной крыши со сдвижным люком, спортивной подвески, сейчас стоит 1 млн. 104 тыс. 900 рублей. Автомобиль Hyundai Sonata с 2-литровым мотором в версии Prestige+Navi, оборудованный системой навигации, Bluetooth и камерой заднего вида со скидкой реализуется по цене - 1 млн. 159 тыс. 900 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Максимальную комплектацию Prestige+Navi с объемом двигателя - 2,4 литра и мощностью 178 л.с. теперь можно купить  по цене в 1 млн. 239 тыс. 900 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>А автосалоне Адмирал-Моторс идет грандиозная акция, несколько <strong>поддержанных автомобилей в Санкт-Петербурге</strong> по супер ценам:</p>\n<p style="text-align: justify;">\n<ul>\n<li>Chrysler Sebring II, 2008 г.в. – 560 вместо 610 тыс. рублей;</li>\n<li>Toyota Avensis II, 2007 г.в. – 487 вместо 530 тыс. рублей;</li>\n<li>Suzuki Ignis, 2007 г.в. – 327 вместо 345 тыс. рублей;</li>\n<li><strong>Nissan Qashqai</strong>, 2007 г.в. – 517 вместо 537 тыс. рублей.</li>\n</ul>\n</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-19 19:00:00', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-19 19:00:00', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 676, 'Chrysler Sebring II, Toyota Avensis II, Suzuki Ignis, Nissan Qashqai, Адмирал-Моторс, автосалон, Hyundai Sonata, Hyundai', 'Ваша любимая Hyundai Sonata стала еще дешевле', 1, 847, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(282, 349, '100% покрытие самых крупных российских автотрасс будет обеспечено сотовыми операторами в 2013 году', '100-pokrytie-samykh-krupnykh-rossijskikh-avtotrass-budet-obespecheno-sotovymi-operatorami-v-2013-godu', '<p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>ОАО «ВымпелКом» («Билайн»), ОАО «Мобильные ТелеСистемы», ОАО «Ростелеком» и ОАО «МегаФон» сообщили о совместной реализации проекта по снабжению мобильной связью автомобильных магистральных дорог федерального значения «Каспий», «Байкал», «Уссури» и «Урал».</p>\n<p style="text-align: justify;" />', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>К возведению базовых станций по автодорогам планируется приступить в начале следующего года, осуществление проекта займет до 9 месяцев. Стоимость работ по организации оценивается в 3,2 миллиарда рублей, информировали в ОАО «ВымпелКом», в департаменте по связям с общественностью.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Автотрассы «Каспий», «Байкал», «Уссури» и «Урал» проходят через 20 регионов России от Владивостока до Москвы и образуют транспортные коридоры «Запад-Восток» и «Север-Юг». Проект был инициативой  Правительства РФ от распоряжения подписанного 05 мая 2012 года, его осуществление позволит обеспечить, как было отмечено ранее полное покрытие ключевых автомобильных артерий страны сотовой связью.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для того, чтобы обеспечить сплошное покрытие сотовые операторы установят более 200 базовых станций вдобавок к уже имеющейся телекоммуникационной инфраструктуре. Планируется следующее: каждый участник проекта поставит антенно-мачтовые сооружения на специальных участках, которые после будут использоваться для общего размещения оборудования от четырех операторов. В результате, будет обеспечено ковровое покрытие всех участников проекта мобильной связи на всем протяжении автотрасс. Траты на постройку антенно-мачтовых сооружений операторы распределят на паритетных началах. Траты по организации электроснабжения государство возьмет на себя.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>МТС, «МегаФон», «ВымпелКом» и «Ростелеком» выступают координаторами работ в определенных регионах. Операторы уже создали адресную программу для установки антенно-мачтовых сооружений и провели предварительные технические решения. А также подали заявки на присвоения определенных частот для базовых станций, на данном этапе проводят согласование по присоединению к электросетям, начали работы по оформлению в аренду земельных участков, для установки инфраструктуры связи.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Теперь можно спокойно совершать поездки по этим автотрассам на <strong>авто с пробегом</strong> от <strong>автосалона Адмирал-Моторс</strong>. <strong>Купить авто с пробегом в Санкт-Петербурге</strong> можно даже в кредит.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-21 03:11:40', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-21 03:11:40', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 675, 'автомобиль, авто с пробегом, автосалон адмирал-моторс, купить авто с пробегом в санкт петербурге, интернет, сайт', 'Четверка крупнейших сотовых операторов совместно с Правительством РФ обеспечит мобильной связью магистральные дороги «Каспий», «Байкал», «Уссури» и «Урал»', 1, 385, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(283, 350, 'Реализация Alfa Romeo упала до уровня 1969 года', 'realizatsiya-alfa-romeo-upala-do-urovnya-1969-goda', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>В текущем году мировые продажи автомобилей бренда <strong>Alfa Romeo</strong> упали до первоначальной отметки.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По данным Autonews.com, в 2012 году итальянцы реализуют менее 100 тыс. автомобилей. В последний раз данного низкого показателя, модель Alfa Romeo добивалась в 1969 году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Итальянцы планируют серьезно обновить модельный ряд. Alfa Romeo планирует показать на рынке США до четырех новых моделей в течение ближайших двух лет.</p>\n<p style="text-align: justify;">По некотором данным, итальянский производитель разработает кроссовер на платформе Jeep, седан Giulia, спорткар 4С, а также купе Detto. О поставках автомобилей в другие страны Латинской и Северной Америки пока неизвестно.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Да, модель Alfa Romeo не пользуется спросом и в поддержанном состоянии. <strong>Автосалоны СПб</strong> по продаже <strong>авто с пробегом</strong> имеют в наличии наиболее популярные и востребованные модели автомобилей, однако вы всегда можете найти у нас свою эксклюзивную модель.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-21 03:18:03', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-21 03:18:03', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 674, 'Alfa Romeo, автосалоны СПб авто с пробегом, автомобиль, Интернет, сайт', 'Бренд Alfa Romeo стал критически невостребованным', 1, 492, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(284, 351, 'Потребители смогут сами подбирать комплектацию для автомобилей Lada', 'potrebiteli-smogut-sami-podbirat-komplektatsiyu-dlya-avtomobilej-lada', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">АвтоВАЗ планирует предложить покупателям самим подбирать, комплектацию своего будущего автомобиля.</span></p>', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">«В ситуации понижения доли АвтоВАЗа на рынке и роста конкуренции со стороны иных производителей таких же видов продукции компания планирует усиливать конкурентоспособность своей продукции за счет внедрения системы опций заказа автомобилей посредством сервисно-сбытовой сети АвтоВАЗа», — сообщается в отчете компании за 3 квартал 2012 года.</span></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На предприятии разъяснили, что данная система предполагает не только заказ автомобиля по доступным комплектациям, но и подбор добавочных опций по каждому пакету. Как информирует РБК daily, сейчас почти весь модельный ряд АвтоВАЗа комплектуется фиксированными пакетами с комплектацией «норма», «стандарт», «люкс». Новая модель <strong>Lada Largus</strong>, например, предлагается в комплектациях «люкс» и «норма».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>При этом «норма» по цене дешевле на 7 тыс. руб. люксовой Largus, оснащенной бортовым компьютером с дисплеем на приборной панели, центральным замком с дистанционным управлением и двутональным звуковым сигналом. Начать вводить систему опций планируется с модели Lada Largus.<span style="white-space: pre;"> </span></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации Ассоциации европейского бизнеса, за 10 месяцев 2012 года дилеры АвтоВАЗа продали около 449,1 тыс. автомобилей Lada, это на 9% меньше, нежели годом ранее. Сейчас доля АвтоВАЗа на рынке России составляет около 20%.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По запросу в поисковой строке «<strong>Автосалоны СПб авто с пробегом</strong>» вы найдете множество автосалонов, но только Адмирал-Моторс может предоставить вам такие услуги как подбор опций к автомобилю, ознакомиться со всем ассортиментом <span style="text-decoration: underline;">авто с пробегом</span> вы можете у нас на <strong><a href="cars/" target="_blank" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong>.</p>\n<p> </p>', 1, 13, '2012-11-22 03:23:19', 792, '', '2012-11-22 03:27:39', 792, 0, '0000-00-00 00:00:00', '2012-11-22 03:23:19', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 3, 673, 'Lada, автосалоны спб авто с пробегом, адмирал-моторс, санкт-петербург, авто с пробегом, сайте по продаже автомобилей, сайт, интернет', 'АвтоВАЗ предлагает самостоятельный выбор комплектации и добавочных опций при выборе Lada', 1, 488, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(285, 352, 'Свой 40-летний юбилей отмечает симметричный полный привод Subaru', 'svoj-40-letnij-yubilej-otmechaet-simmetrichnyj-polnyj-privod-subaru', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">В этом году исполняется ровно 40 лет со дня рождения - «Системы симметричного полного привода» (Sуmmetrical AWD) - одной из уникальнейших технологий </span><strong style="text-align: justify;">Subaru</strong><span style="text-align: justify;">.</span></p>', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">В честь данного знаменательного события, компания ООО «Субару Мотор» дарит ценные и полезные подарки при покупке автомобилей Subaru. А с 20 ноября по 31 декабря (включительно)  на автомобили Subaru предлагаются специальные цены.</span></p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Symmetrical AWD состоит из продольно размещенного оппозитно-горизонтального двигателя SUBARU BOXER и полноприводной 4WD трансмиссии, расположенной симметрично. Данная уникальная трансмиссия была основой развития Subaru, начиная с первого автомобиля на полном приводе, построенного компанией почти 4 десятилетия назад.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>У нас на <strong><a href="cars/" target="_blank" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> есть несколько моделей полноприводных <span style="text-decoration: underline;">авто с пробегом</span>.</p>\n<p> </p>', 1, 13, '2012-11-22 03:27:55', 792, '', '2012-11-22 03:30:39', 792, 0, '0000-00-00 00:00:00', '2012-11-22 03:27:55', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 672, 'Subaru, авто с пробегом, сайт, интернет', '40 лет как появилась Система симметричного полного привода Subaru', 1, 480, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(286, 353, 'Renault получит контроль на АвтоВАЗе', 'renault-poluchit-kontrol-na-vtovze', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span><strong>Renault</strong>, французский автопроизводитель подал в антимонопольное ведомство Германии ходатайство о получения контроля в АвтоВАЗе.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ранее, в конце сентября 2012 года, Федеральная антимонопольная служба России уже  одобрила совместное ходатайство <strong>Nissan</strong>, Renault и ГК «Ростехнологии» о покупке акций АвтоВАЗа. В мае текущего года «Ростехнологии» и Renault-Nissan договорились создать СП для приобретения мажоритарной доли в АВТОВАЗе» и заключили соответствующее соглашение, которое не имеет обязательной юридической силы, рассказывает агентство «Финмаркет».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Основываясь на соглашении, «Ростехнологии» и Renault-Nissan передадут пакеты акций АвтоВАЗа, <span style="white-space: pre;"> </span>принадлежащие им, в виде инвестирования в капитал совместного предприятия, которому собственно и будет принадлежать контроль над АвтоВАЗом. Renault-Nissan инвестирует порядка 750 млн. долларов, и обеспечит автопромышленной французско-японской группе участие в совместном предприятии и обладание акциями в размере 67,13% к середине 2014 года. СП будет иметь 74,5% акций АвтоВАЗа.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Renault, получившая акции АвтоВАЗа в 2008 году в размере 25%, вложит в совместное предприятие порядка 300 млн. долларов. Компания Nissan, в настоящее время не являющаяся акционером АвтоВАЗа, инвестирует в него около 450 млн. долларов. Процесс вкладов со стороны Nissan и Renault будет поэтапным и будет продолжаться до 2014 года. СП к этому моменту приобретет весь пакет акций АвтоВАЗа, который принадлежал «Тройке Диалог».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сейчас основные акционеры АВТОВАЗа - ГК «Ростехнологии», которые владеют 28,98% уставного капитала - это 36,32% обыкновенных акций, и компания Renault – 25% уставного капитала или обыкновенных акций, - передает"Автостат".</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Наконец-то Renault получит контроль, как надо было сделать давно. <strong>Поддержанные автомобили купить в Санкт-Петербурге</strong> в хорошем состоянии можно только в <span style="text-decoration: underline;">Адмирал-Моторсе</span>. Автосалон, в котором до 27 разновидностей автомобильных брендов, всегда готов радовать своих клиентов акциями и услугами в большом ассортименте</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-23 06:34:58', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-23 06:34:58', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 671, 'Renault, Nissan, поддержанные автомобили купить в санкт-петербурге, Адмирал-Моторс, автосалон, сайт, интернет', 'Renault наконец-то получит контроль на Автовазе', 1, 678, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(287, 354, 'BMW изготовит переднеприводные модели', 'bmw-izgotovit-peredneprivodnye-modeli', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Немецкий бренд <strong>BMW </strong>намерен реализовать первые 12 моделей автомобилей с передним приводом.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как сообщил в интервью Automotive News Ян Робертсон, член совета директоров компании,  переднеприводная платформа BMW UKL была премьерована на автосалоне в Париже. Платформа будет в основе следующего поколения моделей MINI в 2014 году, а также остальных моделей BMW, включая и новый автомобиль BMW 1 Series, который будет премьерован в 2018 году.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>«Одно из самых главных преимуществ платформы UKL том, что мы в состоянии дать старт многим продуктам одновременно, так как мы создаем один раз», — сообщил Робертсон.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Расходы на создание новой архитектуры переднеприводной модели будут распределены между моделями и брендами, что разрешит компании предлагать конкурентоспособные цены на свои автомобили, - информирует «Газета.Ru».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Стоит подчеркнуть, что еще в 2006 году BMW была категорически против идей переднеприводных автомобилей, высмеивая конкурентов.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><strong>Купить авто с пробегом в СПб</strong> (Санкт-Петербурге) бренда BMW не составит никакого труда, если вы обратитесь в <span style="text-decoration: underline;">автосалон Адмирал-Моторс</span>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-23 06:38:03', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-23 06:38:03', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 670, 'BMW, купить авто с пробегом в спб, автосалон, адмирал-моторс', 'BMW реализует первые 12 моделей автомобилей с передним приводом', 1, 430, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(288, 355, 'Dacia дебютирует пикап и универсал Logan в 2013 году', 'dacia-debyutiruet-pikap-i-universal-logan-v-2013-godu', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span><span style="text-decoration: underline;">Dacia</span> покажет пикап и универсал <strong>Logan </strong>в следующем году в Румынии.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как информировал вице-президент компании Константин Строе, пикап будет собран на базе седана Logan 2-го поколения. А универсал Logan покажет собой крупную версию седана с крупным багажником. Высота машины при этом не изменится.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сейчас на рынке Румынии Logan нового поколения можно приобрести по цене от 6 тыс. 690 евро. Базовая комплектация автомобиля оснащается бензиновым двигателем емкостью 1,2 л. и мощностью 75 л.с.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Кроме того, для модели доступен 90-сильный бензиновый мотор, который устанавливается на Twingo и <span style="text-decoration: underline;">Clio</span>. Дизельные агрегаты реализуются в двух версиях 1,5 л. двигателя: мощностью 90 и 75 лошадиных сил, - сообщает AutoNews.Ru.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>После дебюта в Румынии, появления нового пикапа и универсала Logan в России не за горами. Уже сейчас <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong> бренда Logan можно в любом из <span style="text-decoration: underline;">автосалонов Адмирал-Моторс</span>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-24 05:27:49', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-24 05:27:49', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 669, 'Dacia, Logan, Clio, купить поддержанный автомобиль в Санкт-Петербурге, автосалон, адмирал моторс', 'Пикап и универсал Logan будет представлен Dacia  в Румынии', 1, 912, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(289, 356, '95-летие отмечают пикапы Chevrolet', '95-letie-otmechayut-pikapy-chevrolet', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>По некоторой информации, несколько пикапов <strong>Chevrolet </strong>490 были собранны для внутреннего применения еще в далеком 1916 году, а еще ранее, в 1914-м, несколько шасси были переформированы в машины скорой помощи и экспортированы во Францию.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Из сообщения компании Chevrolet, первые образцы серии были изготовлены во Флинте, (Мичиган), 22 ноября 1916-го и выпущены с завода 2 декабря этого же года. Официальный выпуск на рынок легких грузовиков марки Chevrolet отмечен выпуском 2-х четырехцилиндровых авто 1918-го модельного года.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Обе эти модели представляли собой грузовые шасси с металлическим капотом. В те времена покупатели часто укомплектовывали автомобили деревянными кабинами, кузовами-фургонами или грузовыми платформами (кузовами) – в зависимости от личных потребностей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Chevrolet 490 Light Delivery, полутонный легкий грузовик, по цене 595$, фактически являлся версией авто Chevrolet 490 без кузова с умноженными задними рессорами, с поставленным на нем фургоном или кузовом он был экономичным легким и маневренным грузовиком для малого бизнеса, который процветал по всей Америке после Первой мировой войны, во время экономического бума.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Модель Т (по предположительным данным – сокращение от Truck) грузоподъемностью 1 тонна и стоимостью 1125$ была разработана на базе автомобиля серии FA с удлиненной базой и усиленной рамой и запущена Chevrolet в производство в 1918 г. Двигатель мощностью 37 л. с. снабжал этот больший грузовик нужной мощностью для перевозки тяжелых грузов с ограниченной максимальной скоростью 40 км/час.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Когда полноразмерные пикапы стали основой американской экономики, их среднеразмерные модификации были востребованы и за пределами Соединенных штатов Америки.  Среднеразмерный глобальный пикап Chevrolet Colorado был создан с целью роста присутствия бренда Chevrolet на активно развивающихся мировых рынках.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Первая глобальная версия <span style="text-decoration: underline;">Colorado</span>, разработанная группой экспертов по легким грузовикам отделения GM do Brasil официально стартовала в производство в ноябре прошлого года в Таиланде, где продажи таких пикапов наиболее активны.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На протяжении последующих лет Colorado будет показан на многих глобальных рынках, охватывая США, где он будет презентоваться как топливосберегающая альтернатива для тех клиентов, которые не нуждаются во всех возможностях полноразмерного пикапа.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Да 95 лет большой срок, за это время <span style="text-decoration: underline;">Chevrolet </span>показала себя только с лучшей стороны. Как и <strong>автосалоны в СПб авто с пробегом</strong> предлагающие под названием Адмирал-Моторс.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-24 05:30:45', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-24 05:30:45', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 668, 'Chevrolet, автосалон, адмирал-моторс, сайт, интернет', '95-лет отмечают пикапы Chevrolet', 1, 616, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(290, 357, 'Импорт бензина – вот что вскоре начнет делать Россия', 'import-benzina-vot-chto-vskore-nachnet-delat-rossiya', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">РФ, несмотря на свой статус одного из крупнейшего нефтедобытчика в мире, может скоро начать импортировать бензин.</span></p>', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">Это пересекается с запретом, который вступит в силу с 1 января 2013 г., связанный с продажей топлива ниже "Евро-3" класса. Подобный график перехода на новые экостандарты, прописанный в топливном техрегламенте, менялся уже два раза, - информируют "Авто.Вести.Ру".</span></p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Из раза в раз нефтяные компании заявляли, что не успевают по времени с модернизацией нефтепереработки для выпуска более качественного топлива. В версии техрегламента, существующей на данный момент, с 1 января 2013 года в России будет запрещена продажа низкосортного топлива.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Нефтяники снова попросили об отсрочке. А некоторые чиновники даже сделали заявление, о переходе на новый класс для некоторых компаний может быть перенесен, чтобы эти компании все же постарались успеть обновить свои установки. Но, как сообщил "Ведомостям" Павел Федоров, замминистра энергетики, устанавливать новые сроки по переходу на бензин "Евро-3" класса на этот раз не будут. В ином случае нарушается концепция запланированного постепенного улучшения качества автомобильного топлива.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Хотя, факт остается фактом. Не все нефтяные компании успели подготовиться, об этом сообщил Федоров, не назвав, конечно же, конкретных имен. По его мнению, из оборота будет исключено не более 1 млн. тонн бензина. Компенсировать подобное планируется именно за счет импорта. Компании, которые опоздали, уже обязались организовать поставки топлива из-за рубежа. Как заметил чиновник, сейчас идут переговоры о поставке в 2013 году 2 млн. тонн топлива из Белоруссии.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Данное решение уже поддержал куратор Аркадий Дворкович, топливно-энергетического комплекса страны и вице-премьер.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Теперь и бензин из России будет импортировать в другие страны. <span style="text-decoration: underline;">Автосалон Адмирал-Моторс</span> занимается <strong>продажей авто с пробегом в Санкт-Петербурге</strong>, и в его ассортименте вы можете встретить как импортные модели автомобилей, так и наши отечественные <span style="text-decoration: underline;">ВАЗ</span>, <span style="text-decoration: underline;">LADA</span>.</p>\n<p> </p>', 1, 13, '2012-11-25 12:51:10', 792, '', '2012-11-25 12:56:11', 792, 0, '0000-00-00 00:00:00', '2012-11-25 12:51:10', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 667, 'автосалон, адмирал-моторс, продажа авто с пробегом в санкт-петербурге, LADA, ВАЗ, сайт. интернет', 'С 1 января 2013 г.будет запрещена продажа низкосортного топлива', 1, 405, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(291, 358, 'Самым популярным автомобилем в Европе по-прежнему остается Volkswagen Golf', 'samym-populyarnym-avtomobilem-v-evrope-po-prezhnemu-ostaetsya-volkswagen-golf', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">По данным отчета компании JATO Dynamics, популярнейшей машиной в Европе по результатам октября стал автомобиль</span><strong style="text-align: justify;"> Volkswagen Golf</strong><span style="text-align: justify;">.</span></p>', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">Volkswagen Golf давно является лидером в рейтингах по продажам у европейцев. За ним строчку в рейтинге популярности занимает Ford Fiesta и Peugeot 208. Как информирует «Авто.Вести.Ру», впервые за продолжительное время в "десятку" самых популярных автомобилей в Европе вошел компактный хэтчбек </span><span style="text-align: justify; text-decoration: underline;">Fiat </span><span style="text-align: justify;">Panda, и именно он показал наибольший рост продаж.</span></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Даже при смене поколений наиболее продаваемой моделью в октябре месяце остался Volkswagen Golf, показатель которого составляет 33 тыс. 156 машин (12,1% падение продаж). Следующее место за ним честно сохранил <span style="text-decoration: underline;">Ford Fiesta</span>, его продажи снизились на 1,1% и составили 26 тыс. 145 единиц. А вот новенький Peugeot 208 поднялся на 3-ю строчку и продажи этой модели стали равны 23 тыс. 352 машины.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Популярные модели, поддержанных автомобилей вы также можете <span style="text-decoration: underline;">купить в автосалоне Адмирал-Моторс в Санкт-Петербурге</span>.  На нашем <strong><a href="cars/" target="_blank" title="Cайт по продаже автомобилей">сайте по продаже автомобилей</a></strong> вы можете ознакомиться с ассортиментом этих автомобилей и не только.</p>\n<p> </p>', 1, 13, '2012-11-25 12:56:19', 792, '', '2012-11-26 01:07:04', 792, 0, '0000-00-00 00:00:00', '2012-11-25 12:56:19', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 3, 666, 'Volkswagen Golf, Fiat, купить в автосалоне адмирал-моторс в Санкт-Петербурге, сайт по продаже автомобилей, сайт, интернет', 'Volkswagen Golf, Ford Fiesta и Peugeot 208 в тройке популярности', 1, 627, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(292, 359, 'С 24 ноября отменена доверенность на управление автомобилем в России', 's-24-noyabrya-otmenena-doverennost-na-upravlenie-avtomobilem-v-rossii', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Правительством России еще 12 ноября издано постановление № 1156, в котором говорится «О внесении изменений в некоторые акты Правительства РФ».', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в отделении пропаганды по Алтайскому краю УГИБДД ГУ МВД России, постановление Правительства РФ № 1156 вступит в действие по истечении 7 дней после его официального опубликования в «Российской газете». «Российская газета» опубликовала этот документ, один из самых значительных для участников дорожного движения, 16 ноября. Таким образом, с 24 ноября 2012 года доверенность на управление автомобилем нет необходимости иметь в наличии.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Этим постановлением, из пункта 2.1.1 (Правил дорожного движения) ликвидируется обязанность водителя в случае управления механическим транспортным средством в отсутствие его владельца иметь в наличии и передавать по требованию сотрудников полиции документ для проверки, который подтверждает право владения, или распоряжения, или пользования,  данным транспортным средством (доверенность).</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Отметим, что доверенность – это уже второй документ по счету, обязанность которого передавать сотрудникам полиции и возить с собой для проверки исключается из ПДД в текущем году. Первым подобным документом стал талон о прохождении технического государственного осмотра.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>С этого самого времени можно смело водить свой любимый автомобиль. А если его нет, то предлагает посетить <span style="text-decoration: underline;">автосалон Адмирал-Моторс</span>, где вы сможете <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-26 01:04:21', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-26 01:04:21', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 665, 'автосалон, Адмирал-Моторс, купить поддержанный автомобиль в санкт-петербурге, сайт, интернет, авто с пробегом', 'Наконец-то отменена доверенность на управление автомобилем!!!', 1, 353, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(293, 360, 'Новый хэтчбек от Chrysler', 'novyj-khehtchbek-ot-chrysler', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>У автомобильного бренда <strong>Chrysler </strong>в ближайшем времени может появиться новая базовая модель.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Для создания хэтчбека Chrysler 100 была применена та же платформа, что в основе седанов Fiat Viaggio и Dodge Dart. В основные конкуренты новинке уже противопоставили <span style="text-decoration: underline;">Ford Focus</span>, информирует Авто.Вести.Ru.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>"Я уверен, что мы должны развеять миф о том, что хэтчбеки в Соединенных штатах Америки не пользуются популярностью", - подобное заявление сделал Серджио Маркионне, руководитель альянса Fiat-Chrysler  в интервью агентству Automotive News.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По его мнению, доказательством потенциальной успешности хэтчбеков вполне может служить их популярность в Европе – первоочередно речь идет о Ford Focus. Помимо этого, с седаном Dodge Dart и Chrysler 100 у американских дилеров концерна предстанет возможность предлагать сразу 2 модели в популярном сегменте.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Серийный Chrysler 100 почерпнет моторную гамму у коллег по платформе, вместе с 1,4-литровым турбированным двигателем. Сопровождать такие силовые агрегаты будет фирменная роботизированная коробка передач с двумя сцеплениями.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Chrysler 100 уже почти готов к производству, но официальное "добро" пока еще не получено. Если конвейеру все же будет положен старт, по словам Маркионне, модель может появиться и в Европе, но, скорее всего, реализовываться в Старом свете хэтчбек будет под наименованием Lancia Delta.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Всех автолюбителей бренда Chrysler мы предлагаем обязательно посетить <span style="text-decoration: underline;">автосалон Адмирал-Моторс</span>. Только здесь представлены поддержанные автомобили этой марки, и только здесь вы можете воспользоваться услугой – «<strong>купить автомобили б у в кредит</strong>».</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-26 01:07:06', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-26 01:07:06', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 664, 'Chrysler, Ford Focus, автосалон, адмирал моторс, купить автомобили б у в кредит, сайт, интернет, авто с пробегом', 'Новая базовая модель хэтчбека Chrysler 100 скоро появится в продаже', 1, 537, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(294, 361, 'Цена на бензин по России перескочила 30-рублевую отметку', 'tsena-na-benzin-po-rossii-pereskochila-30-rublevuyu-otmetku', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">Цены на дизельное топливо бензин в России продолжают повышаться из недели в неделю.</span></p>', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">По информации Росстата, 30-рублевую отметку перешла стоимость дизельного топлива. По данным на 29 октября, дизтопливо за 1 литр составляло 29,42 руб., а на 6 ноября уже оно подорожало до 29,66 руб. по стране в среднем. Чуть позже, 12 ноября, стоимость на дизельное топливо установилось на уровне 29,82 руб., а средняя цена дизтоплива была на отметке - 30,09 руб.</span></p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Подчеркнем, что средняя цена автомобильного бензина увеличивается чуть более низкими темпами. Так к примеру, с 29 октября с отметки - 28,77 руб. бензин подорожал за 7 дней всего на 6 коп. в среднем по России  и составил - 28,83 руб., на 12 ноября его цена осталась на прежнем уровне. А за последнюю неделю цена на бензин увеличилась всего на 3 копейки.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Таким образом, на 19 ноября средняя цена на автомобильный бензин в России составляет 28,86 руб. Бензин марки А-76 (80) в среднем по стране равен - 26,38 руб., марки АИ-92 (93 и т.п.) - 28,06 руб. и марки АИ-95 и выше – 30,69 руб.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>В России, в 27 центрах субъектов зарегистрировано повышение средних цен на бензин. Значительное удорожание бензина отмечалось в Благовещенске, Томске и Омске - на 1,2-1,4%.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>В остальных 45 центрах субъектов РФ цены на бензин остались на уровне ранней недели. Снижение цены на бензин зафиксировано в 11 центрах субъектов России, включая Архангельск - на 0,8%. В Санкт-Петербурге и Москве потребительские цены на автомобильный бензин в среднем за неделю почти не изменились.</p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Бензин, как и коммунальные услуги, растет быстрыми скачками. Но это не приведет к снижению <span style="text-decoration: underline;">продаж автомобилей</span>, все равно автолюбители будут желать <strong>купить в Санкт-Петербурге авто с пробегом</strong>.</p>\n<div style="text-align: justify;"></div>\n<p> </p>', 1, 13, '2012-11-27 05:36:03', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-27 05:36:03', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 663, 'купить в санкт-петербурге авто с пробегом, продажа автомобилей в спб, сайт, интернет, авто с пробегом', 'Цены на дизельное топливо в России растут бешеными темпами', 1, 351, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(295, 362, 'За «невидимые» номера автовладелец потеряет права', 'za-nevidimye-nomera-avtovladelets-poteryaet-prava', '<p> </p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Российские автолюбители выдумывают разные способы уйти от электронного штрафа, которые выписываются комплексами автофиксации нарушений.</p>', '<p> </p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как информирует «Российская газета», нарушители парковки в Москве на практике, научились применять дыры в законе и таким образом уходить от ответственности. По всей столице теперь вдоль дорог стоят <span style="text-decoration: underline;">авто</span> с затертыми или завешенными номерами.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>За парковку не по правилам хозяин авто в Москве должен раскошелиться на 3000 руб. Номерные знаки автомобилей стали завешивать газетами, георгиевскими ленточками, клочками бумаги. Электронные приборы "Парконы" в этом случае не могут считать номера и штраф нарушителям не приходит. Самые хитрые стали прикрывать номера листьями деревьев. Типа того как лист упал на номер, а хозяин тут и вовсе ни при чем.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Серьезно к этой проблеме подошли в ГИБДД Московской области. После появления в Подмосковье электронных приборов видео- и фотофиксации, водители тоже поначалу стали подтирать номера и клеить на них все что угодно. За 10 месяцев 2012 года выявлено 3000 подобных нарушений, и практически 300 любителей делать из машины гербарий уже лишились водительских прав.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Инспектор вправе остановить движущуюся машину с нечитаемыми номерными знаками, и составить протокол. А после выдается санкция вплоть до лишения прав от 1 - 3 месяцев. К всеобщему сожалению, для машин припаркованных эти правила не действуют. В этом случае инспектор ГИБДД может лишь дождаться, пока хозяин авто вернется, и потом потребовать устранить нарушение.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Сотрудник ГИБДД также может самостоятельно отлепить лист, который мешает работе "Паркона" считывать номер. Но в тех условиях, когда вдоль дорог припаркованы сотни машин нарушителей, как правило, сил и рук не хватит каждый раз отклеивать все то, что там наприклеивали к номерам находчивые автовладельцы. Первые аппараты автофиксации нарушений правил парковки вполне возможно будут закуплены уже в этом году для подмосковной ГИБДД.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По сообщению Максима Волнухина, начальника УГИБДД Московской области, отделения исполнения административного законодательства, покупкой приборов "Паркон" занимается Центр организации дорожного движения. На данном этапе уже ясно, что, инспекторы ДПС столкнутся с трудностями, даже получив умную технику. Свои предложения в Мособлдуму в УГИБДД уже внесли.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как уже говорилось в более раннем сообщении, автолюбителя не остановит даже лишение прав за нечитаемые номера, по крайней мере, на несколько месяцев, от огромного желания <strong>купить поддержанный автомобиль в СПб</strong> (Санкт-Петербурге).</p>\n<p> </p>', 1, 13, '2012-11-27 05:38:56', 792, '', '2012-11-27 05:41:48', 792, 0, '0000-00-00 00:00:00', '2012-11-27 05:38:56', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 662, 'авто, купить поддержанный автомобиль в спб, сайт, интернет, авто с пробегом', 'Теперь будут лишать водительских прав за нечитаемые номера', 1, 346, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(296, 363, 'Стартовали продажи Renault Duster в России с системой ESP', 'startovali-prodazhi-renault-duster-v-rossii-s-sistemoj-esp', '<p />\n<p style="text-align: justify;" /><span style="white-space: pre;"> </span>Во всех дилерских центрах <strong>Renault </strong>по России стартовал прием заявок на Duster с системой ESP.', '</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По информации в российском представительстве автоконцерна, серийное производство обновленного Duster с системой ESP начнется в декабре 2012 года на заводе Renault в Москве. Система курсовой устойчивости ESP будет доступна со всеми силовыми установками Duster – бензиновыми и дизельным двигателями 1.6 л. и наиболее востребованным на рынке 2.0 л. двигателем.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Система ESP распределяет крутящий момент за счет притормаживания проскальзывающего колеса. Такое управление распределением на задние колеса крутящего момента настроено таким образом, чтобы сблизить к минимуму риск избыточной поворачиваемости в режимах LOCK и AUTO.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В автомобиле <strong>Renault Duster</strong> набор активной безопасности имеет в наличие такие современные электронные системы, как АБС, система экстренного торможения (EBA) и система распределения тормозного усилия (EBD), дополненные системой стабилизации ESP. В набор пассивной безопасности входят: боковые подушки безопасности в зависимости от версии, подушки безопасности водителя и пассажира,  ремни безопасности с ограничителями усилий для пассажира и водителя.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Реализация внедорожника Renault Duster растет с момента выхода автомобиля на рынок России в марте этого года. В октябре месяца Duster во 2-й раз становится лидером в своем сегменте с объемом продаж в 6 тыс. 680 единиц.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Всем автолюбителям автомобилей Renault просто необходимо посетить <strong>автосалон Адмирал-Моторс</strong>. В котором вы можете <strong>купить поддержанный автомобиль в Санкт-Петербурге</strong>.</p>\n<div style="text-align: justify;"></div>\n</p>', 1, 13, '2012-11-28 15:33:03', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2012-11-28 15:33:03', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 661, 'Renault, Renault Duster, автосалон адмирал моторс, купить поддержанный автомобиль в санкт-петербурге', 'Renault Duster доступен с системой ESP', 1, 687, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(297, 364, 'В России такси станет «электрическим»', 'v-rossii-taksi-stanet-ehlektricheskim', '<p> </p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>На улицах городов России появится новый тип такси - электромобили.</p>', '<p> </p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Данный проект Минтранс готов запустить в Калининграде и в Казани. Плюс объединить усилия регионального и федерального бюджетов и энергетиков по разработке инфраструктуры для электромобилей по всей территории России, - сообщает «Российская газета».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Как сообщил Владимир Котляренко, представитель ведомства, речь пойдет о федеральной программе. Своеобразный эксперимент Минтранса по использованию электромобилей начнется в рамках совместного проекта с ООН по снижению парниковых газов. Но и другие регионы не останутся в стороне. Уже весной 2013 года вывести на линию электромобили намерены власти Ставропольского края в Кисловодске.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Жители Кисловодска решили заказать достаточно большую партию электромобилей в количестве 100 единиц для такси. Кисловодск выбран в качестве экспериментальной площадки не случайно, так как это одна из самых популярных здравниц.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Субсидии из бюджета региона на проект составят 350 млн. рублей, таким образом, на каждый автомобиль частная компания при его приобретении получит компенсацию в 350 тыс. рублей. В Подмосковье в скором времени намерены выпустить на линию 30 <strong>автомобилей </strong>с батарейками. Они будут функционировать в Одинцово и Долгопрудном.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В рамках проекта власти Москвы, который был поддержан на федеральном уровне, готовы внедрять разработки в сфере электротранспорта. Целая улица Москвы станет улицей инноваций под названием Пятницкая. В планах властей изучить всевозможные интеграции электромобилей в сеть большого города.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Интерес к проекту заметен и со стороны энергетиков. Для них это большая возможность для развития нового бизнеса – заправочных станций. Представители отрасли создали свою федеральную программу по развитию зарядной инфраструктуры. В ней подмечено три приоритета. Возможность заправки для частных авто, электроавтобусов и корпоративного транспорта с учетом  географических и климатических особенностей страны, отметил Роман Бердников, член правления ФСК ЕЭС.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Но планы энергетиков требуют поправок в более чем 10 разных нормативных федеральных актов, включая то, чтобы внедрить единую систему билинга за подзарядку. Также требуется внести изменения и в Градостроительный кодекс, чтобы строить парковки не только с розетками, но и дать возможность обычным жильцам оборудовать такие розетки у себя во дворе.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>К примеру, во Франции уже сейчас новые дома проектируются, учитывая заправки для электромобилей, разъяснил Петр Синютин, исполняющий обязанности директора МОЭСК. Но без федеральных властей, которые бы выступили в качестве подталкивающих в развитие таких заправок на федеральных трассах, то электромобили вряд ли будут иметь большой спрос, считают участники рынка.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Ну, так или иначе, электроавтомобили на улицах нашего города, скоро станут постоянным делом. Ну а пока, вы можете <strong>купить авто с пробегом в Санкт-Петербурге</strong> в <strong>автосалоне Адмирал-Моторс</strong>.</p>\n<p> </p>', 1, 13, '2012-11-28 15:35:56', 792, '', '2012-11-28 15:38:45', 792, 0, '0000-00-00 00:00:00', '2012-11-28 15:35:56', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 660, 'автомобиль, купить авто с пробегом в санкт-петербурге, автосалон, адмирал-моторс', 'Электромобили, как новый тип такси будет курсировать в на российских дорогах в скором времени', 1, 360, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(298, 365, 'Opel Mokka получил максимальный рейтинг Euro NCAP', 'opel-mokk-poluchil-maksimalnyj-rejting-euro-ncap', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">Субкомпактный кроссовер </span><strong style="text-align: justify;">Opel </strong><span style="text-align: justify;">Mokka завоевал 5 звезд в рейтинге Euro NCAP.</span></p>', '<p><span style="white-space:pre"> </span><span style="text-align: justify;">По информации в сообщении автоконцерна, еще одна модель Opel получила наивысшую оценку Euro NCAP, европейской авторитетной организации. Сейчас Mokka стоит в одном линейке с моделями </span><span style="text-align: justify; text-decoration: underline;">Ampera</span><span style="text-align: justify;">, </span><span style="text-align: justify; text-decoration: underline;">Corsa</span><span style="text-align: justify;">, </span><span style="text-align: justify; text-decoration: underline;">Astra</span><span style="text-align: justify;">, </span><span style="text-align: justify; text-decoration: underline;">Insignia</span><span style="text-align: justify;">, </span><span style="text-align: justify; text-decoration: underline;">Zafira </span><span style="text-align: justify;">и </span><span style="text-align: justify; text-decoration: underline;">Meriva</span><span style="text-align: justify;">, каждая из которых тоже получила самый высокий рейтинг – 5 звезд – в краш-тестах Euro NCAP.</span></p>\n<p style="text-align: justify;"><span style="white-space:pre"> </span>Opel Mokka показывает впечатляющие результаты во всех 5 категориях безопасности, которые проводит Euro NCAP. Новый субкомпактный кроссовер получил самый большой рейтинг в 100% шкале за эффективность систем активной безопасности, и это делает его единственной из немногих моделей на рынке Европы, удостоенных такой наивысшей оценки в краш-тестах. EuroNCAP в положительную сторону оценила ESP®Plus - электронную систему стабилизации, технологию ограничения скорости и функцию оповещения о непристегнутых ремнях безопасности. Все данные системы входят в базовое оснащение автомобилей модели Mokka.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span> В категории «Защиты взрослых пассажиров» Mokka заработал 96% из 100. Этот великолепный показатель обеспечили головные, фронтальные, боковые подушки и шторки безопасности, плюс преднатяжители с ограничителем нагрузки и двойным действием. Регулируемые подголовники передних сидений выступают в защиту шеи от хлыстовых травм в ситуации наезда на автомобиль сзади. Помимо этого, дополнительной особенностью модели Mokka стал травмобезопасный педальный узел.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>По поводу безопасности детей, Euro NCAP отметила: «<span style="text-decoration: underline;">Opel Mokka</span> завоевал максимальное количество баллов в испытании на динамическую нагрузку с применением манекена трехлетнего ребенка». Добиться столь первоклассных показателей в этой категории удалось за счет детского кресла Opel, имеющего штатные крепления ISOFIX на задних боковых сиденьях и крепления для страховочной лямки.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Максимальное число баллов в категории безопасности пешеходов заработал и бампер модели Mokka. Euro NCAP подметила «обеспечение им отличной степени защиты ног пешеходов».</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>В Mokka показано масса систем активной безопасности, не относящихся даже к обязательным требованиям Euro NCAP. Такие как: фронтальная камера <strong>Opel </strong>Eye 2-го поколения с функцией оповещения о столкновении, системой распознавания дорожных знаков и оповещения о выходе из занимаемой полосы. Помимо всего прочего, система адаптивного головного света AFL+ и камера заднего вида поднимают планку стандартов безопасности автомобилей Opel Mokka еще выше.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span><span style="text-decoration: underline;">Автосалон Адмирал-Моторс</span> располагает 5 автомобилями марки Opel в наличии. Но база поддержанных автомобилей постоянно пополняется, это можно легко отследить на <a href="cars/" target="_self" title="Сайт по продаже автомобилей">сайте по продаже автомобилей</a>, и возможно уже завтра там появится ваш Opel Mokka.</p>\n<p> </p>', 1, 13, '2012-11-29 05:01:45', 792, '', '2012-11-29 05:06:04', 792, 0, '0000-00-00 00:00:00', '2012-11-29 05:01:45', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 659, 'Opel, Ampera, Corsa, Astra, Insignia, Zafira, Meriva, автосалон адмирал-моторс, сайт по продаже автомобилей', 'Opel Mokka теперь обладает пятью звездами от Euro NCAP', 1, 593, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
