INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(356, 423, 'Камеры проследят за нарушениями автомобилистов на переездах', 'kamery-prosledyat-za-narusheniyami-avtomobilistov-na-pereezdakh', '<p><img src="images/stories/fotonews/ 28.12.2012.jpg" border="0" align="left" />Водители начнут получать в январе 2013 года первые штрафы за нарушения ПДД на железнодорожных переездах, которые зафиксированы камерами видеофиксации.</p>', '<p><span style="white-space: pre;"> </span><span style="text-align: justify;">Первые камеры фото и видеофиксации нарушений ПДД на железнодорожных переездах Подмосковья будут функционировать с января 2013 года. Об этом «Известиям» сообщил Юрий Фельде, заместитель министра транспорта Московской области. Пока они действуют в тестовом режиме, и штрафы автовладельцам не выписывают.</span></p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Камеры поставлены на четырех переездах железной дороги Горьковского направления на 19-м км (вблизи Электрогорска), на 67-м км (рядом с Павловским Посадом), а также 33-м и 36-м км (в Ногинском районе). Цена таких камер в количестве четырех штук — порядка 20 млн. рублей. Между тем, экспертов считают, что железнодорожные светофоры сегодня настроены таким образом, что часть нарушений, за которые придется оплачивать штраф, могут оказаться вынужденными.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Камеры фото и видеофиксации на железной дороге устанавливаются, «дабы предупредить происшествия, потому как они отличаются особой тяжестью последствий и высоким общественным резонансом», разъяснил Фельде. Пока власти региона не дали согласие, появятся ли такие камеры в скором времени на других переездах.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Эксперты считают, что нарушения на переездах чаще всего вызваны большими пробками, возникающими из-за того, что люди вынуждены ждать, когда очередной поезд пройдет. Нередко такие ожидания длится не менее часа. При этом практически все переезды  в Подмосковье одинаково плохие. Они разбиты, и по ним лучше продвигаться со скоростью 40 км/ч. При такой скорости и при частых перекрытиях дороги большинство теряют терпение, отмечает Виктор Травин, президент Коллегии правовой защиты автовладельцев.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Камера сможет зарегистрировать такие нарушения, как въезд на переезд при закрывающемся шлагбауме, при загоревшемся красном сигнале светофора, а также въезд в случае, если вслед за переездом образовался затор. Помимо этого, «письма счастья» автомобилисты будут получать за нарушение рядности, парковку ближе 50 м от переезда, обгон ближе 100 м. Штраф за данные нарушения составляет 500 рублей.</p>\n<p style="text-align: justify;"><span style="white-space: pre;"> </span>Нарушения ПДД на переездах происходит довольно часто в наши дни. Но этого можно избежать, необходимо просто быть терпимее или искать другие объездные пути, на которых можно проехать на внедорожниках. А <strong>купить авто с пробегом в Санкт-Петербурге</strong>, а именно внедорожник можно в <strong><a href="cars/" target="_blank" title="автосалон Адмирал-Моторс">автосалоне Адмирал-Моторс</a></strong>.</p>\n<p> </p>', 1, 13, '2012-12-28 04:18:02', 792, '', '2013-01-24 04:11:51', 792, 0, '0000-00-00 00:00:00', '2012-12-28 04:18:02', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 3, 601, 'купить авто с пробегом в санкт петербурге, автосалон адмирал моторс, сайт, интернет, авто с пробегом', 'С января 2013 автовладельцы должны будут оплачивать штрафы за нарушения ПДД на перездах', 1, 313, '{"robots":"","author":"Автосалон Адмирал моторс"}', 0, '', '');
