INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(546, 613, 'Прием заказов на инновационный городской кроссовер Toyota начался', 'priem-zakazov-na-innovacionnyj-gorodskoj-krossover-toyota-nachalsja', '<p style="text-align: justify;"><img class="caption" title="Купить автомобиль" src="images/stories/fotonews/2013/03.04.2013.jpg" alt="Купить автомобиль" align="left" />Вчера уполномоченные партнеры <strong>Toyota</strong> и официальные дилеры на территории РФ стартовали с приема заказов на городской инновационный кроссовер Toyota Venza.</p>', '<p style="text-align: justify;"><br />Как информировали в представительстве марки в России, новый кроссовер будет доступен для покупки на российском рынке в 3-х версиях комплектации: «Элеганс», «Престиж» и «Элеганс Плюс». <a href="cars/"><strong>Купить автомобиль</strong></a> Toyota Venza с 2,7-л. бензиновым двигателем с мощностью 185 л.с. и автоматической 6-ступенчатой трансмиссией можно будет по следующим ценам: </p>\n<ul>\n<li style="text-align: justify;"> «Элеганс» переднеприводная комплектация: 1 570 000 руб.</li>\n<li style="text-align: justify;"> «Престиж» полноприводная комплектация: 1 776 000 руб.</li>\n<li style="text-align: justify;"> «Элеганс Плюс» полноприводная комплектация: 1 671 000 руб.</li>\n</ul>\n<p style="text-align: justify;"><strong>Toyota</strong> Venza укомплектована биксеноновыми фарами с дневными светодиодными ходовыми огнями, обладающими системой автопереключения ближнего света на дальний (Auto High Beam) при встрече впереди приближающегося или идущего встречного автомобиля. Автомобиль оснащен полным приводом с системой распределения активного крутящего момента и высокого клиренса 205 мм.</p>', 1, 13, '2013-04-03 04:16:16', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-04-03 04:16:16', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 411, 'Toyota, Купить автомобиль, куплю авто, купить авто', '2 апреля ознаменовалось стартом авто продаж городского инновационного кроссовера Toyota Venza', 1, 422, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(547, 614, 'О комплектациях и ценах на новый KIA Cerato теперь известно всем', 'o-komplektacijah-i-cenah-na-novyj-kia-cerato-teper-izvestno-vsem', '<p style="text-align: justify;"><img class="caption" title="авто продажи" src="images/stories/fotonews/2013/03.02.2013-1.png" alt="авто продажи " align="left" />Определены цены и комплектации, на <strong>KIA Cerato</strong> на российском автомобильном рынке. Пришло новое поколение самого популярного в мире автомобиля в линейке KIA.</p>', '<p style="text-align: justify;"><br />Как информировали в российском представительстве автомобильного бренда, Cerato – глобальная модель, которая занимает 2-е место по величине <a href="cars/"><strong>авто продажи</strong></a> среди других моделей бренда KIA в мире. На автомобильном рынке России данный показатель уже завоевал популярность и любовь. Это поколение сохранило все главные особенности модели, и стала еще динамичнее и комфортабельнее, а степень его укомплектования заметно повысилась. <br />Снижений размеров пропорций заднего и переднего свесов автомобиля, подштамповки на боковинах, рельефные формы крыльев и характерное ребро внизу крышки багажника – наделяют облик новой модели Cerato современностью и спортивностью. Компоновка обеспечивает максимальный рост простора для пассажиров и водителя при относительно малом увеличении базы колес на 50 мм. <br />KIA Cerato снабжен обновленными бензиновыми двигателями в объемах 1,6 л. (по Gamma линейке) и 2,0 л. (по Nu линейке). Двигатели за счет усилий инженеров стали по весу легче, получили рост в крутящем моменте и мощности, показывая при этом наилучшие показатели по экономии. Современные решения и технологии сделали двигатели более отзывчивыми и «эластичными» в поведении. Двигатель емкостью в 1,6 л. стал сильнее на 4 л.с. <br />Для покупки предлагаются две версии 6-ступенчатой трансмиссии – автоматическая и механическая. Комплектация Prestige с АКПП и версии, идущие за ней оснащаются подрулевыми "лепестками" для переключения передач.<br />Рост цены в сравнении с поколением предыдущим минимальный и равен 20 000 рублям. <strong>Купить автомобиль</strong> стартовой версии Comfort можно по цене 669 900 рублей. Повысилось число комплектаций: новый Сerato предлагается в 4 уровнях оснащения –Luxe, Comfort, Prestige, Premium в семи комплектациях.</p>', 1, 13, '2013-04-03 04:19:28', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-04-03 04:19:28', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 410, 'KIA Cerato, авто продаж, продажа авто, Купить автомобиль', 'Определены цены и комплектации, на KIA Cerato на российском автомобильном рынке', 1, 385, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(548, 615, 'Citroen вложил 120 млн. евро в производство седана С4 в России', 'citroen-vlozhil-120-mln-evro-v-proizvodstvo-sedana-s4-v-rossii', '<p style="text-align: justify;"><img class="caption" title="Продажи авто" src="images/stories/fotonews/2013/04.04.2013.jpg" alt="Продажи авто" align="left" />Французский автомобильный бренд <strong>Citroen</strong> вложил в запуск производства серийного С4 седана в Калуге на заводе свыше 120 млн. евро.</p>', '<p style="text-align: justify;"><br />Седан С4 стал самой первой моделью Citroen в РФ, производящего в режиме полного цикла. Автомобиль разработан специально для России учитывая все климатические и дорожные условия.<br />Как информирует "Автостат", существенная часть комплектующих для седана <strong>Citroen</strong> С4 изготовляется в России: на момент старта локализация производства показала 34%. Завод действует с 18-ю поставщиками из России, большинство находится в Калуге, снабжая поставкой в 70% всех материалов для сборки седана Citroen С4.<br />«В последние пять лет с создания в России филиала Citroen, мы каждый год увеличивали <strong>авто продажи</strong> и часть рынка. Сегмент «С» - важнейший для России, в прошлом году он занял до 16% рынка рост составил 11%. Сейчас мы выводим в сегмент «С» новый Citroen С4. С помощью этого автомобиля мы планируем подняться на новый уровень, усилив <a href="cars/"><strong>продажи авто</strong></a> на 30% в сравнении с минувшим годом. Мы убеждены, что до конца текущего года нам все-таки удастся продать более 13 тыс. автомобилей», – сообщает директор Citroen в РФ Жан-Луи Шамла.<br />Уже стала известна стоимость на новый седан Citroen С4. Стартовая стоимость седана с двигателем VTi 115 и механической трансмиссией составляет 579 тыс. рублей. Авто предлагается с 3 комплектациями: Tendance, Dynamique и Exclusive.<br /><strong>Citroen</strong> С4 – это седан с двигателем VTi 120 сочетается c автоматической трансмиссией и представлен в четырех комплектациях: Dynamique, Exclusive, Tendance и Exclusive + , с начальной ценой 647 тыс. рублей.<br />И авто с двигателем THP 150 c 6-ти ступенчатой АКПП в 3 уровнях комплектации: Exclusive, Tendance и Exclusive + и начальной ценой в 763 тыс. рублей.</p>', 1, 13, '2013-04-04 03:30:31', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-04-04 03:30:31', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 409, 'Citroen, авто продажи, продажи авто', 'Французский автомобильный бренд Citroen вложил в запуск производства серийного С4 седана в Калуге на заводе свыше 120 млн. евро', 1, 398, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(549, 616, 'MINI показал самый необычный коммерческий автомобиль', 'mini-pokazal-samyj-neobychnyj-kommercheskij-avtomobil', '<p style="text-align: justify;"><strong><img class="caption" title="Авто продажи" src="images/stories/fotonews/2013/04.04.2013-1.jpg" alt="Авто продажи" align="left" />MINI</strong> Clubvan — первый в мире компактный фургон премиум-класса.</p>', '<p style="text-align: justify;"><br />Коммерческий универсальный автомобиль MINI Clubvan - наследник модели Morris Mini Van, производившейся с 1960 - 1986 годы. Как информировали в представительстве марки в России, MINI Clubman обладает следующими габаритами: ширина 1683 мм, длина 3961 мм, высота 1426 мм и колесной базой 2547 мм. Применяется также Clubdoor - специальная правая дверь, обеспечивающая комфортный и быстрый доступ к грузам, за передними сиденьями.<br />Длина отсека для груза равна 115 см, а ширина 102 см. Между передними сиденьями и грузовым отсеком есть разделительная перегородка, прикрепленная к кузову авто. В момент экстренного торможения эта перегородка грузу не даст удариться о пассажирские сиденья. Она разрешает загружать отсек прямо до крыши и с максимальной эффективностью применять все 860 л. полезного объема. Наибольшая грузоподъемность MINI Clubvan равна 500 кг.<br />За динамику, разрешающую <strong>MINI</strong> Clubvan осуществлять доставку своих грузов настолько быстро, насколько это возможно. Линейка автомобилей состоит из двух мощных бензиновых четырехцилиндровых двигателей емкостью 1,6 л. Двигатель MINI One Clubvan способен развивать мощность до 72 кВт 98 л. с., а модель MINI Cooper Clubvan оснащена агрегатом на 90 кВт - 122 л. с.. В комплектации «Стандарт» мощность передается на колеса передние путем 6-ступенчатой механической коробки передач, но в числе опции каждая модификация может быть укомплектована аналогичной АКПП.<br />Дата начала <a href="cars/"><strong>авто продажи</strong></a> автомобиля MINI Clubvan пока неизвестна.</p>', 1, 13, '2013-04-04 03:33:44', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-04-04 03:33:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 408, 'MINI, авто продажи, автомобили с пробегом, автомобиль с пробегом', 'MINI Clubvan — первый в мире компактный фургон премиум-класса', 1, 348, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(550, 617, 'По результатам 2012 года наиболее загруженным городом названа Москва', 'po-rezultatam-2012-goda-naibolee-zagruzhennym-gorodom-nazvan-moskva', '<p style="text-align: justify;"><img class="caption" title="купить авто" src="images/stories/fotonews/2013/05.04.2013.jpg" alt="купить авто" align="left" />Специалисты назвали города с наиболее длинными автомобильными пробками в мире.</p>', '<p style="text-align: justify;"><br />Данный рейтинг представила компания-производитель gps-навигаторов TomTom. Эксперты протестировали ситуацию с загруженностью на дорогах в 161 городе. Учитывались только те города, в которых население более 800 тыс. человек, а из маленьких лишь те, которые играют важную транспортную роль в тои или ином регионе. <br />Как информирует auto.mail.ru, информация о загруженности эксперты TomTom получали с навигаторов своих потребителей. В расчет при исследовании брались поездки по магистралям скоростным, и по локальной проезжей части. Отличие между временем среднего периода наименьшей загруженности и в часы пик отображалось в процентах.<br />По информации специалистов, передвижение по столице России занимает не больше 66% времени в сравнении со средним показателем, в часы пик утром эта величина равна 106%, а в часы пик вечером —136%. В часы пик показатель загруженности задержки в пути равен 74 мин. на каждый час. Средние поездки ежедневные отнимают 127 часов в год у москвичей.<br />Наибольшая длина пробок в Москве была в 2012 году, зарегистрирована 29 ноября. Самым загруженным днем недели оказалась среда, а самым свободным в будние дни дороги были по утрам в пятницу и в понедельник вечером.<br />2-е место по загруженности автомобильных дорог досталось Стамбулу с показателем в 55%, а 3-е Варшаве 42%. В десятку таких городов вошли по итогам 2012 года Марсель (40%), Лос-Анджелес (33%), Палермо (39%), Сидней (33%), Париж (33%), Штутгарт (33%) и Рим (33%).<br />Ситуация с пробками конечно напряженная, но это не причина не <a href="cars/"><strong>купить авто</strong></a>.</p>', 1, 13, '2013-04-05 04:54:44', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-04-05 04:54:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 407, 'купить авто, куплю авто, купить авто в спб, куплю авто в спб', 'Наибольшая длина пробок за 2012 год была зарегистрирована в Москве', 1, 265, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(551, 618, 'Шины Nokian Tyres заполучили 1/4 шинного рынка РФ', 'shiny-nokian-tyres-zapoluchili-1-4-shinnogo-rynka-rf', '<p style="text-align: justify;"><img class="caption" title="авто продажи" src="images/stories/fotonews/2013/05.04.2013-1.jpg" alt="авто продажи" align="left" />Компания Nokian Tyres по итогам прошлого года больше подкрепила свое место на рынке шин России.</p>', '<p style="text-align: justify;"><br />Как информировали в представительстве компании в России, часть рынка Nokian Tyres в классах А + В выросла с 22% в 2011 году и до 25% в минувшем году. Каждая 4-я шина сегмента А + В, реализованная в России в прошлом году, была изготовлена компанией.<br />Позиции на вторичном рынке автомобильных шин оцениваются, без учета шин поступающих на первичную комплектацию, <a href="cars/"><strong>авто продажи</strong></a> в России дистрибьюторам в единицах в сегменте А + В, средней и премиум ценовой сегмент. По итогам 2012 года, в первый раз, Nokian Tyres стала на рынке летних шин лидером в сегменте А + В, реализовав летних шин больше, в отличие от конкурентов.<br />В прошлом году в РФ в единичном выражении было реализовано свыше 40 млн. шин для легковушек, легких грузовиков и внедорожников, а в денежном эквиваленте – рынок стал выше отметки в 2 млрд. евро. В Nokian Tyres делают прогноз, что шинный рынок для легковушек и грузовиков в этом году не покажет большого роста, и суммарный объем рынка будет аналогичен 2012 году, около 41 – 42 млн. шин.<br />При этом общий объем <strong>продаж авто</strong> шин сегмента С чуть понизится, тем самым дав больше возможностей для совершенствования сегмента В. Рынок премиум-шин сегмента А останется прежним. Nokian Tyres прогнозирует, шинный рынок в 2018 году будет около 60 млн. шин в год.<br />«В этом году мы убеждены больше укрепить позиции на рынке шин в России за счет новинок в зимних и летних шинах и усовершенствованию сети дистрибьюции. Настоящий год стал просто беспрецедентным в истории шинного концерна Nokian Tyres. Компания сразу обновила три шины в зимней линейке шин Nokian Hakkapeliitta и показала новую летнюю шину высокоскоростную Nokian Hakka Black», – дал комментарий гендиректор компании в России Nokian Tyres, Андрей Пантюхов.</p>', 1, 13, '2013-04-05 04:58:26', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-04-05 04:58:26', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 406, 'авто продажи, продажи авто, продажа подержанных автомобилей в санкт петербурге', 'Часть рынка Nokian Tyres в классах А + В выросла с 22% в 2011 году и до 25% в минувшем году', 1, 287, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
