INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(635, 702, 'Стоимость новой Lada Kalina начинается с 324000 рублей', 'stoimost-novoj-lada-kalina-nachinaetsja-s-324000-rublej', '<p style="text-align: justify;">Теперь у российских автовладельцев есть возможность узнать о цене новой <strong>Lada Kalina</strong>.</p>', '<p style="text-align: justify;">Сотрудники "АвтоВАЗа" рассказали, что хэтчбек в "стандарт" исполнении, вместе с электроусилителем руля и подушкой безопасностью, будет стоить 324000 рублей.<br />Для справки, новое поколение автомобилей Lada Kalina будет выпускаться в 2 типах кузовов. <a href="cars/"><strong>Купить авто</strong></a> в кузове универсал с исполнением "стандарт" можно будет за 331000 рублей. <br />Kalina в дорогой "люкс" комплектации оснащается автоматической трансмиссией и оценивается в 445000 рублей - хэтчбек и 452000 рублей - универсал. Об опциях входящих в люксовую версию вслед за сменой поколений - пока нет данных.<br />Сейчас базовая стоимость люкс - комплектации равна - 393400 для хэтчбека, а для универсала - 399000 рублей. Стандартное оборудование хэтчбека:</p>\n<ul>\n<li style="text-align: justify;">подушка безопасности переднего пассажира и водителя,</li>\n<li style="text-align: justify;">система ABS,</li>\n<li style="text-align: justify;">противотуманки,</li>\n<li style="text-align: justify;">сочетание приборов с опцией бортового компьютера,</li>\n<li style="text-align: justify;">индикация температуры воздуха снаружи,</li>\n<li style="text-align: justify;">электронные часы,</li>\n<li style="text-align: justify;">управление рулем на электроусилителе,</li>\n<li style="text-align: justify;">электростеклоподъемники всех дверей,</li>\n<li style="text-align: justify;">обогрев передних сидений,</li>\n<li style="text-align: justify;">аудиосистема с 2 динамиками,</li>\n<li style="text-align: justify;">климат-система.</li>\n</ul>\n<p style="text-align: justify;">Экономный вариант новой Калины 1-ого поколения еще есть в авто продаже по стоимости от 311700 рублей. Но там отсутствует подушка безопасности и усилитель руля.<br />Для справки, в ближайшую субботу пройдет первый тест-драйв авто.</p>\n<p><img class="caption" style="margin: 10px auto; display: block;" title="Купить авто" src="images/stories/fotonews/2013/17.05.2013-1.jpg" alt="Купить авто" width="500" height="375" /></p>', 1, 13, '2013-05-17 07:21:31', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-17 07:21:31', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 322, 'Lada Kalina, Купить авто, купить авто в спб, куплю авто в спб', 'Новая Lada Kalina доступна от  324000 рублей', 1, 508, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(636, 703, 'Продажи роскошных автомобилей в РФ растут даже на фоне общего падение рынка', 'prodazhi-roskoshnyh-avtomobilej-v-rf-rastut-dazhe-na-fone-obshhego-padenie-rynka', '<p style="text-align: justify;">По результатам апреля авторынок России снизился на 8%, как сообщалось ранее в новостях.</p>', '<p style="text-align: justify;">Но падение <a href="cars/"><strong>авто продаж</strong></a> коснулось только бюджетного сегмента, потому как с дорогими автомобилями дела обстоят по-другому. Премиум-марки показали увеличение авто продаж на 10% в среднем. <br /><strong>Продажи автомобиля</strong> Lada Kalina снизились, аналогично и Lada Priora. Автомобили Renault Logan, Opel Astra, Daewoo Nexia тоже показали упадок показателей. <br />Но, тем не менее, премиум-бренды автопроизводителей за апрель месяц, показали постоянный рост. К примеру, авто продажи новинок Mercedes на территории России подросли на 18%, а реализация автомобилей BMW увеличилась на 7%, Audi на 11%, Lexus повысил продажи на 14%, а Land Rover на 9%.<br />"Остановка рынка касается лишь бюджетного сегмента, так как он более подвержен этому. А люксовые бренды более стабильны в этом плане, как и покупательская способность потребителей приобретающих BMW и Mercedes. В экономном сегменте клиент чувствительнее к разному роду колебаниям", - полагает Владимир Беспалов, аналитик ВТБ-Капитал.<br />Однако аналитик-эксперт БКС Игорь Краевский, дал свою точку зрения. Он считает что мало обеспеченные люди покупают экономные авто за автокредит. "Хотя сейчас из-за нестабильной макроэкономической ситуации в России многие брать кредиты сроком больше года просто опасаются, соответственно это влияет на продажи авто", - комментирует аналитик.<br />Вообщем и в целом эксперты уверены, лето 2013 года тоже станет периодом упадка <strong>продаж авто</strong>.</p>\n<p><img style="margin: 10px auto; display: block;" title="продажи авто" src="images/stories/fotonews/2013/18.05.2013.jpg" alt="продажи авто" width="500" height="375" /></p>', 1, 13, '2013-05-18 06:55:21', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-18 06:55:21', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 321, 'авто продажи, Продажи автомобиля, продажи авто', 'Премиум-марки показали увеличение авто продаж на 10% в среднем', 1, 555, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(637, 704, 'Впервые подушку безопасности в центре поставили на серийный автомобиль', 'vpervye-podushku-bezopasnosti-v-centre-postavili-na-serijnyj-avtomobil', '<p style="text-align: justify;">В компании General Motors рассказали о нововведении в использовании подушек безопасности, а именно об эксперименте на новом кроссовере от Chevrolet Traverse.</p>', '<p style="text-align: justify;">Авто укомплектуют центральной подушкой, она по плану разработчиков, должна понизить тяжесть повреждений, полученных при боковых автостолкновениях.<br />Создатели новой системы в защиту утверждают, что ремни безопасности не способны обеспечивать достаточную степень защиты пассажиров сидящих спереди при боковых ударах в ДТП. Например, они не в состоянии защитить пассажира и водителя от вероятности столкнуться головами, в случае удара в переднюю дверь. Потому данную возможность обязана исключить центральная подушка безопасности.<br />Подушка установлена в боковину сиденья водителя и в момент столкновения раскрывается достаточно хорошо аналогично шторкам безопасности. Но эта известная шторка, как бы делит переднюю часть автосалона пополам, тем самым изолируя водителя и пассажира друг от друга.<br />Для справки, данная система была разработана еще в 2011 году. Но до серийного использования центральный эйр-бэг (подушка безопасности) доработали только сейчас. Премьера состоится на кроссовере Chevrolet Traverse. Напомним, что Chevrolet Traverse комплектуется 3,6 л. V6 с мощностью 288 л.с и крутящим моментом 366 Нм, а также 6-ступенчатым автоматической коробкой передач и дополнительной опцией – полноприводной системой. <br />Данных о начале <a href="cars/"><strong>продажи авто</strong></a> с центральной подушкой безопасностью пока нет.</p>\n<p><img style="margin: 10px auto; display: block;" title="продажи авто" src="images/stories/fotonews/2013/18.05.2013-1.jpg" alt="продажи авто" width="500" height="375" /></p>', 1, 13, '2013-05-18 06:59:00', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-18 06:59:00', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 320, 'продажи авто, автомобили с пробегом в спб, продажа подержанных автомобилей в санкт петербурге', 'На новом кроссовере от Chevrolet Traverse провели эксперимент с центральной подушкой безопаностью', 1, 449, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(638, 705, 'Новый S-класс Mercedes-Benz отличает животных от людей', 'novyj-s-klass-mercedes-benz-otlichaet-zhivotnyh-ot-ljudej', '<p style="text-align: justify;">Новый <strong>Mercedes-Benz</strong> класса S получил полный комплекс электронных "штучек", объединенных в единую систему.</p>', '<p style="text-align: justify;">В компании называют этот класс Intelligent Drive и горды его способностями и технологическими решениями.<br />Дабы обеспечить S-классу почти автономное существование во многих дорожных ситуациях, инженеры в Штутгарте создали полный набор датчиков, камер и радаров. А именно на седане впервые использована "многоцелевая стереокамера". Ее местонахождение - лобовое стекло в области зеркала заднего вида. Стереокамера может наполнять "мозг" авто стереокартинками о ситуации происходящих на дороге. Стереовидение действует на дальности - 50 м, однако общая дальность видения камеры – в десять раз больше. Стереокамера ассистирует целый комплекс многорежимных радаров и радиолокационных датчиков. <br />Использование стереокамеры в автомобиле дело возможность выводить вспомогательные системы на абсолютно новый уровень. Адаптивный Distronic Plus (круиз-контроль) теперь сочетается с ассистентом управления руля и системой тормозов, который способен удерживать не только заданное расстояние до впереди движущейся машины, но и без посторонней помощи следовать всем изгибам автотрассы. Система работает на скорости до 200 км/ч. При желании можно ее отключить, достаточно положить руки на руль: тогда автомобиль поймет знак водителя взять управление в свои руки. <br />Система слежения за слепыми зонами доработана и научилась предотвращать ситуации такие как: отказаться от выполнения действия водителя, в случае если он задумал небезопасно обогнать другое авто. Если обгон совершается в зоне, где запрещен, то электроника даст знаки водителю вернуться к порядку, сверив данные с системой распознавания знаков на дороге.<br />Система Cross-Traffic Assist понижает тяжесть аварий происходящих на перекрестках, да и вовсе помогает избежать их. Она способна выследить пешеходов, транспортные средства, которые движутся поперечным ходом, и даже тормозить при необходимости до того как водитель спохватится в ситуации.<br />Естественно в авто не обошлось без системы автопарковщика. Теперь он может автоматически припарковать авто на подходящем для этого месте, а также выехать с автопарковки при надобности. <br />Система ночного видения теперь может распознавать помимо пешеходов животных на дальности до 160 м. Она умеет предупреждать пешеходов о возникшей опасности - миганием фар. <br />Одно из нововведенных инноваций Mercedes-Benz S-класса - отказ от применения галогенных ламп. Седан опционально укомплектовывается светодиодным освещением – в части интерьера, головной оптики. Такое освещение способно понизить потребление электроэнергии, и автомобиль научить самолично регулировать световой пучок, дабы не ослепить встречных авто водителей. Задние фары способны менять интенсивность действия за счет времени суток, дорожной ситуации и погоды.<br />Да электроники в новом Mercedes-Benz навалом, только пока нет данных о начале <a href="cars/*"><strong>авто продажи</strong></a> в России и в мире.</p>\n<p><img style="margin: 10px auto; display: block;" title="авто продажи" src="images/stories/fotonews/2013/19.05.2013.jpg" alt="авто продажи" width="500" height="375" /></p>', 1, 13, '2013-05-18 08:06:21', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-18 08:06:21', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 319, 'Mercedes-Benz, авто продажи, автокредит без первоначального взноса, продажа автомобилей в санкт-петербурге', 'Новый Mercedes-Benz класса S получил полный комплекс электронных штучек, объединенных в единую систему', 1, 473, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(639, 706, 'Мотоциклистам теперь разрешат ездить в Москве по выделенным полосам', '2013-05-18-08-10-36', '<p style="text-align: justify;">В скорое время в столице России стартует эксперимент.</p>', '<p style="text-align: justify;">В нем мотоциклистам дадут разрешение ездить по выделенным полосам, предназначенным для общественного транспорта. Данный эксперимент состоится на одной из специальных полос города. Его итоги власти примут, и закрепят как инициативу в ПДД.<br />Сегодня выезд на выделенные полосы разрешен и легковым такси. Но московские власти теперь решили ввести запрет на езду для них по спецполосам повесив знак "кирпич". Штраф в Москве и в Питере, за выезд на специальную полосу равен трем тысячам рублей. А в других городах России за это нарушение налагается штраф в размере 1,5 тысячи рублей. Исключение лишь бывает в праздничные и выходные дни.<br />На будущей неделе выделенные полосы станут доступны и для мотоциклистов. Как информирует "Российская газета", мотоциклы будут ездить легально, как и скутеры и велосипедисты. Однако не по всем полосам, а лишь на одной из автомагистралей города. <br />Эксперимент даст понять властям насколько двухколесное транспортное средство затрудняло движение общественного маршрутного транспорта или же наоборот. Если результаты будут позитивные, то и мотоциклы с мопедами получат право ездить по выделенкам.<br />Для справки, статистика от председателя Павла Фролова, Союза мотоциклистов России за 2012 год в РФ насчитывалось свыше 1,15 млн. водителей с категории "А". И в 2012 году насчитано 10160 ДТП, в которых участвовали мотоциклисты.<br />Его мнение таково - главная проблема стоит в недостаточной подготовке и обучению водителей категории «А». Фролов также предложил разработать межведомственную группу по созданию требований и стандартов подготовки мотоциклистов. <br />Если вы решили <a href="cars/"><strong>купить авто</strong></a> или мотоцикл то вам обязательно нужно сдать на водительские права.</p>\n<p><img style="margin: 10px auto; display: block;" title="купить авто" src="images/stories/fotonews/2013/19.05.2013-1.jpg" alt="купить авто" width="500" height="375" /></p>', 1, 13, '2013-05-18 08:08:49', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-18 08:08:49', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 318, 'купить авто, куплю авто, купить авто', 'В столице России стартует эксперимент - мотоциклистам дадут разрешение ездить по выделенным полосам', 1, 518, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(640, 707, 'Новинка от Infiniti выйдет за рамки классов', 'novinka-ot-infiniti-vyjdet-za-ramki-klassov', '<p style="text-align: justify;">Компания <strong>Infiniti</strong> собирается выпустить на авторынок через пару лет новинку - компактную модель Infiniti Q30.</p>', '<p style="text-align: justify;">Она по планам автопроизводителей, станет основанием нового класса премиум-авто, совмещая в себе характер роскошного хэтчбека и премиум-кроссовера.</p>\n<p style="text-align: justify;">Японский производитель люксовых авто ищет в данный момент новые и не занятые нишы для будущих своих моделей. По мнению Энди Палмера, вице-президента концерна Nissan, будущая новая модель, под именованием Q30, вполне может конкурировать вместе с Audi Q3 и A3. По заявлению руководства компании, сама идея организации таких классов замечена у автопроизводителя Nissan и взята из-за не малого успеха компактных кроссоверов Qashqai и Juke, но конечно копировать идеи в компании Infiniti не собираются.</p>\n<p style="text-align: justify;">В итоге, будущая новинка Q30 не будет премиум-клоном кроссовера Qashqai. Вице-президент Nissan сделал заявление, что бренд должен привлечь потенциальных покупателей своим эмоциональным дизайном и оригинальным стилем.<br />Смотря на отзывы покупателей концепт-кара Etherea, являющегося прототипом серийного автомобиля Q30, новая модель будет привлекательной для многих поклонников. И до поступления ее в продажу можно <a href="cars/"><strong>купить авто</strong></a> модели Etherea . Помимо этого, она получит и хорошие ездовые качества, потому как создается на базе переднеприводной платформы от Mercedes-Benz. В результате, новинка Infiniti Q30 может стать "родственницей" автомобилей Mercedes классов A и B.</p>\n<p><img class="caption" style="margin: 10px auto; display: block;" title="купить авто" src="images/stories/fotonews/2013/20.05.2013.jpg" alt="купить авто" width="500" height="375" /></p>', 1, 13, '2013-05-20 03:35:00', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-20 03:35:00', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 317, 'Infiniti, купить авто, купить автомобиль, покупка авто с пробегом', 'Компания Infiniti собирается выпустить на авторынок через пару лет новинку - компактную модель Infiniti Q30', 1, 515, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(641, 708, 'За гибридной моделью Volvo V60 выстраиваются длинные очереди', 'za-gibridnoj-modelju-volvo-v60-vystraivajutsja-dlinnye-ocheredi', '<p style="text-align: justify;">У автомобильного бренда <strong>Volvo</strong> есть повод для большой гордости.</p>', '<p style="text-align: justify;">Скандинавские автопроизводители сделали объявление о том, что увеличивают объемы производства почти в 2 раза, так как он имеет огромную популярность. Возможно все из-за впечатляющего расхода топлива.<br />Гибридная модель Volvo V60 производится на заводе в Торсланде. Для удовлетворения на удивление высокого спроса, шведы хотят повысить выпуск модели до 282 штук вместо 150 в неделю. В итоге, за этот год Volvo выпустит порядка 7 600 гибридов, а в 2014 с конвейера по предположениям сойдут уже 10000 гибридных автомобилей V60.<br />Самые активные покупатели, по информации Volvo из Голландии, Бельгии и Италии. Для справки, первая 1000 универсалов почти сразу после анонсирования, тогда когда еще автомобили не поступили в дилерские центры. Ранее в компании Volvo рассчитывали объем <a href="cars/"><strong>авто продаж</strong></a> за 2013 год в объеме 5000 дизельных гибридов.<br />Универсал комплектуется дизель-электрической установкой, с комбинированным расходом топлива - 1,8 л. /100 км пути. Общая отдача выше 280 лошадиных сил и 640 Нм крутящего момента, и это разрешает ускоряться до 100 км/ч за 6,9 сек. До 50 км. пути гибридное авто способно проехать на одной только электротяге, а полный объем бака увеличивает расстояние до 1200 км. Подзарядка аккумуляторов от обычной бытовой электросети составит 4,5 часа.</p>\n<p><img style="margin: 10px auto; display: block;" title="авто продажи" src="images/stories/fotonews/2013/20.05.2013-1.jpg" alt="авто продажи" width="500" height="374" /></p>', 1, 13, '2013-05-20 03:38:40', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-20 03:38:40', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 316, 'Volvo, авто продажи, продажа автомобиля, автомобили с пробегом', 'Гибридная модель Volvo V60 имеет огромную популярность', 1, 423, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(642, 709, 'На территории России стартуют авто продажи новинки Seat Leon', 'na-territorii-rossii-startujut-avto-prodazhi-novinki-seat-leon', '<p style="text-align: justify;">Компания <strong>Seat</strong> с начала лета запускает <a href="cars/"><strong>авто продажи</strong></a> в России 3-го поколения автомобилей модели Leon.</p>', '<p style="text-align: justify;">Авто будет доступно в 4 исполнениях, стоимость на которые начинается от 639990 рублей. Версия базовая оснащена бензиновым движком объема 1,2 л. и мощностью 86 лошадиных сил, а также 6-ю подушками безопасности.<br />Технические характеристики нового Seat Leon: бензиновые двигатели TSI:</p>\n<ul>\n<li style="text-align: justify;">1,2 л. 105 лошадиных сил;</li>\n<li style="text-align: justify;">1,4 л. 140 лошадиных сил;</li>\n<li style="text-align: justify;">1,8 л. 180 лошадиных сил.</li>\n</ul>\n<p style="text-align: justify;">На момент старта авто продаж на автомобильном рынок России новинки от Seat, модель будет выполнена в 4-х комплектаций и в пятидверном варианте кузова. <strong>Купить автомобиль</strong> с двигателем 1,2 TSI и с мощностью 86 лошадиных сил и 5-ступенчатой механической КПП можно будет за 639990 рублей, а с движком 1,2 TSI и с мощностью 105 лошадиных сил и трансмиссией DSG - от 796990 рублей.<br />В комплектации «стандарт» новый Leon располагает:</p>\n<ul>\n<li style="text-align: justify;">шестью подушками безопасности,</li>\n<li style="text-align: justify;">антиблокировочной ABS-системой;</li>\n<li style="text-align: justify;">антипробуксовочной ASR-системой;</li>\n<li style="text-align: justify;">ESC - системой курсовой устойчивости;</li>\n<li style="text-align: justify;">передними электростеклоподъемниками;</li>\n<li style="text-align: justify;">бортовым компьютером;</li>\n<li style="text-align: justify;">боковыми зеркалами на электроприводе.</li>\n</ul>\n<p style="text-align: justify;">В качестве опций доступны следующие:</p>\n<ul>\n<li style="text-align: justify;">подогрев передних сидений;</li>\n<li style="text-align: justify;">мультимедийная система;</li>\n<li style="text-align: justify;">климат-контроль;</li>\n<li style="text-align: justify;">сенсорный дисплей и Bluetooth-интерфейс;</li>\n<li style="text-align: justify;">OPS - парковочная оптическая система;</li>\n<li style="text-align: justify;">передние светодиодные фары.</li>\n</ul>\n<p style="text-align: justify;">По сообщению руководства марки Seat в РФ Романа Кузьмина, новинка сочетает в себе образцовое качество реализации, превосходный дизайн, последние технологии по отличной цене.  </p>\n<p><img style="margin: 10px auto; display: block;" title="авто продажи" src="images/stories/fotonews/2013/21.05.2013.jpg" alt="авто продажи" width="500" height="375" /></p>', 1, 13, '2013-05-21 03:51:51', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-21 03:51:51', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 315, 'Seat, авто продажи, Купить автомобиль', 'Компания Seat с начала лета запускает авто продажи в России 3-го поколения автомобилей модели Leon по цене от 639990 рублей', 1, 368, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(643, 710, 'Дешевая новинка кроссовер Hyundai в продажу поступит только в 2015 году', 'deshevaja-novinka-krossover-hyundai-v-prodazhu-postupit-tolko-v-2015-godu', '<p style="text-align: justify;">Компания <strong>Hyundai</strong> (Южная Корея) уже не раз делала объявление о том, что скоро выпустит в <strong>авто продажу</strong> новый дешевый кроссовер в течение пары лет.</p>', '<p style="text-align: justify;">Плюс к этому корейская автомобильная марка разрабатывает и экономный минивэн. Оба автомобиля будут разрабатываться на одной платформе и в <a href="cars/"><strong>авто продаже</strong></a> появятся в 2015 году. <br />Есть информация о том, что бюджетные авто оснастят и бензиновыми и дизельными двигателями. Разработка движков ведется при участии инженеров исследовательского индийского центра Хайдарабад. Поначалу автомобили появятся в Южной Корее и Индии.<br />Неофициальные данные повествуют, что внешний облик нового кроссовера слегка будет напоминать Hyundai Curd, представленный в Женеве два года назад. Автопроизводитель Hyundai планирует в дальнейшем расширить свое производство в России. <br />"У нас имеется запас мощности на заводе под Санкт-Петербургом, а именно 200000 единиц в год – сегодняшний потенциал, но есть вероятность расширить до 300000 автомобилей. По поводу новой модели, мы сейчас думаемо том, что бы могли предложить российским клиентам. Например - популярный Hyundai Solaris. ", - прокомментировал Ку Йон-Ги, глава руководства Hyundai в России.</p>\n<p><img style="margin: 10px auto; display: block;" title="авто продажи" src="images/stories/fotonews/2013/21.05.2013-1.jpg" alt="авто продажи" width="500" height="375" /></p>', 1, 13, '2013-05-21 04:08:29', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-21 04:08:29', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 314, 'Hyundai, авто продаже, сайт продажи автомобилей, сайты продажи автомобилей', 'Компания Hyundai скоро выпустит в авто продажу новый дешевый кроссовер', 1, 963, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(644, 711, 'Toyota и BMW разработают на платформе GT86 новый спорткар', 'toyota-i-bmw-razrabotajut-na-platforme-gt86-novyj-sportkar', '<p style="text-align: justify;">Новый спортивный автомобиль, созданный совместными силами <strong>Toyota</strong> и <strong>BMW</strong>, возможно будет основан на GT86-платформе.</p>', '<p style="text-align: justify;">Спорткар будет больше, его оснастят другими движками и системы полного привода в качестве выбора. Планируется дебют прототипа новинки на автосалоне в Токио. <br />Для справки, в компании Toyota уже были озвучены подобные планы по будущему расширению линейки GT86. Тецуя Тада, главный инженер по этому проекту, уже делал заявление о сотрудничестве с BMW, но как сообщал портал Motor Trend, он и будет возглавлять работу над проектом партнерского баварско-японского спортавто. По утверждению источника портала, это напрямую связано с намерениями применить уже отлично зарекомендовавшей платформы GT86, ее главный инженер Тада знает ее как таблицу умножения. <br />Основной задачей группы инженеров станет помимо роста габаритов спорткара, дабы в BMW смогли дистанцировать новинку от предшественника Z4 – адаптация платформы к более мощным моторам. По неофициальным данным, новинка получит вместе с 4-цилиндровый баварским турбодвигателем, ряд других моторов, включая и гибридную установку. Команда инженеров рассчитывает электромотор заставить вращать колеса спереди.<br />Для справки, информировалось, что разработка японских и баварских инженеров будет идеологическим наследником модели Toyota Supra и будет комплектоваться 3,5 л. двигателем V6, но позже инженеры склонились к выбору более экономичных и компактных четырехцилиндровых силовых агрегатов.<br />Планируется, что концепция прототипа совместной новинки от BMW и Toyota покажут миру на автовыставке в ноябре в Токио. А когда именно начнутся <a href="cars/"><strong>авто продажи</strong></a> и будет ли новинка серийной - не уточняется.</p>\n<p><img class="caption" style="margin: 10px auto; display: block;" title="авто продажи" src="images/stories/fotonews/2013/22.05.2013.jpg" alt="авто продажи" width="500" height="375" /></p>', 1, 13, '2013-05-22 03:26:03', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-22 03:26:03', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 313, 'Toyota, BMW, авто продажи, машины с пробегом в санкт-петербурге', 'Новый спортивный автомобиль создан совместными силами Toyota и BMW', 1, 395, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(645, 712, 'Популярнейшим авто в Европе до сих пор остается VW Golf', 'populjarnejshim-avto-v-evrope-do-sih-por-ostaetsja-vw-golf', '<p style="text-align: justify;">Уже не первый месяц европейцы отдают свое предпочтение одному и тому же автомобилю - <strong>Volkswagen Golf</strong>.</p>', '<p style="text-align: justify;">Бестселлер из Германии снова стал самым реализуемым в ЕС. На 2-м месте данного рейтинга находится авто Renault Clio. А 3-ю строчку занимает Volkswagen Polo.<br />По информации компании JATO Dynamics, автомобиль VW Golf за прошлый месяц 2013 года купили 43175 европейца и рост продаж составил 17,9%. Renault Clio с <a href="cars/"><strong>авто продажей</strong></a> за аналогичный период с ростом 29% - 26352 авто. И Volkswagen Polo - продажи которого приравнялись к 25460 единицам, т.е. рост показал 6,5%, информирует "Автостат".<br />Далее в рейтинге расположились Peugeot 208, его приобрели 24007 покупателей из Европы. И в конце пятерки лидеров находится Ford Fiesta, с тиражом 23564 штук проданных авто.<br />В ТОП-10 также вошли в порядке убывания Opel Corsa, Ford Focus, BMW 3 серии, Audi A3/S3/RS3 и Opel Astra.<br />Для справки, обновленная линейка Audi A3 по результатам апреля 2013 года стала лидером по увеличению продаж. Как комментируют в Audi AG, запуск A3 Sportback и Q3 нового поколения прошел успешно и разрешил автомобильному бренду войти в 5-ку лидеров авторынка Европы.</p>\n<p><img style="margin: 10px auto; display: block;" title="авто продажи" src="images/stories/fotonews/2013/22.05.2013-1.jpg" alt="авто продажи" width="500" height="375" /></p>', 1, 13, '2013-05-22 03:29:01', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-22 03:29:01', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 312, 'Volkswagen Golf, авто продажи, продажа автомобилей с пробегом в санкт-петербурге, автомобили с пробегом в спб', 'Рост продаж Volkswagen Golf вырос на  17,9%', 1, 521, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(646, 713, 'Бренд Renault окрасил модель  Megane RS в черно-желтые оттенки', 'brend-renault-okrasil-model-megane-rs-v-cherno-zheltye-ottenki', '<p style="text-align: justify;">Компания <strong>Renault</strong> отметила победу Red Bull Racing в Кубке на тему конструкторов и по старой доброй традиции – произвела очередной особый вариант хот-хэтча Megane RS.</p>', '<p style="text-align: justify;">Хот-хэтч отличается от "специального" Megane 2012 года выпуска иной "ливрей".<br />Модель Megane RS имеет окраску Red Bull Racing RB7 - черно-желтый цвет. Новое специздание, имеет и другие оттенки – платиновый и глубокий синий, соответствующие в большой степени командным цветам, по мнению производителя авто.<br />Помимо этого, праздничная модель Megane RS распознается по определенным шильдикам с рисунком спонсора команды и особыми дисками R19 на резине Bridgestone Potenza RE050A. Укомплектование хот-хэтча пополнилось системой RS Monitor, позволяющей отслеживать конкретные показатели хот-хэтча при езде по гоночной полосе, мультимедия R-link с сенсорным 7-дюймовым экраном и возможностью управления голосом. Покрышки колес аналогичны шинам в них Renault Megane RS сделал рекордный забег по Нюрбургрингу.<br />В компании Renault будут выпускать только ограниченную линейку таких хэтчбеков. Хотя <a href="cars/"><strong>авто продажи</strong> </a>будут ограничены не числом единиц, а временем. Купить праздничный Megane RS жители Европы смогут в конце июня 2013 года по стоимости от 36000 евро, а уже 30 сентября <strong>продажи авто</strong> будут закончены.</p>\n<p><img style="margin: 10px auto; display: block;" title="продажи авто" src="images/stories/fotonews/2013/23.05.2013.jpg" alt="продажи авто" width="500" height="375" /></p>', 1, 13, '2013-05-23 03:41:47', 792, '', '2013-05-23 03:45:14', 792, 0, '0000-00-00 00:00:00', '2013-05-23 03:41:47', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 2, 311, 'Renault, авто продажи, продажи авто', 'Компания Renault отметила победу Red Bull Racing в Кубке и произвела очередной особый вариант хот-хэтча Megane RS', 1, 470, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(647, 714, 'Наиболее дорогим автобрендом в мире стала Toyota', 'naibolee-dorogim-avtobrendom-v-mire-stala-toyota', '<p style="text-align: justify;">Компания Millward Brown специализирующая на исследованиях представила ежегодный ТОП-100 самых дорогих брендов мира - BrandZ Top 100.</p>', '<p style="text-align: justify;">Возглавляет список, как правило, компания Apple, а среди автомобильных марок первое место занимает Toyota. Для справки, конкретно этот автопроизводитель крупнейший в мире.<br />По данным ТОП-100, за 2012 год увеличилась стоимость таких брендов как Toyota, Audi, Mercedes-Benz и Hyundai. Данный рост разрешил японцам выйти перед BMW.<br />В результате, бренд Toyota оценен в 24,49 млн. евро, это на 12% выше и на 5 позиций выше в рейтинге. BMW в этом году встал на 2-е место с отметкой в 24,01 млн. евро. 3-е место за Mercedes-Benz с показателем 17,95 млн. евро с ростом на 11%. 4-е место у Honda с $12,40 млн. И пятерку замыкает - Nissan с оценкой $10,18 млн.</p>\n<p style="text-align: justify;">Лидерам рейтинга удалось увеличить свои позиции благодаря повышенному спросу в США и Китае. BMW же показала второе место за счет относительно малого числа премьер новинок в сравнении с 2012 годом.</p>\n<p style="text-align: justify;">Единственным производитель авто от Америки, занимающий 7-е место в 100-ке – Ford с капиталом в $7,55 млн. с коэффициентом роста 8%. Хотя в Топ-100 он не попал. Бренд Volkswagen занимает 100-е место в списке с оценкой $8,79 млн.</p>\n<p style="text-align: justify;">По информации издания Automotive News, многие автобренды, не попавшие в рейтинг, сохранили около 70% от своей стоимости до рецессии.<br /><a href="cars/"><strong>Купить авто</strong></a> с пробегом любой марки из ТОП-100 вы всегда можете в автосалоне Адмирал-Моторс в Санкт-Петербурге.</p>\n<p><img style="margin: 10px auto; display: block;" title="Купить авто" src="images/stories/fotonews/2013/23.05.2013-1.jpg" alt="Купить авто" width="500" height="375" /></p>', 1, 13, '2013-05-23 03:45:17', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-23 03:45:17', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 310, 'Купить авто, купить автомобиль, покупка авто с пробегом', 'Millward Brown ТОП-100 самых дорогих брендов мира', 1, 534, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(648, 715, 'Hyundai планирует автомобили на водороде сделать массовыми', 'hyundai-planiruet-avtomobili-na-vodorode-sdelat-massovymi', '<p style="text-align: justify;">Автопроизводители Кореи сделали объявление о выводе на авторынок машин на водородных двигателях.</p>', '<p style="text-align: justify;">Самой первой моделью от марки <strong>Hyundai</strong> станет версия кроссовера ix35 или Tucson, но <a href="cars/"><strong>авто продажи</strong> </a>ее начнутся чем пару лет. <br />Руководство компании Дерек Джойс сделал заявление порталу Cars.com о том, что корейский производитель автомобилей вполне готов занять место первой компании в мире, наладившей производство авто на водородных моторах и сделавшей его массовым. Модель Hyundai ix35 с данным силовым агрегатом выйдет в 2015 году, и за первый год корейцы планируют реализовать порядка тысячи экземпляров. Но впоследствии объемы автопроизводства могут подскочить в 10 раз. <br />По мнению представителей компании, для того чтобы добиться таких результатов потребуется некоторое время. Все ограничивается необходимой инфраструктурой. Пэтому вначале Hyundai ix35 поставляться будет корпоративным клиентам Калифорнии, так как именно там свое применение уже нашли некоторые подобные разработки японских компаний. Ранее говорилось, первые 15 автомобилей поделят администрация провинции Сконе и мэрия Копенгагена. Массовые <strong>авто продажи</strong> частникам станут вторым шагом к продвижению.<br />Почему выбран такой крассовер? Потому что кузов ix35 разрешает без потерь пространства разместить такие двигатели. Технические характеристики пока не раскрыты. Но кое что все таки рассказали - запас хода будет в 600 км., а также то, что такой двигатель может работать при температурах от -20 - +40 градусов, а разгон до 100 км/ч происходит за 12,5 секунды.</p>\n<p><img style="margin: 10px auto; display: block;" title="авто продажи" src="images/stories/fotonews/2013/24.05.2013.jpg" alt="авто продажи" width="500" height="375" /></p>', 1, 13, '2013-05-24 03:59:37', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-05-24 03:59:37', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 309, 'авто продажи, Hyundai, продажа автомобилей, продажа автомобиля', 'Автопроизводители Кореи сделали объявление о выводе на авторынок машин на водородных двигателях', 1, 431, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
