INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(724, 791, 'Завод моторов для новой Chevrolet Niva построят в Тольятти', 'chevrolet-niva', '<p style="text-align: justify;">Испанская компания CIE Automotive и индийская AVTEC Ltd. хотят создать партнерское предприятие в Тольятти в особо экономической зоне, дабы организовать производство моторов нового поколения <strong>Chevrolet Niva</strong>.</p>', '<p style="text-align: justify;"><br />Проект по организации получения лицензии производства моторов получил одобрение на заседании экспертсовета в Минэкономразвития. Именно выпуском двигателей будет заниматься АЗ Пауэртрэйн.<br />CIE Automotive одобрена наблюдательным советом Особой экономической зоны города Тольятти на создание и выпуск литых алюминиевых частей и будет одним из поставщиков.</p>\n<p style="text-align: justify;">Главным клиентом будущего завода станет "GM-АвтоВАЗ". Двигателями будут комплектовать кроссовер Chevrolet Niva будущего поколения, производство которого стартует в 2015 году.<br />Завод начнут возводить весной следующего года, а уже в конце 2015 стартует серийный выпуск силовых агрегатов, сообщил "Ведомостям" генеральный директор Алексей Нестеренко "АЗ Пауэртрэйн". Мощность предприятия на 1-ом этапе составит 84000 двигателей в год, а в будущем объем производства будут увеличивать вместе с ростом мощности "GM-АвтоВАЗ".<br />Нестеренко добавил, что моторы будут производиться по лицензии СП "GM-АвтоВАЗ", приобретенной у PSA Peugeot Citroen, - двигатели емкостью 1,8 л.</p>\n<p style="text-align: justify;">Вложения в этот проект составят 31,5 млн. долларов. В планах на 7 лет вперед - производство механической обработки. Степень локализации поначалу составит 15%. А к 2020 году данный показатель составит 60%. Составляющие будут завозить из Индии и Китая. <br />Отметим, что уже сейчас можно <a href="cars/"><strong>купить авто</strong></a> Chevrolet Niva в автосалонах Санкт-Петербурга.  </p>\n<p><img class="caption" style="margin: 10px auto; display: block;" title="купить авто" src="images/stories/01.07.2013.jpg" alt="купить авто" width="500" height="375" /></p>', 1, 13, '2013-07-01 03:42:48', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-07-01 03:42:48', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 233, 'Chevrolet Niva, купить авто, продажа автомобилей в санкт-петербурге, купить авто в спб', 'Скоро будет создано партнерское предприятие в Тольятти в особо экономической зоне для организации производство моторов нового поколения Chevrolet Niva', 1, 505, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
