INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(726, 793, 'Новый Nissan Murano стал дешевле на 5000 рублей', 'novyj-nissan-murano-stal-deshevle-na-5000-rublej', '<p style="text-align: justify;"><strong>Nissan</strong> показал обновленную модель Murano 2013 года. Внешних изменений не наблюдается, разве что цена на 5 тысяч рублей меньше.</p>', '<p style="text-align: justify;"><br />Из изменений в экстерьере - новый цвет - белый. А также к кроссоверу можно прикупить легкосплавные 20-дюймовые колесные диски обновленного дизайна. Обновленный Nissan Murano имеет ряд новых опций - камера обзора сбоку с правой стороны для простой парковки и легкого маневрирования на небольших скоростях.<br />Премиальный кроссовер комплектуется 3,5 л. 6-цилиндровым мотором мощностью 249 лошадиных сил и крутящим моментом 336 Нм вместе с бесступенчатым вариатором нового поколения - Xtronic CVT. Максимальная скорость - 210 км/ч, разгоняется автомобиль до 100 км. за 8 секунд. При данных показателях затрата топлива равна 10,9 л. /100 км. в смешанном цикле. Кроссовер также комплектуется полноприводной системой All-Mode 4x4-i с системой ESP динамической стабилизации.</p>\n<p style="text-align: justify;">Базовый вариант <strong>Nissan Murano</strong> в салоне имеет кожаные сиденья с множеством регулировок кресел пассажира и водителя, управление задними сиденьями - дистанционное, камеры бокового обзора и заднего вида, аудиосистема BOSE 5.1 Digital Surround, одиннадцать динамиков, навигация - Nissan Connect Premium с накопителем.<br />В дорогих комплектациях автомобиль оснащен электроприводом задней двери, системой доступа с запоминающими настройками Intelligent Key, панорамной крышей, и в качестве опций – мультимедиа система для сидящих сзади.<br /><a href="cars/"><strong>Купить автомобиль</strong></a> Nissan Murano можно за 1585000 рублей и выше. Версию-предшественник можно купить за 1590000 рублей. Для справки, кроссовер Murano производится на заводе в Санкт-Петербурге.</p>\n<p><img class="caption" style="margin: 10px auto; display: block;" title="Купить автомобиль" src="images/stories/fotonews/2013/01.07.2013-1.jpg" alt="Купить автомобиль" width="500" height="375" /></p>', 1, 13, '2013-07-01 04:05:12', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-07-01 04:05:12', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 231, 'Nissan, Nissan Murano, Купить автомобиль, покупка авто с пробегом', 'Nissan показал обновленную модель Murano 2013 года. Внешних изменений не наблюдается, разве что цена на 5 тысяч рублей меньше', 1, 525, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(727, 794, 'Первый водородный автомобиль Toyota покажут в ноябре', 'pervyj-vodorodnyj-avtomobil-toyota-pokazhut-v-nojabre', '<p style="text-align: justify;">Автомобильный бренд <strong>Toyota</strong> скоро покажет первый водородный автомобиль.</p>', '<p style="text-align: justify;">Прототипы проходят тестирование. Топливная машина выглядит довольно выгодным транспортом, потому как потребляет электричество, вырабатываемое за счет химреакции, побочным продуктом которого является вода. <br />Ноябрьский автосалон в Токио 2013 года представит водородный автомобиль, запуск которого в серию может произойти уже в 2014 году, информирует агентство Bloomberg.</p>\n<p style="text-align: justify;">Топливные элементы получают энергию в следствии электро-химической реакции кислорода и водорода. Но для этого необходимы драгметаллы, соответственно и установка выходит дорогостоящей. И на разработку прототипов автомобилей на водороде уходило до миллиона долларов. По заявлению главы американского автопроизводителя электроавтмообилей Tesla, нехватка водорода вполне может приостановить развитие водородных машин. <br />Автопроизводитель Toyota, наоборот полагает, что седан на водороде сможет переплюнуть Prius. Для справки, в момент запуска компанией в 1997 году массового производства первого гибридного автомобиля, большинство считали данный автомобиль не более чем затратным проектом. С того момента Toyota продала около 4 млн. автомобилей Prius, став популярнейшим автопроизводителем гибридом в мире. <br />Сегодня водородные авто выпускают бренды Hyundai, Honda и Daimler. В компании Toyota пока не говорят об объемах <a href="cars/"><strong>авто продаж</strong></a>, но называют цену, аналогичную Lexus, до 50000 долларов для авторынка США. Можно сравнить, модель Tesla S можно купить - 69900 долларов и выше, пробег на одном подзаряде равен 483 км.</p>\n<p><img style="display: block; margin-left: auto; margin-right: auto; margin-top: 10px;" title="авто продажи" src="images/stories/02.07.2013.jpg" alt="авто продажи" width="500" height="374" /></p>', 1, 13, '2013-07-02 04:48:41', 792, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2013-07-02 04:48:41', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 230, 'Toyota, авто продажи, продажа автомобиля, автомобили с пробегом', 'Автомобильный бренд Toyota скоро покажет первый водородный автомобиль', 1, 482, '{"robots":"","author":"Автосалон Адмирал-Моторс"}', 0, '', '');
