INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(861, 928, 'Renault изготовит среднеразмерный пикап  Французский производитель автомобилей сможет изготовить авто на базе Nissan Navara', 'renault------------nissan-navara', '<p><img src="images/nissan-navara-iii-d40-double-cab-20.jpg" alt="nissan-navara-iii-d40-double-cab-20" width="600" height="400" /></p>\n<p>Скоро компания Renault составит соперничество модели Toyota HiLux выпустив первый в своей истории среднеразмерный автомобиль. Обо всем этом ссылаясь на президента азиатско-тихоокеанского подразделения Renault Жиля Нормана передает издание Car Advice .</p>', '<p> </p>\n<p>По словам Нормана, новейший образец Renault появится приблизительно в 2016 г.. В основае новшества будет лежать автомобиль Nissan Navara, от которого новейшая модель станет«значительно различаться согласно стилистике». Это не исключает, что изготовление пикапа Renault будет производится на одной линии с Nissan Navara в Таиланде.<br />Топ-директор еще прибавил, то что основными рынками для новшества будут государства Северной Африки и Латинской Америки, и еще Восточная Европа, где, по его мнению спрос на аналогичные автомашины не падает, а кроме того крепки позиции бренда Renault.<br />О каких-либо технических тонкостях в новеньком пикапе Renault не сообщаются. С целью сравнения новенький Nissan Navara оснащается 2,5-литровым дизельным движком, легкодоступным в 2-ух вариантах форсировки — 163-сильным и 190-сильным. </p>', 1, 13, '2014-10-11 20:37:31', 794, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2014-10-11 20:37:31', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 6, 'Nissan Navara', '', 1, 99, '{"robots":"","author":""}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(862, 929, '«Народные» автомобилиToyota обучатся без помощи других тормозить к 2018 году.', 'lr-toyota-------2018', '<p><img src="images/011-toyota-camry-2012.jpg" alt="011-toyota-camry-2012" width="600" height="450" /></p>\n<p>В ближайшие пару лет предприятие Toyota Motor намеревается создать экономную версию концепции предупреждения столкновений, каторая в настоящее время ставится на дорогостоящие модификации японского производителя. Новшество будет рассчитана на «народный» транспорт.</p>', '<p> </p>\n<p>Как информирует японский выпуск Nikkei, к 2018 г. Toyota намеревается оборудовать практически все свои автомашины, издаваемые на местный рынок (в настоящее время насчитывается почти 50 моделей), электронным помощником, какой дает возможность различать вероятность фронтального удара, оповещать о данной угрозе водителя и предупреждать о несчастном случае, если человек за рулём не сделал достаточно мер, для того чтобы уклонится от аварии. Сейчас разговор идёт об автоматическом замедлении автомашины, однако предстоящие версии концепции вдобавок сумеют безопасно изменять траекторию перемещения автомобиля в случаях, если действий тормозных устройств мало. <br />Toyota предложила технологию предупреждения столкновений в 2003 г., и с этих пор она появилась почти в патнадцати моделях бренда, в том числе линейку гибридов Prius и представителей шикарных серий Lexus и Crown. В наиболее дешёвых автомашинах на японском рынке данная система станет появляться в 2015 г., а ещё чрез 3 года станет применяться в основной массе автомашинах марки Toyota, в том числе Corolla и др.</p>\n<p>Ранее Toyota заявила, то что к 2017 г. почти все из показанных в США автомашин (почти 30) станут комплектоваться технологиями автоматического торможения перед преградами. Со сроками пуска этих систем безопасности в автомобилях, направляемых в европейский рынок, фирма еще не сделала свой выбор, так как не до конца оценены условия и инструкция дорожного движения в регионе, замечает информатор.</p>\n<p> </p>', 1, 13, '2014-10-18 08:45:36', 794, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2014-10-18 08:45:36', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 3, 'Toyota, 2017, Nikkei', '', 1, 129, '{"robots":"","author":""}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(863, 930, 'Автомобили, в которых продумано всё вплоть до деталей', '2014-10-19-22-39-09', '<p><img src="images/Opel_Astra-H-Sedan-ext-5.jpg" alt="Opel Astra-H-Sedan-ext-5" width="600" height="450" /></p>\n<p>Автомобиль в настоящее время – это комфортное средство передвижения. Подбирать его для себе следует таким образом, чтобы не просто наслаждаться приобретением, а достигать абсолютное блаженство при каждой поездке в нём.</p>', '<p>Нынешний авто до такой степени разнообразен, что сделать верный выбор нелегко. Чтобы упростить для себя проблему, нужно побывать в салонах Опель в Москве. Которые предлагают своим клиентам интересные предложения, как по стоимости, так и по качеству. Бренду Opel больше 110 лет. Для автомобилей данной марки все время разрабатывают новейшие технологии, что дает возможность приводить в восторг почитателей данного автомобиля неожиданными инноваторскими решениями.</p>\n<p>Опель постоянно имеет огромный модельный ряд. Он хорошо совмещает в себе стиль, надежность, детально проработанные мелочи и немецкое качество. Автомашины данной марки популярны всему миру. По желанию покупателя в автомобильных салонах возможно подобрать приглянувшийся вам тип кузова. Постоянно имеется возможность подобрать классический sedan для какого угодно путешествия, вместительный универсал либо семейный минивэн. Цветовая палитра авто легко предоставит вам свободу выбора</p>\n<p> </p>', 1, 13, '2014-10-19 22:34:56', 794, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2014-10-19 22:34:56', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 2, 'опель, Opel', '', 1, 104, '{"robots":"","author":""}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(864, 931, 'Бренд Maybach возвратится на рынок уже в ноябре', 'maybach', '<p><img src="images/2014-Chevrolet-SS-0011.jpg" alt="2014-Chevrolet-SS-0011" width="600" height="450" /></p>\n<p>Компания Мерседес-Benz официально утвердила дату премьерного показ самой шикарной и длинной модели собственного флагманского седана S-класса. Данная модификация, приобретет знаменитое название Maybach, будет показана на выставке в Лос-Анджелесе.</p>', '<p> </p>\n<p>От изготовления лимузинов марки Maybach автоконцерн Daimler категорически отказался примерно два года назад по причине очень невысокого спроса. Но оставлять данный участок рынка премиальных седанов немцы никак не намереваются, и расчитывают сделать марку в название одной из версий S-класса – она будет самой шикарной и приобретет самую длинную колесную основу в семействе.</p>\n<p>По предварительным сведениям, она достигнет 3 600 мм, а совокупная протяжённость седана составит примерно 5,5 м. Подобным образом, модификация Maybach станет почти на 250 мм длиннее теперешней длиннобазной модели седана S-класса, при этом все добавочные миллиметры достанутся задним пассажирам. Предполагается, что sedan станет издаваться в четырех- и шестиместном исполнении.</p>\n<p>Mercedes-Benz S-class Maybach будет оборудоваться 12-цилиндровым мотором, развивающим 530 л.с. По другой информации, новшество приобретет более сильный вариант того же двигателя, развивающий 630 л.с. и 1 000 Нм вращающего момента. Раньше говорилось, что Мерседес-Benz собираются обеспечить исполнением Maybach и полноразмерный автомобиль GL-класса.</p>\n<p> </p>', 1, 13, '2014-10-22 06:55:09', 794, '', '0000-00-00 00:00:00', 792, 0, '0000-00-00 00:00:00', '2014-10-22 06:55:09', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_section":"","link_section":"","show_category":"","link_category":"","show_vote":"","show_author":"","show_create_date":"","show_modify_date":"","show_pdf_icon":"","show_print_icon":"","show_email_icon":"","language":"","keyref":"","alternative_readmore":""}', 1, 1, 'Mercedes-Benz S-class Maybach, Daimler', '', 1, 80, '{"robots":"","author":""}', 0, '', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(865, 945, 'Выкуп авто. Срочный выкуп авто в СПб', 'sell-in-spb', '<p><span style="line-height: 107%; font-family: arial, helvetica, sans-serif;">Выкуп авто</span></p>\r\n<p style="text-align: justify;">Компания Адмирал Моторс уже много лет занимается продажей и покупкой автомобилей в СПб, большой объем продаж и профессионализм команды позволяют нам осуществлять выкуп автомобилей в СПб по максимально возможной цене! Цена определяется на основе среднерыночных цен на автомобиль. Учитывается год производства, уровень покупательского спроса на данную модель и техническое состояние машины. <strong>Выкуп авто</strong> позволит сэкономить Ваши силы и деньги; все формальности, связанные с процедурой выкупа, происходят в одном месте и в одно время. Кроме чистого выкупа мы предлагаем услугу <strong><a href="trade-in.html">трейд ин в СПб</a></strong>. В этом случае Ваша старая машина может быть обменена на авто поновее.&nbsp;</p>\r\n<h2 style="text-align: center;">Срочный выкуп авто</h2>\r\n<p style="text-align: justify;">Предлагаем<strong> срочный выкуп авто (всего за несколько часов)</strong> по максимально возможной цене. Звоните <strong><span id="itemtelephone" class="ya-phone-long">+7 (812) 921-95-99</span></strong>! Самые выгодные условия!&nbsp;Разумеется, цена будет несколько ниже чем при комиссионной продаже, но мы постараемся предложить максимально возможную сумму за ваш автомобиль!&nbsp;</p>\r\n<h2 style="text-align: center;">Выкуп авто в СПб. Кредитные машины</h2>\r\n<p>Компания Адмирал Моторс производит также Выкуп кредитных авто.<br />Оценка и последующая диагностика автомобиля на СТО производятся БЕСПЛАТНО.</p>\r\n<p>Цена выкупа автомобиля определяется менеджерами Автосалона на основе среднерыночных цен на данную модель и год производства, также учитывается уровень покупательского спроса на данный автомобиль и его техническое состояние.</p>\r\n<p>Оценка и последующая диагностика автомобиля на СТО производятся БЕСПЛАТНО.</p>\r\n<p><strong>Услуга выкуп кредитного авто в СПб происходит следующим образом:</strong></p>\r\n<ol>\r\n<li>Предварительная оценка производится по телефону на основе данных, которые Вы нам сообщите о своем автомобиле.</li>\r\n<li>Если Вас устраивает предварительная оценка, Вы приезжаете к нам в автосалон для окончательной оценки выкупа авто и диагностики на СТО</li>\r\n<li>Наши экперты проверяют автомобиль на залог, кредит, угон в базе данных ГИБДД и на наличие изменений в идентификационных номерах.</li>\r\n<li>После согласования окончательной цены выкупа Вы снимаете автомобиль с учета в ГИБДД (если автомобиль еще не снят с учета).</li>\r\n<li>Мы заключаем договор купли-продажи и Вы получаете деньги за автомобиль в кассе Автосалона<strong><sup><a href="#footnote-1">[*]</a></sup></strong></li>\r\n<li>Вы передаете нам автомобиль, ключи, документы.</li>\r\n</ol>\r\n<p style="text-align: justify;"><strong>Срочный выкуп авто возможен за несколько часов</strong>, в случае отсутствия дополнительных проблем с конкретной машиной.<br />Если автомобиль КРЕДИТНЫЙ и находится в залоге у банка, свяжитесь с нами для уточнения процедуры ВЫКУПА по тел.: <strong><span id="itemtelephone" class="ya-phone-long">+7 (812) 921-95-99</span></strong> Мы не только выкупаем кредитные машины, но и&nbsp;<strong><a href="crediting.html">оформляем бу авто в кредит</a></strong>.</p>\r\n<p><strong>Документы, необходимые на выкуп авто в Санкт Петербурге:</strong></p>\r\n<ol>\r\n<li>паспорт транспортного средства (ПТС)</li>\r\n<li>свидетельство о регистрации автомобиля (если автомобиль не снят с учета)</li>\r\n<li>генеральная доверенность (если Вы не являетесь собственником автомобиля)</li>\r\n<li>личный паспорт владельца</li>\r\n<li>сервисная книжка и другие документы по автомобилю (желательно)</li>\r\n</ol>\r\n<p>Мы гарантируем Вам конфиденциальность и Вашу личную безопасность при расчетах за автомобиль.</p>\r\n<p>&nbsp;</p>\r\n<p><a name="footnote-1"></a>* При необходимости мы можем произвести оплату по безналичному расчету.</p>', '', 1, 14, '2014-11-17 10:53:30', 792, '', '2014-11-18 08:38:53', 792, 0, '0000-00-00 00:00:00', '2014-11-17 10:53:30', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 11, 'Выкуп авто, срочный выкуп авто, выкуп авто в СПб', 'Хотите без проблем продать авто с пробегом в СПб? Выкуп авто по максимально возможной цене! Выкуп машин с непогашенным кредитом! Компания Адмирал Моторс. Лучшие условия выкупа', 1, 47, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
