INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(867, 948, 'Автострахование в Санкт-Петербурге - КАСКО, ОСАГО в СПб', 'insurance-spb', '<h1 style="text-align: center;">Автострахование</h1>\r\n<p style="text-align: justify;">С каждым днем увеличивается интенсивность автомобильного движения в нашей стране. Поэтому, как это ни печально, растет число ДТП. У многих автолюбителей нет своего парковочного места и они вынуждены оставлять свой автомобиль перед домом. Конечно, это связано с определённым риском, но когда выбора нет, думать не приходится. В этом случае нам может помочь АВТОСТРАХОВАНИЕ.</p>\r\n<p style="text-align: justify;">Существует 2 вида автомобильного страхования: ОСАГО и КАСКО.</p>\r\n<p style="text-align: justify;">В отличие от КАСКО, автострахование ОСАГО предполагает базовое покрытие основных автомобильных рисков. Тогда как КАСКО обеспечивает дополнительную страховую защиту для автолюбителя и членов его семьи.</p>\r\n<h2 style="text-align: center;">Автострахование ОСАГО</h2>\r\n<p style="text-align: justify;">ОСАГО – обязательный вид автострахования, на который государство дает автовладельцу пять дней, чтобы приобрести полис с того момента, как он приобрел транспортное средство. Но если до истечения этого срока инспектор ГИБДД остановит вас на дороге, он может выписать Вам штраф в размере 800 рублей. Несмотря на то, что автострахование ОСАГО является обязательным, оно служит личным интересам владельца машины, ведь если ваше авто застраховано, то вам и в случае ДТП не придется возмещать ущерб своими силами.&nbsp;Оформить такое автострахование может как собственник машины, так и любой другой автолюбитель при предъявлении доверенности.</p>\r\n<p style="text-align: justify;">Чтобы быть в курсе всех автомобильных новостей, читайте&nbsp;<strong><a href="news.html">авто статьи на Адмирал Моторс</a></strong>.</p>\r\n<h2 style="text-align: center;">КАСКО автострахование</h2>\r\n<p style="text-align: justify;">КАСКО - добровольный вид автострахования. Он позволяет застраховать автомобиль как по рискам «Угон+Ущерб», так и по риску «Угон».</p>\r\n<p style="text-align: justify;">Цена этой услуги зависит от модели автомобиля, его стоимости, года выпуска, какими противоугонными системами он оснащен, возраста и стажа водителей, допущенных к управлению, от того, где Вы оставляете автомобиль в ночное время и каким образом хотели бы получать страховое возмещение (калькуляция, ремонт на дилере, ремонт на СТО страховщика), а также в какой страховой компании Вы страхуете свой автомобиль по КАСКО. Автострахование в нашей компании отличается разумными тарифами.</p>\r\n<p style="text-align: justify;">На момент страхования или в течение нескольких дней после него автомобиль должен быть осмотрен и сфотографирован специалистами страховой компании. Страховой полис КАСКО начинает действовать ТОЛЬКО с момента осмотра ТС. Осмотр необходим во избежание мошенничества со стороны страхователей.</p>\r\n<p style="text-align: justify;">Компания Адмирал Моторс рада сообщить Вам, что нашим партнером по услуге автострахование является страховой брокер «САКВОЯЖ».</p>\r\n<p style="text-align: justify;">Это крупный страховой брокер Северо-Западного региона, партнерами которого являются 25 ведущих страховых компаний. Это компании РЕСО, РОСГОССТРАХ, ИНГОССТРАХ, ЮГОРИЯ, СУРГУТНЕФТЕГАЗ, МСК, ТАЛИСМАН, ГУТА СТРАХОВАНИЕ, УРАЛСИБ, ЦЮРИХ и т.д.</p>\r\n<p style="text-align: justify;">Мы подберем Вам лучшую по привлекательности страховку только в НАДЕЖНОЙ страховой компании.</p>\r\n<p style="text-align: justify;">Постоянным клиентам скидки от 10%. Дополнительные данные Вы можете найте на странице&nbsp;<strong><a href="contacts.html">Автосалон Адмирал Моторс. Продажа подержанных авто. Контакты</a></strong></p>', '', 1, 14, '2014-11-17 11:07:07', 792, '', '2014-11-18 08:38:26', 792, 0, '0000-00-00 00:00:00', '2014-11-17 11:07:07', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 9, 'Автострахование, автострахование осаго, каско автострахование', 'Вам нужно оформить автострахование на личное или корпоративное авто в СПб? Автострахование ОСАГО и КАСКО в Адмирал Моторс. Быстро, без проблем, с гарантией! Скидки постоянным клиентам', 1, 2, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO admiralaru_new.dgb4n_content(id, asset_id, title, alias, introtext, `fulltext`, state, catid, created, created_by, created_by_alias, modified, modified_by, checked_out, checked_out_time, publish_up, publish_down, images, urls, attribs, version, ordering, metakey, metadesc, access, hits, metadata, featured, language, xreference) VALUES
(868, 949, 'Трейд ин в СПб. Обмен авто с пробегом на авто с пробегом', 'trade-in-spb', '<p>Трейд ин<strong>&nbsp;</strong></p>\r\n<p>Автосалон «Адмирал Моторс» предлагает вам воспользоваться возможностями приобретения автомобилей по программе трейд ин. Эта услуга, означающая передачу старой вещи в счет оплаты новой покупки. Ваш бывший в употреблении автомобиль принимается в зачет стоимости нового, по наиболее привлекательной для Вас цене. Кроме того, Ваш старый автомобиль может быть первым взносом при покупке в кредит. Такой обмен авто на авто очень популярен во всем мире, а сейчас набирает обороты и в нашей стране. Если у вас еще нет автомобиля, и не достаточно средств сразу оплатить его мы рекомендуем вам купить&nbsp;<strong><a href="crediting.html">авто в кредит в СПб</a></strong>.</p>\r\n<h2 style="text-align: center;">Трейд ин СПб</h2>\r\n<p style="text-align: justify;">Наша компания одна из первых начала предоставлять эту услугу жителям Петербурга. Богатый опыт работы позволяет нам предлагать гибкие условия трейд ин программ. Большая клиентская база обеспечивает широкие возможности для покупки автомобилей в счет продажи более новых моделей. Заинтересованность в питерских авто, как правило, является обоюдной. Ведь предыдущую историю машины легче отследить в пределах одного города. Трейд ин СПб, таким образом, становится выгодным и социально значимым делом.</p>\r\n<p style="text-align: justify;">В нашей компании существуют и другие программы тесно связанные с этой услугой. Возможен, например, прием на комиссию и выкуп автомобилей с пробегом. Кроме того, в нашем автосалоне можно произвести&nbsp;<strong><a href="insurance.html">страхование авто в СПб</a></strong></p>\r\n<h2 style="text-align: center;">Обмен авто с пробегом на авто с пробегом</h2>\r\n<p style="text-align: justify;">Назовем преимущества трейд ин сервиса:</p>\r\n<p style="text-align: justify;">1) Во первых это надежно. Отсутствует риск стать жертвой аферистов при самостоятельной продаже автомобиля на рынке или по объявлению;</p>\r\n<p style="text-align: justify;">2) Во вторых – удобно. Обмен авто с пробегом на авто с пробегом происходит непосредственно в автосалоне.</p>\r\n<p style="text-align: justify;">3) В третьих это быстро. После оценки и согласования стоимости прежнего автомобиля, не нужно ждать когда он будет продан. Его стоимость сразу же идет в счет оплаты нового автомобиля; Всё можно оформить в течение одного дня;</p>\r\n<p style="text-align: justify;">И наконец трейд ин это выгодно. Стоимость автомобиля максимально приближена к рыночной. Вам не нужно тратиться на платные объявления, рыночные сборы и предпродажную подготовку. Возможность использования старого авто в счет оплаты кредита на более новую модель является безусловным преимуществом этой трейд ин СПб в «Адмирал моторс».</p>', '', 1, 14, '2014-11-17 11:08:37', 792, '', '2014-11-18 08:37:57', 792, 0, '0000-00-00 00:00:00', '2014-11-17 11:08:37', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 3, 8, 'трейд ин, трейд ин СПб, обмен авто с пробегом на авто с пробегом', 'Трейд ин в СПб. Обмен авто с пробегом на авто с пробегом. Меняем старое авто на более новое. Для погашения разницы в стоимости можем предоставить кредит. Только для жителей СПб', 1, 5, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
